css:
@-moz-document domain("aftenposten.no") {
:root {
    --primaryDark: #000019;
    --primary: #000c26;
    --primaryLight: #003f9d;
    --primaryLighter: #3372D0;

    --darknessDark: #000200;
    --darkness: #151B16;
    --darknessLight: #2F3530;
    --darknessLighter: #484E49;

    --typeDark: #fff;
    --type: #d9d9d9;
    --typeLight: #a0a0a0;
    --typeDisabled: #5e617c;

    --yellowDark: #efc327;
    --yellow: #F1CA3F;
    --yellowLight: #f6de86;
    --yellowLighter: #fbf1ce;
}

.css-pp-box.show,
.plussbox_container,
#menuwrapper,
.native-advertisement,
#pluss-banner-container,
[class*="brandstudio"],
aside.related,
.mm-nyhetsbrev {
    display: none;
}

body,
.page-wrapper,
body.sport-layout,
[class^="styles___SportsHeader__sportsHeaderWrapper"],
[class^="styles___SportsHeader__sportsHeader"],
[class^="styles___SportsHeader__placementLabel"],
.content-main-container,
a.mm-front-link,
a.mm-front-link h2,
.pks-bt-frontpage,
.pks-bt-frontpage .pks-products .pks-product-link,
.pks-bt-frontpage .pks-products .pks-product-date,
.main-content,
.ch-core-header.ch-core-header .ch-header-sub,
.pswp__bg,
.pswp .pswp__caption,
body.article-type-feature {
    background-color: var(--darkness);
    color: var(--type);
}

body.sport-layout .main-container,
.content-container,
.article-main-info,
.top-media,
.media-info,
.top-media .media-info,
a.mm-spelt__entry,
.content-outer .factbox .factbox-body,
.article-type-feature .article-body .image-wrapper,
.article-type-feature .article-body .video-wrapper,
.article-content .media-info.image-info,
.mm-spelt__w-article,
.bg-standard,
body.sport-layout.pub-aftenposten .article-wrapper .article-right-column .wsl-title,
.widget-curate-list-farticles .widget-section-list .wsl-first-article .wsl-description,
[class^="Entry___default__entry"] {
    background-color: var(--darknessLight);
    color: var(--type);
}
.arrow-top,
.article-content .media-info.image-info::after,
body:not(.article-type-feature):not(.article-type-photo-feature) .top-media .media-wrapper.image-wrapper::after {
    border-bottom-color: var(--darknessLight);
}

.article-text-only-1-col:not(.skin-dark),
.article-text-only-2-col:not(.skin-dark),
.article-text-only-3-col:not(.skin-dark) {
    background-color: var(--darknessLighter);
}

.article-body,
.color-standard,
.ch-core-header.ch-core-header .ch-header-sub .ch-section-label {
    color: var(--type);
}

h1.article-title,
.article-body h1,
.article-body h2,
.article-body h3,
.article-body h4,
.article-body h5,
.article-body h6,
strong,
.widget-container .widget-header,
.widget-container .widget-articles .widget-article .title-container .timestamp,
.read-articles-widget .widget-header .title {
    color: var(--typeDark);
}

.ch-core-header.ch-core-header {
    box-shadow: 0 1px 10px 0 var(--darknessLighter);
}
.ch-core-header.ch-core-header .ch-header-main {
    background: var(--primary);
    border-bottom-color: var(--darkness);
}
.ch-core-header.ch-core-header .ch-logo:active,
.ch-core-header.ch-core-header .ch-logo:hover,
.ch-core-header.ch-core-header .ch-logo:link,
.ch-core-header.ch-core-header .ch-logo:visited {
    color: var(--type);
}

.ch-core-header.ch-core-header .ch-main-links a,
.ch-core-header.ch-core-header .ch-main-links a:link,
.ch-core-header.ch-core-header .ch-main-links a:visited,

.ch-core-header .ch-subsections-container .ch-subsections a,
.ch-core-header .ch-subsections-container .ch-subsections a:link,
.ch-core-header .ch-subsections-container .ch-subsections a:visited,

.widget-container .widget-header .description {
    color: var(--typeLight);
}

.ad,
.standard-ad-container,
body.sport-layout #topboard {
    background-color: var(--darknessDark);
}

a,
a:focus,
a:hover,
a:visited,
body a:link,
.widget-container a,
.widget-container a:active,
.widget-container a:hover,
.article-content.section-color-primary a {
    color: var(--primaryLighter);
}

.label-basic,
.label-basic .article-author-name,
.label-basic .article-timestamp,
.label-basic .paywall-label.closed {
    color: var(--typeLight);
}

body a:hover,
body a:visited:hover,
.mm-spelt a:hover {
    color: var(--typeDark);
}

body a:visited {
    color: var(--typeDisabled);
}

p.mm-subscribers.mm-dark {
    color: var(--typeLight) !important;
}

.section-sid .label-basic,
.section-sid .label-basic .article-author-name,
.mm-spelt__title {
    color: var(--primaryLighter);
}

.widget-container.have-you-read-widget,
.decoration-standard,
.content-main-container {
    border-color: var(--darkness);
}
.decoration-standard-bottom {
    border-color: var(--type);
}

.article-content .article-body a {
    text-decoration: underline;
}
.article-content .article-body a:hover {
    text-decoration: none;
}

.content-outer .factbox.collapsible .factbox-body:after {
    height: 70px;
    background: linear-gradient(to bottom, rgba(255, 255, 255, 0) 0, var(--darknessLight) 82%);
}

.hnews {
    border-color: var(--darkness);
}
.article-wrapper {
    background-color: var(--darknessLight);
}

/* TOP ARTICLES WITH FLUID TEXT BLOCK SIZE */
.article-fluid-text .fluid-text {
    background-color: transparent;
}
}
