@namespace url(http://www.w3.org/1999/xhtml);@-moz-document domain("tumblr.com"){#dashboard_edit_post,#dashboard_index{background:none #ffffff !important;color:#342a1c !important;font-family:"Lucida Grande","Gill Sans","Vera Sans" !important;font-size:12px;}
#dashboard_index,#dashboard_edit_post,#dashboard_edit_post div.l-header,#dashboard_index div.l-header,#dashboard_edit_post #container,#dashboard_index #container,#dashboard_index div.l-content,#dashboard_edit_post #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background:none #ffffff !important;}
#dashboard_index div.l-header{position:absolute !important;top:0px !important;left:0px !important;height:250px !important;width:100% !important;background-color:#342A1C !important;}
#dashboard_index #new_post_notice_container{position:absolute !important;top:60px !important;left:30px !important;}
#dashboard_index #logo{top:100px !important;left:50px !important;}
#dashboard_index #nav{top:120px !important;right:50px !important;}
#dashboard_index #search_query,#dashboard_index .search_icon,#dashboard_index #tip{display:none !important;}
#dashboard_index #dashboard_controls a,#dashboard_controls .control{color:#342a1c !important;}
#dashboard_index #content_top,#dashboard_index #content_bottom{display:none !important;}
#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {margin-top:280px !important;}
#dashboard_index .dashboard_nav_item.active{background:none #ffffff !important;color:#000000 !important;border:2px solid #342a1c !important;}
#dashboard_index #dashboard_nav_following{background:none #ffffff !important;color:#000000 !important;border:none !important;}
#dashboard_index .dashboard_nav_item a:hover,#dashboard_index .tumblelog_link:hover{color:#342a1c !important;}
#dashboard_index .nav_item{color:#999999 !important;}
#dashboard_index .arrow{display:none !important;}
#dashboard_index .nav_item.active{color:#ffffff !important;background:none #000000 !important}
#dashboard_index #new_post a{position:relative !important;padding:0px !important;margin:0px auto !important;left:auto !important;top:auto !important;display:inline-block !important;width:70px !important;}
#dashboard_index #new_post_icons{display:none !important;}
#dashboard_index #new_post .avatar,#dashboard_index #new_post .arrow{display:none !important;}
#dashboard_index .avatar{border:none !important;}
#dashboard_index .posts{-webkit-border-bottom-left-radius:none !important;-webkit-border-bottom-right-radius:none !important;-webkit-border-top-left-radius:none !important;-webkit-border-top-right-radius:none !important;}
#dashboard_index .post{border:none !important;color:#342a1c !important;margin-top:35px !important;padding-bottom:25px !important;}
#dashboard_index .new_post{border:none !important;}
#dashboard_index .post_title{color:#dd0000 !important;font-weight:bold !important;}
#dashboard_index .post_title a{color:#dd0000 !important;font-weight:bold !important;}
#dashboard_index img.permalink{height:0 !important;width:0 !important;/* these numbers match the new image's dimensions */
padding-left:16px !important;padding-top:16px !important;background:url(data:image/png;base64,) no-repeat !important;}
#dashboard_index #next_page_link,#previous_page_link{background:none #342a1c !important;}
#dashboard_index #footer,#dashboard_index #footer a{color:#342a1c !important;}
#dashboard_index #dashboard_activity{background:none #ffffff !important;}
#dashboard_index #right_column{color:#342a1c !important;}
#dashboard_index #right_column a{color:#342a1c !important;}
#dashboard_index #right_column img{display:none !important;}
#dashboard_index #nav .nav_item{color:#ffffff !important; background:none #342a1c !important;border-width:0 !important;}
#dashboard_index #nav .nav_menu{background:none #342a1c !important;}
#dashboard_edit_post #logo{display:none !important;}
#dashboard_edit_post #nav .nav_item{color:#342a1c !important; background:none #ffffff !important;border-width:0 !important;}
#dashboard_edit_post #nav .nav_menu{background:none #ffffff !important;}
#dashboard_edit_post img#content_top,#dashboard_edit_post img#content_bottom{display:none !important;}
}
