css:
@-moz-document url-prefix("https://cake.o2switch.net"), regexp(".*phpmyadmin.*")
{

  :root
  {
    --bg: #263238;
    --lbg: #32424A;
    --dbg: #1E272C;
    --contrast: #1E272c;
    --border: #2A373E;
    --excl: #2E3C43;
    --button: #2E3C43;
    --txt: #B0BEC5;
    --primary: #607D8B;
    --selBg: #314549;
    --selFg: #fff;
    --disabled: #415967;
    --hl: #425B67;
    --ttl: #009688;
    --accent: #009688;
    --error: #ff5370;
    --tag: #f07178;
    --number: #f78c6c;
    --attribute: #FFCB6B;
    --string: #C3E88D;
    --operator: #89ddff;
    --link: #80CBC4;
    --function: #82aaff;
    --keyword: #c792ea;
    --success: #c3e88d;
    --variable: #eeffe3;
    --comment: #546E7A;
    --font: 'Open Sans', 'Calibri', Tahoma, Helvetica, Arial, sans-serif;
    --mono: 'Operator Mono', 'Menlo', 'Consolas', 'Source Code Pro', monospace;
  }

  html, body
  {
    font-family: var(--font);
  }

  body
  {
    background: var(--bg);
    color: var(--txt);
  }

  code
  {
    font-family: var(--mono);
  }

  table
  {
    box-shadow: 0 2px 6px 0 var(--contrast) !important;
  }

  input[type="checkbox"], input[type="radio"]
  {
    vertical-align: baseline !important;
  }

  button
  {
    background: var(--button) !important;
    color: var(--primary) !important;
    text-transform: uppercase !important;
  }

  div.tools a,
.checkall_box+label,
a,
a:link
  {
    color: var(--link) !important;
  }

  ::selection,
::-webkit-selection,
::-moz-selection
  {
    background-color: var(--selBg) !important;
    color: var(--selFg);
  }

  #topmenu a,
.submenu a
  {
    color: var(--txt) !important;
    font-weight: normal;
  }

  #topmenu a:hover,
.submenu a:hover
  {
    text-shadow: none !important;
    color: var(--selFg) !important;
  }

  .database a,
.table a
  {
    color: var(--primary) !important;
  }

  ul#topmenu2 a
  {
    color: var(--primary) !important;
    font-weight: normal;
  }

  .selectallarrow
  {
    filter: invert(1);
  }

  textarea
  {
    font-family: Menlo, Consolas, 'Fira Code', monospace;
    font-size: 12px;
    background: var(--dbg);
    color: var(--txt);
  }

  li.fast_filter span
  {
    color: var(--accent) !important;
  }

  #serverinfo
  {
    background: var(--lbg);
    text-shadow: none;
  }

  #pma_navigation_collapser
  {
    background: var(--lbg);
    border: none;
    color: var(--txt);
    z-index: 0;
  }

  #pma_navigation_tree li.activePointer
  {
    background: var(--lbg);
  }

  ul#topmenu,
ul#topmenu2,
ul.tabs,
#topmenu a,
ul#topmenu > li,
.menucontainer
  {
    background: var(--lbg);
    text-shadow: none;
    border: none;
    color: var(--txt);
    margin-top: 0;
  }

  ul#topmenu > li > a:hover,
ul#topmenu > li > .tabactive
  {
    background: var(--lbg) !important;
    text-shadow: 0 0 1px white;
  }

  ul#topmenu2 a,
ul#topmenu2 a.tab:hover,
ul#topmenu2 a.tabactive
  {
    background: var(--lbg) !important;
    border-radius: 0;
    border: none;
    margin-top: 0;
    height: 15px;
  }

  ul#topmenu2 a.tabactive,
ul#topmenu2 a.tab:hover
  {
    text-shadow: 0 0 1px var(--lbg);
  }

  h2
  {
    text-shadow: none;
    color: var(--ttl);
  }

  .exportoptions h3,
.importoptions h3
  {
    border: none;
  }

  h1.notice,
div.notice,
p.notice,
.ajax_notification
  {
    border: none;
    border-radius: 0;
    box-shadow: none;
    color: var(--txt);
    background: var(--dbg);
  }

  #maincontainer,
#page_content
  {
    background: var(--bg);
    color: var(--txt);
    margin-left: 20px;
    margin-top: 30px;
  }

  fieldset legend,
fieldset,
.preview_sql,
div.tools,
.tblFooters
  {
    border: none;
    border-radius: 0;
    box-shadow: none;
    background: var(--bg);
    color: var(--txt);
    width: 100%;
    padding-left: 0;
  }

  table tr th,
table tr,
table td,
thead,
div#page_content div#tableslistcontainer table.data,
#div_mysql_charset_collations table th,
#div_mysql_charset_collations table td
  {
    background: var(--lbg) !important;
    color: var(--txt);
    text-shadow: none;
    border: none;
    padding: 5px;
  }

  table tr:nth-child(2n) th,
table tr:nth-child(2n) td
  {
    background: var(--bg) !important;
  }

  th
  {
    font-size: 14px;
    text-align: left;
    border-bottom: 1px solid var(--border) !important;
    font-weight: normal;
  }

  table tbody:first-of-type tr:not(.nopointer):hover,
table tbody:first-of-type tr:not(.nopointer):hover th,
.hover:not(.nopointer)
  {
    color: var(--selFg) !important;
  }

  table
  {
    box-shadow: 0 2px 6px 0 rgba(0, 0, 0, 0.5);
  }

  td.marked:not(.nomarker),
table tr.marked:not(.nomarker) td,
table tbody:first-of-type tr.marked:not(.nomarker) th,
table tr.marked:not(.nomarker)
  {
    background: var(--lbg);
  }

  .CodeMirror-scroll,
.CodeMirror-sizer,
.CodeMirror-gutter,
.CodeMirror-gutters,
.CodeMirror-linenumber
  {
    border: none;
    font-size: 15px;
  }

  fieldset fieldset,
#fieldset_add_user_login div.item
  {
    background: var(--lbg);
    border: none;
  }

  div.success,
div.notice,
div.error,
.error
  {
    border-radius: 0;
    box-shadow: none;
    border: none;
    color: var(--txt);
  }

  div.success
  {
    border-left: 5px solid var(--success);
    background: var(--lbg);
  }

  div.error
  {
    border-left: 5px solid var(--error);
    background: var(--lbg);
  }

  #pma_navigation_content
  {
    background: var(--bg);
    box-shadow: 2px 0 9px 0px var(--border);
    color: var(--txt);
  }

  #pma_navigation_tree a
  {
    color: var(--txt);
  }

  #pma_navigation_tree li.selected
  {
    background: var(--selBg) !important;
  }

  .group,
.group h2
  {
    border: none;
    box-shadow: none;
    text-shadow: none;
    border-radius: 0;
    background: var(--bg);
  }

  .group h2
  {
    color: var(--ttl);
  }

  #pma_navigation_resizer
  {
    background: var(--bg);
  }

  #selflink
  {
    visibility: hidden;
  }

  span
  {
    color: var(--txt);
  }

  #pma_console .toolbar
  {
    background: var(--lbg);
    border: none;
    height: 20px;
    text-transform: uppercase;
  }

  #pma_console .message .action_content
  {
    background: var(--button);
    color: var(--txt);
    border: 1px solid var(--border);
  }

  #pma_console .message .text
  {
    background: var(--excl);
    color: var(--txt);
  }

  #pma_console .message .action_content .action
  {
    font-family: var(--font);
    text-transform: uppercase;
  }

  #pma_console .message span.action:hover,
#pma_console .toolbar .button:hover,
#pma_console .switch_button:hover,
#pma_console .toolbar .button.active
  {
    background: var(--lbg);
  }

  #pma_console .content
  {
    background: var(--dbg);
    border: none;
    color: var(--txt);
  }

  .CodeMirror-scroll,
.CodeMirror-sizer,
.CodeMirror-gutter,
.CodeMirror-gutters,
.CodeMirror-linenumber
  {
    background: var(--dbg);
  }

  #pma_console .message,
#pma_console .query_input
  {
    color: var(--txt);
  }

  select,
select[multiple],
#select_seriesColumn,
#select_valueColumn
  {
    background: var(--dbg);
    border: none;
    border-radius: 0;
    height: auto;
    color: var(--txt);
  }

  #select_seriesColumn[disabled],
#select_valueColumn[disabled]
  {
    background: var(--disabled);
  }

  select:focus,
input[type=submit],
input[type=button],
input.button,
input[type=submit]:hover,
input[type=button]:hover,
.exportoptions #buttonGo,
.importoptions #buttonGo
  {
    height: auto;
    background: var(--button);
    border: none;
    border-radius: 0;
    text-shadow: none;
    color: var(--primary);
    text-transform: uppercase;
  }

  div#tablefieldscontainer select
  {
    background: var(--lbg);
  }

  #fieldset_user_priv div.item select
  {
    background: var(--dbg);
    border: none;
    border-radius: 0;
    text-shadow: none;
    color: var(--txt);
    height: auto;
  }

  .pma_quick_warp .drop_button,
.pma_quick_warp .drop_list:hover .drop_button,
.pma_quick_warp .drop_list li,
.pma_quick_warp .drop_list ul
  {
    background: var(--dbg);
    border: 4px solid;
    border-color: var(--dbg);
    border-radius: 0;
    color: var(--txt);
    box-shadow: none;
  }

  input[type=text]:hover,
input[type=password]:hover,
input[type=number]:hover,
input[type=date]:hover,
input[type=text],
input[type=password],
input[type=number],
input[type=date],
input[type=text]:hover,
input[type=text]:focus,
input[type=password]:hover,
input[type=password]:focus,
input[type=number]:hover,
input[type=number]:focus,
input[type=date]:hover,
input[type=date]:focus
  {
    background: var(--bg);
    box-shadow: none;
    text-shadow: none;
    height: auto;
    font-size: 12px;
    color: var(--txt) !important;
    border: none;
    border-bottom: 1px solid var(--accent);
    border-radius: 0;
  }

  button[type=submit]:not(.mult_submit),
input[type=reset],
input[name=submit_reset],
#sectionlinks a,
.buttonlinks a,
a.button
  {
    background: var(--button);
    box-shadow: none;
    text-shadow: none;
    height: auto;
    color: var(--txt) !important;
    border: none;
  }

  button[type=submit]:not(.mult_submit):hover,
input[type=reset]:hover,
input[name=submit_reset]:hover,
input.button:hover,
#sectionlinks a:hover
  {
    background: var(--hl);
    color: var(--selFg);
    box-shadow: none;
    text-shadow: none;
    height: auto;
    border: none;
  }

  li.fast_filter input
  {
    background: var(--lbg);
  }

  code.sql,
div.sqlvalidate
  {
    background: var(--lbg);
    font-size: 14px;
  }

  .result_query div.sqlOuter
  {
    background: var(--lbg);
  }

  #sectionlinks
  {
    border: none;
    border-radius: 0;
    background: var(--lbg);
    box-shadow: none;
  }

  .tblHeaders
  {
    color: var(--txt);
    background: var(--lbg);
    border: none;
    text-shadow: none;
  }

  th.print_ignore,
td.print_ignore
  {
    background: var(--lbg) !important;
    display: none;
  }

  #canvas,
#name-panel,
.side-menu
  {
    background: var(--lbg) !important;
    border: none;
  }

  #osn_tab
  {
    background: var(--lbg);
  }

  .pmd_tab
  {
    border: 1px solid;
    border-color: var(--txt);
  }

  a.M_butt_Selected_down_IE:hover,
a.M_butt_Selected_down:hover,
a.M_butt:hover
  {
    background: var(--lbg);
  }

  .ui-dialog .ui-dialog-content,
.ui-widget.ui-widget-content,
.ui-dialog .ui-dialog-buttonpane,
.ui-draggable .ui-dialog-titlebar
  {
    border: none;
    border-radius: 0;
    background: var(--bg);
  }

  .draggable span
  {
    text-transform: uppercase;
    font-weight: bold;
  }

  .ui-widget.ui-widget-content
  {
    box-shadow: 2px 2px 11px 0 var(--border);
  }

  .ui-widget-overlay
  {
    background: var(--dbg);
  }

  .ui-dialog .ui-dialog-buttonpane button
  {
    background: var(--dbg);
    border: none;
    border-radius: 0;
    color: var(--txt);
  }

  .tooltip,
.ui-widget-shadow
  {
    box-shadow: none;
    text-shadow: none !important;
    border: none !important;
    border-radius: 0 !important;
    background: var(--border) !important;
  }

  em
  {
    color: var(--keyword) !important;
  }

  td[data-type="int"] span
  {
    color: var(--number) !important;
  }

  .cEdit .edit_area
  {
    background: var(--lbg);
    color: var(--txt);
    border: var(--border);
  }

  .cEdit .cell_edit_hint
  {
    color: var(--primary);
  }

  .ui-widget-content
  {
    color: var(--txt);
  }

  #pma_console .message
  {
    border: none;
  }

  .CodeMirror-code
  {
    font-family: var(--mono);
  }

  #inline_editor_outer .CodeMirror
  {
    min-height: 12em;
  }

  .cm-s-default .cm-header
  {
    color: var(--link);
  }

  .cm-s-default .cm-quote
  {
    color: var(--string);
  }

  .cm-negative
  {
    color: var(--error);
  }

  .cm-positive
  {
    color: var(--success);
  }

  .cm-header, .cm-strong
  {
    font-weight: bold;
  }

  .cm-em
  {
    font-style: italic;
  }

  .cm-link
  {
    text-decoration: underline;
  }

  .cm-strikethrough
  {
    text-decoration: line-through;
  }

  .cm-s-default .cm-keyword
  {
    color: var(--keyword);
  }

  code .cm-s-default .cm-keyword:before
  {
    content: '';
    display: block;
  }

  .cm-s-default .cm-atom
  {
    color: var(--attribute);
  }

  .cm-s-default .cm-number
  {
    color: var(--number);
  }

  .cm-s-default .cm-def
  {
    color: var(--ttl);
  }

  .cm-s-default .cm-variable,
.cm-s-default .cm-punctuation,
.cm-s-default .cm-property,
.cm-s-default .cm-operator
  {
    color: var(--operator);
  }

  .cm-s-default .cm-variable-2
  {
    color: var(--variable);
  }

  .cm-s-default .cm-variable-3, .cm-s-default .cm-type
  {
    color: var(--type);
  }

  .cm-s-default .cm-comment
  {
    color: var(--comment);
  }

  .cm-s-default .cm-string
  {
    color: var(--string);
  }

  .cm-s-default .cm-string-2
  {
    color: var(--string);
  }

  .cm-s-default .cm-meta
  {
    color: var(--comment);
  }

  .cm-s-default .cm-qualifier
  {
    color: var(0--comment);
  }

  .cm-s-default .cm-builtin
  {
    color: var(--function);
  }

  .cm-s-default .cm-bracket
  {
    color: var(--operator);
  }

  .cm-s-default .cm-tag
  {
    color: var(--tag);
  }

  .cm-s-default .cm-attribute
  {
    color: var(--attribute);
  }

  .cm-s-default .cm-hr
  {
    color: var(--comment);
  }

  .cm-s-default .cm-link
  {
    color: var(--ttl);
  }

  .cm-s-default .cm-error
  {
    color: var(--error);
  }

  .cm-invalidchar
  {
    color: var(--error);
  }

  .CodeMirror-composing
  {
    border-bottom: 2px solid;
  }

  .CodeMirror-cursor
  {
    border-color: var(--accent);
    border-width: 2px;
    /*transform: translateY(5px);*/;
  }


/* Default styles for common addons */

  div.CodeMirror span.CodeMirror-matchingbracket
  {
    color: var(--ttl);
  }

  div.CodeMirror span.CodeMirror-nonmatchingbracket
  {
    color: var(--error);
  }

  .CodeMirror-matchingtag
  {
    background: rgba(255, 150, 0, .3);
  }

  .CodeMirror-activeline-background
  {
    background: var(--lbg);
  }

  span.cm-keyword, span.cm-statement-verb
  {
    color: var(--keyword);
  }

  span.cm-variable
  {
    color: var(--variable);
  }

  span.cm-comment
  {
    color: var(--comment);
  }

  span.cm-mysql-string
  {
    color: var(--string);
  }

  span.cm-operator
  {
    color: var(--operator);
  }

  span.cm-mysql-word
  {
    color: var(--txt);
  }

  span.cm-builtin
  {
    color: var(--function);
  }

  span.cm-variable-2
  {
    color: var(--variable);
  }

  span.cm-variable-3
  {
    color: var(--attribute);
  }

  span.cm-separator
  {
    color: var(--operator);
  }

  span.cm-number
  {
    color: var(--number);
  }

  .autocomplete-column-hint
  {
    color: var(--comment);
  }

  .insertRowTable .CodeMirror
  {
    border: none;
  }

  .navigation_separator
  {
    color: var(--primary);
    text-shadow: none;
  }

  .ic_s_db
  {
    background: url('data:image/svg+xml;utf8,<svg version="1.1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0" y="0" viewBox="0 0 32 32" width="16px" height="16px"><path id="file_type_ai.svg" class="i-color" fill="#F78C6C" d="M16 27.534c-5.323 0-9.641-1.44-9.641-3.214 0-0.97 0-2.031 0-3.214 0-0.279 0.14-0.545 0.341-0.803 1.075 1.384 4.813 2.41 9.301 2.41s8.226-1.026 9.301-2.41c0.201 0.259 0.34 0.524 0.34 0.803 0 0.931 0 1.918 0 3.214 0 1.773-4.318 3.214-9.641 3.214zM16 21.106c-5.323 0-9.641-1.441-9.641-3.214 0-0.97 0-2.030 0-3.214 0-0.17 0.064-0.336 0.151-0.5v0c0.049-0.103 0.113-0.204 0.19-0.304 1.075 1.383 4.813 2.41 9.301 2.41s8.226-1.027 9.301-2.41c0.077 0.1 0.141 0.201 0.19 0.304v0c0.086 0.164 0.151 0.33 0.151 0.5 0 0.931 0 1.917 0 3.214 0 1.773-4.318 3.214-9.641 3.214zM16 14.679c-5.323 0-9.641-1.44-9.641-3.214 0-0.508 0-1.044 0-1.607 0-0.511 0-1.041 0-1.607 0-1.774 4.318-3.214 9.641-3.214s9.641 1.44 9.641 3.214c0 0.501 0 1.033 0 1.607 0 0.492 0 1.011 0 1.607 0 1.774-4.318 3.214-9.641 3.214zM16 6.645c-3.55 0-6.427 0.718-6.427 1.607s2.878 1.607 6.427 1.607c3.55 0 6.427-0.718 6.427-1.607s-2.878-1.607-6.427-1.607z"></path></svg>');
  }
}
