css:
@-moz-document domain("bloxcity.com") {
.site-content, .col.s12.m12.l6, div {
background: #191919 !important;
}
h6, h5, h4, span, strong, center, .col.s4.center-align div, p, td, th, input, textarea, .col.s12.m12.l3.center-align div, .col.s12.m6.l6 div, .col.s9 div:nth-of-type(2), .col.s3.left-align, html {
color: rgba(255,255,255,0.87) !important;
}
.site-content {
box-shadow: 0 1px 2px #000000 !important;
}
.nav-wrapper.light-blue.darken-2 {
background-color: #013b5b !important;
}
.card-panel.orange.center-align {
background-color: #804d02 !important;
}
.red-ribbon, .blue-ribbon {
background: rgba(255,255,255,0) !important;
}
.tabs, .select-wrapper .dropdown-content.select-dropdown, .dropdown-content {
background-color: rgba(25,25,25,0.87) !important;
}
.col.s9 a {
background-color: rgb(25,25,25) !important;
}
tbody tr td a:first-child div {
color: rgb(200,200,200) !important;
}
tbody tr:hover {
transition: background-color 0.25s ease !important;
background-color: rgb(25,25,25) !important;
}
}
