css:
@-moz-document domain("game8.co") {
.archive-style-wrapper {
    background-color: #110127;
}
.a-paragraph {
    color: #faf6fb;
    background-color: transparent;
}
.a-link {
    color: #f8b3ff;
}
.a-red {
    color: #00ff00;
}
.a-table th {
    background-color: #f52dfd;
}
.a-header--3 {
    color: #f5d0f9;
}
.a-header--2 {
    background-color: #5f2065;
    color: #fceefd;
}
.a-btn {
    border: 1px solid #e300ff;
    color: #9a7bf0;
    background-color: #201524;
}
.p-gameHeader {
    background-color: #000;
}
.menuItem-header--3, .game_menu h3 {
    background-color: #ed97ff;
    color: #130025;
    text-shadow: 1px 1px 0px black;
}
.menuItem-link, .game_menu a {
    background-color: #1e003c;
}
a {
    color: #dc62f3;
}
html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, canvas, details, embed, figure, figcaption, footer, header, hgroup, menu, nav, output, ruby, section, summary, time, mark, audio, video {
    background-color: #0e0215;
    color: #f7c2fd;
}
html, body {
    background-color: #0e0215 !important;
    color: #f7c2fd;
}
.l-header {
    background-color: #1d123c;
}
.a-announce__inner p {
    color: #ef48c3;
}
.a-announce__inner {
    background-color: #000;
    border: 1px solid #dc00a7;
}
.p-gameNavText {
    border-top: 1px solid #b00fce;
    background-color: #7a1b7d;
}
.p-archiveHeader__title {
    color: #ffaffd;
}
.l-3colMain__center {
    background-color: #04000400;
}
.a-table th, .a-table td {
    color: #c55bd8;
    background-color: #0c0013;
}
.l-footerCorp {
    background-color: #130029;
}
.l-footerGame {
    background-color: #000000;
}
.l-footerCopyright {
    background-color: #000000;
}
.a-img {
    background-color: #3f007d;
}
.p-gameHeader__game_title {
    color: #eb7eff;
}
.p-gameNavText_list--grow .p-gameNavText_list_item_link {
    color: #db9ef5;
}
.p-archiveBreadcrumb {
    background-color: #0d000e;
}
.c-commentItem {
    background-color: #8c00ff;
    border-top: 1px solid #0d001e;
}
.c-btn--white {
    border: 1px solid #a539c6;
    background-color: #000000;
    color: #b639c6;
}
.a-header--2:first-child {
    background-color: #2d184a;
    color: #fbb4fd;
}
.c-heading--black {
    background-color: #1b0d25;
}
.c-search__input {
    background-color: #10001d;
    color: #00ff37;
}
.c-btn--red {
    color: #000000;
    background-color: #ef48de;
}
.c-tab__link.is-active {
    color: #04000e;
    background-color: #925dc5;
}
.p-rootFooterCopyright__outer {
    background-color: #000000;
}
.menuItem-header--4, .game_menu h4 {
    background-color: #9a5df1;
    color: black;
}
.cookies-eu {
    background-color: #430071;
}
.cookies-eu-content-holder, .cookies-eu-button-holder {
    background-color: #170531;
}
.cookies-eu .cookies-eu-button-holder .cookies-eu-ok {
    background-color: #6d4dfe;
}
.remodal {
    background: #3e178c;
}
.button-green {
    background-color: #bb4398;
}
.button-gray {
    color: #000;
    background-color: #8259bd;
}
.input--textarea {
    background-color: #1f1227;
    color: #ffffff;
}
.a-header--4 {
    color: #e65fff;
}
.c-heading--blue {
    background-color: #aa5eba;
}
.c-form__label {
    color: #ff6efb;
}
.c-formContainer {
    background-color: #000000;
}
.c-heading--blue .c-heading__icon {
    background-color: #aa5eba;
}
.c-input {
    border: 1px solid #fa76ff;
}
.c-input--textarea, .c-input--text {
    padding: 10px 4px 10px 10px;
    background-color: #261029;
    color: #fec6ff;
}
.c-btn--blue {
    color: #ffffff;
    background-color: #ad39c6;
}
.c-count .c-count__icon {
    background-color: #fb9bf1;
}
.c-count {
    background-color: #2a0b39;
}
.c-count .c-count__count {
    background-color: #2a0b39;
}
.c-count .c-count__icon svg {
    fill: #000000;
}
.c-heading--page {
    background-color: #25003a;
    color: #fb83fd;
}
.p-commentHeader__title a {
    color: #e476ff;
}
.c-heading__icon {
    fill: #ff63ff;
}
.c-heading--black .c-heading__icon {
    background-color: #1b0d25;
}
.a-outline p {
    background-color: #572761;
}
.p-archiveCommentListItem_comment {
    color: #c263f3;
}
.p-archiveCommentListItem_count {
    color: #af63e8;
}
.c-tabListItem__link {
    color: #956cf7;
}
}
