css:
@-moz-document domain(idealo.de) {

/* An Idealo re-color by u/Apache3348 */

/* License info: Feel free to change the style at any time and reupload it 
(note: Leave this tag here and add your own below) */


/* Colors
    Go to color.adobe.com if you want to get some preset color packs
     You can change these at any given point of time
      Invert the Accent and Background Color if you want light theme */

:root {
    --Accent-color: #F2EBDF;
    --Background: #262523;
    --Highlight: #d97547;
}

/* Color Section END */



/* ----------------------- Style-optimization -----------------------
 (Experimental) Gets rid of the white flash when website loads
 Btw you should be able to add this to any website and it should work */

html {
    background-color: var(--Background);
}

/*Ends here*/


/* reset of the page, so it is easily editable  */
*,
*:after,
*:before{
    background-color: var(--Background)!important;
    border: none !important;
    box-shadow: none !important;
    color: #F2EBDF !important;
}


/* ----------------------- START OF THE ACTUAL STYLE -----------------------
   -----------------------     EDIT AT OWN RISK      -----------------------

 Changes some text I forgot to add  */
.h1 {
       color: var(--Accent-color)  !important; 
}


/* header */

.header,
.i-header-row,
.i-header,
.i-header-claim {
    background: none !important;
    background-color: var(--Background) !important;
    color: var(--Accent-color)  !important;
}


/* Basic changes of the Website */

.body,
.teaser-container,
.wishlist-item-list-front,
.stage,
.offerList-item-description-title,
.myidealo-menu--loggedIn,
.offerList-item-description-text,
.productOffers-listItemOffer,
.small-24,
.carousel-slideLink,
.aboutIdealo-itemContentTitle,
.actions-pricewatcher,
.productOffers-listItem,
.filter-tab,
.tab-group,
.tab-categories,
.price__StyledPrice-sc-1hpi5re-1,
.productOffers-listItemTitleInner,
.title,
.myidealo-menu-username,
.myidealo-menu-entries,
.myidealo-menu-body,
.myidealo-menu-footer,
.carousel-slideImage,
.stage-title,
.i-search-input,
.i-search-button,
.carousel-slideHintButton,
.carousel-navigationButton,
.actions-compareProduct,
.actions-deleteItem,
.overlay-titleInner,
.overlay-content,
.i-button,
.swiper-wrapper,
.wishlist-item-tile-front-details-title,
.icon,
.wishlist-container,
.i-header,
.overlay-contentWrapper,
.datasheet-list,
.datasheet,
.SearchItem__StyledSearch-sc-1me3ulu-0,
.button,
.datasheet-listItem,
.oopStage-variantLink,
.oopStage-variantWrapper,
.recent-products-category,
.recent-products-content,
.swiper-slide,
.oopStage-actionPricewatcher,
.pageFooter-optinTitle,
.recent-products-name,
.productOffers-header-sortText,
.oopMarginal-wrapperListItem,
.newPriceChart-actionPricewatcher,
.productOffers-headerTitle,
.oopMarginal-wrapperTitle,
.bargains-newsletter-label,
.oopMarginal-wrapper,
.oopMarginal-wrapperTitle,
.oopMarginal-wrapper,
.productOffers-listItemOfferLogoShop,
.small-12 ,
.oopStage-title,
.carousel-slideTitle,
.oopStage-productInfoTopItemWrapper,
.carousel--recentProducts,
.pageFooter-optin,
.pageFooter-top,
.aboutIdealo-itemContentText,
.carousel-slideDate,
.breadcrumb-leaf,
.icon_arrow-right-thin,
.description-part-one,
.breadcrumb-leafSeparator,
.carousel-slideType {
    background: none !important;
    background-color: var(--Background) !important;
    color: var(--Accent-color)  !important;
}


/*Wishlist page and navigation menu*/
.i-wishlist-element-heart,
.i-wishlist-element-text,
.i-wishlist-element-content,
.datasheet-closeButton,
.productOffers-listItemOfferGreyBadge,
.productOffers-header-sortLabel,
.productOffers-listItemOfferVoucherMessage,
.myidealo-button-icon,
.myidealo-button-wrapper {
    background: none !important;
    background-color: var(--Background) !important;
    color: var(--Highlight) !important;
}


/* Custom Hover-Hightlights*/
.carousel-navigationButton:hover {
    color: var(--Highlight)  !important;
}
.myidealo-menu-username:hover {
    color:var(--Highlight) !important;
}
.myidealo-menu-entries:hover {
      color: var(--Highlight) !important;
}
.myidealo-menu-body:hover {
     color: var(--Highlight)  !important;
}
.myidealo-menu-footer:hover {
     color: var(--Highlight)  !important;
}

/* Price-Hightlight and other important things */
.priceFrom,
.carousel-slidePriceNow,
.i-search,
.testRating,
.table-cell,
.carousel-slidePriceOld {
    background: none !important;
    background-color: var(--Background) !important;
    color: var(--Highlight) !important;
}


/* Idealo-Ads */

/* Add "display:none" if you want to hide them */
.aboutIdealo-title,
.stage-titleHint,
.flightRoutes-brandLogo,
.flightRoutes,
.pageFooter-appTitle,
.pageFooter-appBadgeLinkImage,
.flightRoutes-brand {
    background: none !important;
    background-color: var(--Background) !important;
    color: var(--Accent-color)  !important;
}

/*footer*/

.footer,
.pageFooter-bottom {
    background: none !important;
    background-color: var(--Background) !important;
    color: var(--Accent-color)  !important;
}

/* Some highlights */
.icon,
.info-datasheet--action,
.ListItem__IconHeart-sc-1s395hg-1,
.layoutSwitch-item,
.i-navigation-hamburger,
.ListItem__IconCollapse-sc-1s395hg-0 {
    color: var(--Highlight)  !important;
}


/* Hyperlinks */
a {
    background-color: var(--Background) !important;
    color: var(--Highlight)  !important;
}

a:hover {
     color: var(--Accent-color)  !important;
}

/* Rating-Color */
.rating-starsImage {
    background-color: var(--Background) !important;
    color: var(--Accent-color)  !important;
}



}
