css:
@-moz-document domain("newlms.magtu.ru") {
@font-face {
    font-family: "Fira Code";
    src: url(https://cdn.jsdelivr.net/gh/tonsky/FiraCode@1.207/distr/fira_code.css);
}


body {
    background: #f5f5f5;
    font-family: 'Fira Code';
}

img {
    filter: brightness(0.96);
}

#header .header-menubar {
    background: #272640;
}

.header-main {
    background: #272640;
    box-shadow: 0 3px 8px rgba(0, 0, 0, 0.5);
}

.search-input-wrapper>form>input {
    background: #272640;
}

.usermenu .login {
    font-size: large;
}

.usermenu .login a {
    color: #93bbd4;
    text-transform: uppercase;
}

.dir-ltr .titlebar .slidenav .nav-next i,
.dir-ltr .titlebar .slidenav .nav-prev i {
    top: 1px;
}

.titlebar .slidenav .nav-item {
    position: absolute;
    top: 60px;
    
    background: #222640;
    color: #f5f5f5;
    border-radius: 10px;
    border: none;
    z-index: 5;
}

.titlebar .slidenav .nav-prev {
    left: 15px;
}

.titlebar .slidenav .nav-next {
    right: 15px;
}

#region-main-box {
    display: flex;
}

@media (min-width: 768px) {
    .col-md-9 {
            flex: 1 0 50%;
    }
}

#site-news-forum {
    border-color: #f5f5f500;
}

#site-news-forum h2 {
    background: #2D3359;
    border-radius: 8px;
    color: #f5f5f5;
    border: none;
    box-shadow: 3px 3px 10px rgba(0, 0, 0, 0.3);
    text-align: center;
}

.block-region .block .card-body .card-title {
    background: #2D3359;
    color: #f5f5f5;
    border-radius: 5px;
    font-family: 'Open Sans';
    text-align: center;
    box-shadow: 3px 3px 10px rgba(0, 0, 0, 0.3);
}

.block-region .block .card-body {
    border-radius: 5px;
}

.forum-post-container {
    margin-bottom: 30px !important;
    border-radius: 10px;
    box-shadow: 3px 3px 10px rgba(0, 0, 0, 0.3);
}

.post-content-container p span {
    font-family: 'Open Sans' !important;
    font-size: 18px !important;
}

#site-news-forum .forumpost {
    border-radius: 10px;
}

.forumpost.unread .row.header {
    background: #f5f5f5;
}

.forumpost .row.header {
    border-bottom: #c5c5c5 solid 1px;
}

.p-2 {
    padding: 20px !important;
}

.author-groups-container {
    display: none;
}

[data-region="blocks-column"] {
    min-width: 250px;
    max-width: 750px;
    width: auto;
}

.container {
    position: relative;
    max-width: 1920px;
}

.homepage-carousel {
    display: none;
}

#magtu_banner_div_all {
    margin: auto;
}

.block .minicalendar {
    max-width: 400px;
}

.custom-courses-list .course-box .thumb {
    margin: auto;
    background: #f5f5f5;
}
.custom-courses-list .course-box .info {
    font-family: 'Open Sans';
    text-align: center;
}

.custom-courses-list .course-box .thumb a img {
    filter: brightness(0.96);
}

.mb-3 {
    background: #f5f5f5;
}

.course-content .section li.activity {
    padding-bottom: 0 !important;
}

h3 {
    text-align: center;
}

/*
.course-content ul li.section.main .section li.modtype_url,
.course-content ul li.section.main .section li.modtype_folder,
.course-content ul li.section.main .section li.modtype_resource {
    margin: auto;
    margin-bottom: 20px;
    min-width: 400px;
    max-width: 1200px;
    border-radius: 5px;
    --background: #2D3359;
    box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.3);
}
*/

.course-content ul li.section.main .section li.modtype_label {
    display: block;
    width: 600px;
    margin: auto;
}

.course-content .topics .section .activity .activityinstance a {
    --color: #f5f5f5;
    color: #272640;
}

#page-wrapper #page {
    min-height: calc(100vh - 280px - 198px);
}

#region-main {
    background: #f5f5f5;
}

#region-main-settings-menu {
    width: 0px !important;
}

#page-login-index .card {
    background: #2D3359;
    margin-top: calc(25vh - 180px);
}

.yui3-skin-sam .yui3-tabview-panel {
    background: #f5f5f5;
    border: none;
}

.yui3-skin-sam .yui3-tab-label {
    background: none;
}

.yui3-skin-sam .yui3-tab-selected .yui3-tab-label,
.yui3-skin-sam .yui3-tab-selected .yui3-tab-label:focus,
.yui3-skin-sam .yui3-tab-selected .yui3-tab-label:hover {
    background: #2D3359;
}

.yui3-skin-sam .yui3-tabview-list {
    border-top: none;
    border-bottom: #2D3359 solid 1px;
}

.region_main_settings_menu_proxy { width: 0px; }

#region-main-settings-menu>div {
    left: 0;
}

.coursebox .content .summary {
    display: inline-block;
    padding: 0;
    padding-left: 10px;
}

.coursebox .content .courseimage {
    position: inherit;
}

.course_category_tree .category>.info {
    background: #2D3359;
    border-radius: 5px;
    box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.5);
}

.course_category_tree .category>.info>.categoryname>a {
    color: #f5f5f5;
}

.course_category_tree .category.with_children.collapsed>.info>.categoryname {
    filter: brightness(10);
}

.course_category_tree .category.with_children>.info>.categoryname {
    filter: brightness(10);
}

.formulation {
    font-size: 18px;
}

.answer div {
    display: flex;
    justify-content: flex-start;
}

input[type="radio"], input[type="checkbox"] {
    width: 20px !important;
    height: 20px;
}

label.ml-1 {
    margin-bottom: 4px
}

.alert-info, #page-admin-index .releasenoteslink, #adminsettings .form-overridden, .que .formulation, .assignfeedback_editpdf_widget .label {background: none;}
.alert-info, #page-admin-index .releasenoteslink, #adminsettings .form-overridden, .que .formulation, .assignfeedback_editpdf_widget .label {background: none;}

.que .info {
    background: #2D3359;
    color: #f5f5f5;
    box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.3);
}

.que .info h3 {color: #f5f5f5;}
.que input.questionflagimage {filter: brightness(5);}

#page-mod-quiz-attempt .submitbtns {text-align: center;}
input[type="submit"] {
    height: 80px;
    background: #2D3359;
    box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.3);
    border-radius: 3px;
}

input[type="button"] {
    background: #2D3359;
    box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.3);
    border-radius: 3px;
    color: #f5f5f5;
}

input[type="button"]:hover {color: #f5f5f5;}

.path-mod-quiz #mod_quiz_navblock .qnbutton .trafficlight {height: 0;}
.path-mod-quiz #mod_quiz_navblock .qnbutton {line-height: 40px;}

.quizsummaryofattempt thead tr th {background: #2D3359;}
.quizattemptsummary thead tr th {background: #2D3359;}

#region-main .singlebutton .btn, #region-main .singlebutton #page-grade-grading-manage .actions .action,
#page-grade-grading-manage .actions #region-main .singlebutton .action,
#region-main .singlebutton #rubric-rubric.gradingform_rubric #rubric-criteria .criterion .addlevel input, #rubric-rubric.gradingform_rubric #rubric-criteria .criterion .addlevel #region-main .singlebutton input,
#region-main .singlebutton #rubric-rubric.gradingform_rubric .addcriterion,
#rubric-rubric.gradingform_rubric #region-main .singlebutton .addcriterion {
    background: #2D3359;
    color: #f5f5f5;
    box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.3);
    border-radius: 3px;
}

.moodle-dialogue-confirm .moodle-dialogue {width: auto !important; left: calc(50vw - 315px) !important;}

.message-app.drawer {
    width: 30vw;
}
.message-app.hidden {
    right: -30vw;
}

#footer {
    height: 280px;
}

#footer .footer-main {
    background: #272640;
}

@media (min-width: 1280px) {
    .col-md-3 {
        --max-width: 40%;
        min-width: 30%;
    }
}

@media (max-width: 480px) {
    .titlebar .slidenav .nav-item {
        top: 0;
    }
}

/*--Scroll Bar--*/
::-webkit-scrollbar {
  width: 0.8em !important;
  height: 0.5em !important;
}
::-webkit-scrollbar-thumb {
  background: #6A8FA6 !important;
}
::-webkit-scrollbar-track {
  background: #2D3359 !important;
}

.availabilityinfo {
    text-align: center;
}

.badge-info {
    background: none;
    font-size: 24pt;
    color: #b0b0b0;
}

}
