css:
@-moz-document domain("redcafe.net") {
body{
    max-width: 100%;
    margin: 0 auto;
}

html{
    background: #1f171a;
    font-family: 'Segoe UI','Helvetica Neue',Helvetica,Roboto,Oxygen,Ubuntu,Cantarell,'Fira Sans','Droid Sans',sans-serif;
}

a {
    color: #b59c9c;
}

a:hover{color:#e1e3e3;text-decoration:underline}


.structItem-title{
    font-size: 17px;
    font-weight: 300;
}

.message-name{
    font-size: 17px;
    font-weight: bold;
} 

.p-body, .p-body-header {
    background:#272024;
}

.p-body-header{
border-radius: 0;
}

.p-nav-list .p-navEl.is-selected {
    color: #c0781d;
font-weight: 500;
background:    #101010;
}

.uix_headerContainer, .block, .breadcrumb {
    background: #272024;
}

.block-container {
    color: #e1e3e3;
    background:#292024;
    border-width: 0;
    padding:0px;
    border-radius: 0px !important;
}

.block--latest-posts-widget .block-container {
    padding: 0px;
    background: #272024;
}

.block--category .block-header, block-header js-nodeMain {
    background: #272024;
color:    #babebe;
    font-weight: 100;
    font-size: 21px;
}

.block-textHeader {
    margin: 8px 0;
        margin-top: 8px;
    padding: 0;
    font-weight: 400;
    font-size: 1.6rem;
    color:     #ddd;
}

.memberTooltip-header {
    display: table;
    table-layout: fixed;
    width: 100%;
    padding: 10px;
    background:#392f2f;
border-bottom: 1px solid    #d4d4d4;
    display: flex;
}

.node-body  {
    background: #292024;
    color:#fff;
    border-top: 1px solid #40343a !important;
    padding:0px;
}


.node .node--id73 .node--depth2 .node--forum .node--unread{
        background: #292024 !important;
    color:#fff;
    padding:0px;  
}

.node--depth2:nth-child(2n) .node-body {
        background: #292024 !important;
    color:#fff;
}

.node-body .node-extra a:not(:hover) {
    color: #babebe;
}

.node-body .node-extra a {
    color: #babebe;
}

.node-subNodeFlatList > li a {
    color:#babebe;
}

.p-nav-list .p-navEl.is-selected {
    color:#aa3f34;
font-weight: 500;
background:    #292024;
}

.structItem {
    background-color: #292024 !important;
    display: table;
border-top: 1px solid #40343a;
list-style: none;
margin: 0;
padding: 0;
width: 100%;
}

.structItem-pageJump a {
    color: #e1e3e3;
background:#372c32;
border: 1px solid    #40343a;
    display: inline-block;
    max-width: 100%;
    padding: 0 3px 1px;
    margin: 0 0 2px;
    border-radius: 0px;
    text-decoration: none;
    border-radius: 4px;
    padding: 0 3px;
    opacity: .5;
    -webkit-transition: all .2s ease;
    transition: all .2s ease;
}

.structItem:nth-child(2n) {
    background-color: #292024 !important;
}

.is-unread .structItem-title {
    color: 
    #d00000;
    font-weight: 700;
}


.badge--highlighted, .badge{
    background:#ffffff;
    color: #e0e0df;
    font-weight: bold;
}

.u-dt{
    color:#997070;
}

.username--invisible {
    color: #b09a9a;
}

.message--post{
    border-top:10px solid black;
}    

.uix_nodeList{
}


.block--latest-posts-widget.block > .block-container{
    border-radius:0px;
}

.p-body-sidebar{
    margin-right:0px;
}


.uix_extendedFooter, .uix_extendedFooterRow .block{
    background:#c20000;
    color: #ffffff;
}

.block-minorHeader.uix_threadListSeparator {
    display: flex;
    align-items: center;
    font-size: 1.3rem;
    color: #ffffff;
    background:#1f171b;
    border-top: 1px solid    #40343a;
}

.structItemContainer .structItemContainer-group--sticky .structItem--thread {
    border-left: 5px solid #40343a;
}

.message-cell.message-cell--user, .message-cell.message-cell--action {
    position: relative;
    font-size: 1.5rem;
    background: #372c32;
    border-right: 1px solid #40343a;
    min-width: 0;
    color: #000000ba;
    font-family: segoe ui;
}

.menu, .menu--structural, .menu--left, .is-active is-complete{
    z-index:5000;
}

.p-breadcrumbs{
    z-index:1000;
}

.p-breadcrumbs {
    color:    #b0473c;
    border: 1px solid #40343a;
    padding:10px;
    font-size:13px;
}

.p-breadcrumbs a{
    color:    #b04439 !important;
}

.block--messages .message .message-attribution--split, .js-quickReply .message .message-attribution--split, .block--messages .block-row .message-attribution--split, .js-quickReply .block-row .message-attribution--split {
    color:#eee;
background-color:#313131;
    padding: 4px 10px;
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
}


span.username{
    font-size:11px;
}

.bbCodeBlock-content .bbImage{
    width:20%;
}

.fauxBlockLink-blockLink{
    font-weight: bold;
    font-size:14px;
}

.badge.badge--highlighted, .badgeContainer.badgeContainer--highlighted::after {
    display: inline-block;
    color: #e0e0df;
    background:#ffffff;
    font-weight:bold;
}

.block--latest-posts-widget .structItem-cell.structItem-cell--meta  {
display: none;
}

.structItem-cell.structItem-cell--meta  {
    background: transparent;
    border: 0px;
}

.block-tabHeader {
background:#c61818;
    color:#fff;
}

.block-tabHeader .tabs-tab {
background:    #c61818;
color:#fff;
}

.block-tabHeader .tabs-tab.is-active {
    color:#fff;
   
}

.block-tabHeader .tabs-tab.tabs-tab--gapLeft {
    border-left: 0px solid     #f9f6f6;
        border-left-color: rgb(236, 234, 234);
    border-radius: 0;
}

.block-tabHeader .tabs-tab.tabs-tab--gapRight {
    border-right: 0px solid #7b7b7b;
    border-radius: 0;
}

.actionBar-set.actionBar-set--internal {
    opacity: 0.5;
}

.p-body-header .p-title-value {
    padding: 0;
    margin: 0 0 3px 0;
        margin-right: 0px;
    font-size: 2.3rem;
    font-weight: 400;
    margin-right: auto;
    min-width: 0;
    color: #babebe;
    font-family: 'Roboto',-apple-system,BlinkMacSystemFont,'Segoe UI','Oxygen','Ubuntu','Cantarell','Fira Sans','Droid Sans','Helvetica Neue',sans-serif;
}

.p-body-header .p-description {
    margin: 0;
    padding: 0;
    font-size: inherit;
    color: #a27272;
}

.node-stats {
    display: inline-flex;
    align-items: left;
    vertical-align: middle;
    text-align: center;
    color: #babebe;
    padding: 5px 0;
    opacity: 1;
}

@media only screen and (max-width: 800px) {
.node-stats {
    display: none;
}
 .uix_nodeList{
    width: 100%;
}   
    
    .block--latest-posts-widget.block > .block-container{
    width: 100%;
}
   
}

.node-stats dt {
    color: #997070;
}

.node--unread .node-title {
    color: #babebe;
}

.node-title {
    color: #babebe;
}

.node-body .node-extra .node-extra-title:not(:hover) {
    color: #babebe;
}

.node-extra .node-extra-title {
    color:#babebe;
}

.node-icon {
    filter:hue-rotate(340deg) saturate(1) contrast(65%);
}

.smilie {
    filter:hue-rotate(270deg);
}

.label {
    color: #fff !important;
background:#884259 !important;
border-color:#5e374e00 !important;
    padding:2px !important;
    padding-left:7px !important;
    padding-right:7px !important;
    font-size:12px !important;
}

.uix_logo {
    filter:grayscale(30%) contrast(55%);
}

.p-header {
    color: rgba(255,255,255,0.7);
background: #372c32;
    padding-top: 16px;
    padding-bottom: 16px;
}

.p-nav {
    color: rgba(255,255,255,0.7);
    background:  #372c32;
    transition: ease-in 1.5s all;
}

.p-nav-scroller .hScroller-action.hScroller-action--end {
    background: #372c32;
    background: linear-gradient(to right, rgba(208,0,0,0) 0%,    #372c32 33%);
}

.p-nav-scroller .hScroller-action.hScroller-action--start {
    background: #372c32;
    background: linear-gradient(to right, rgba(208,0,0,0) 0%,    #372c32 33%);
}

.label {
    display: inline-block;
    padding: 1px .35em;
    border: 1px solid 
    transparent;
        border-top-color: transparent;
        border-right-color: transparent;
        border-bottom-color: transparent;
        border-left-color: transparent;
    border-radius: 4px;
    font-size: 80%;
    line-height: 1.26;
    text-decoration: none;
}

.p-nav{
    font-size:12px;
}

.p-nav-list .p-navEl {
    font-size: 12px;
    color: 
    rgba(255,255,255,0.7);
    font-weight: 500;
    text-decoration: none;
    text-transform: uppercase;
    line-height: 1;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    height: 40px;
    -webkit-transition: all .2s ease;
    transition: all .2s ease;
}

.node-subNodeMenu{
    display:none;
}

.node-description{
    font-size:12px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  max-width: 200px;
    font-family: segoe ui;
    }

.block--messages .message-cell:last-child, .js-quickReply .message-cell:last-child {
    background:#292024;
    color: #babebe;
}

.block block--messages{
    min-width: 900px;
}

.blockLink {
    color: #ececec;
background:    #292024;
}

.blockLink.is-selected {
    color:#fff;
font-weight: 500;
    background:#372b31;
    border-left: 3px solid    #78251c;
    padding-left: 13px;
}

.dataList-row.dataList-row--header .dataList-cell {
    color:#fff;
font-weight: 500;
background:#101010;
border-bottom: 1px solid    #bababa;
}

.block--latest-posts-widget .block-container {
    padding: 0px;
    background: transparent;
    box-shadow: 0px 0px 0px 0    rgba(0,0,0,0.2);
}

.node, .node--id6 node--depth2, .node--forum node--unread {
    list-style: none;
    margin: 0;
    padding: 0;
    box-shadow: 0px 0px 0px 0    rgba(0,0,0,0.2);
}

.uix_nodeList .block-body {
    box-shadow: 1px 1px 2px 0     rgba(0, 0, 0, 0);
}

.block[data-widget-id] .block-minorHeader, .p-body-sideNav .block-minorHeader, .p-body-sidebar .block-minorHeader, .uix_extendedFooterRow .block-minorHeader, .columnContainer-sidebar .block-minorHeader, .block[data-widget-id] .block-header, .p-body-sideNav .block-header, .p-body-sidebar .block-header, .uix_extendedFooterRow .block-header, .columnContainer-sidebar .block-header, .block-minorHeader {
    display: flex;
    align-items: center;
    padding: 16px;
    font-weight: 500;
    border-bottom: 1px solid #dbdbdb;
    border-radius: 0px !important;
}

.block:not(.block--messages) .block-container:not(.block-container--noStripRadius) > .block-body:last-child > .message:last-child .message-cell:first-child, .block:not(.block--messages) .block-bottomRadiusContent.message .message-cell:first-child, .block:not(.block--messages) .block-bottomRadiusContent > .message:last-child .message-cell:first-child {
    border-radius: 0px;
}

.block:not(.block--messages) .block-container:not(.block-container--noStripRadius) > .block-body:last-child > .message:last-child .message-cell:last-child, .block:not(.block--messages) .block-bottomRadiusContent.message .message-cell:last-child, .block:not(.block--messages) .block-bottomRadiusContent > .message:last-child .message-cell:last-child {
    border-radius: 0px;
}

.pageNav-page {
background: #2f252a;
color:#f9f9f9;
border-radius: 0px;
border: 1px solid   #40343a;
    white-space: nowrap;
    height: 10px;
}

.pageNav-page:not(:first-child) {
    border-left-color:    #40343a;
}

.pageNav-page a, .pageNav-jump {
    color: #f9f9f9;
}

.pageNav-page.pageNav-page--current {
    color: #d00000;
box-shadow: 0 0px    #d00000 inset;
    background: #78251c;
    border: 1px solid   #a13226;
}

.pageNav-jump {
    display: inline-block;
background: #2f252a;
color:#f9f9f9;
border-width: 0;
border-style: solid;
border-top-color: #d4d4d4;
border-right-color: #c7c7c7;
border-bottom-color: #bababa;
border-left-color: #c7c7c7;
border-radius: 0;
border: 1px solid   #40343a;
    white-space: nowrap;
    height: 24px;
    line-height: 24px;
    padding: 0 8px;
    height: 26px;
    line-height: 26px;
}

.block-filterBar {
    padding: 8px 8px;
    font-size: 1.3rem;
    color: #f7f7f7;
    background:#78251c;
    border-radius: 0px !important;
}

