css:
@-moz-document url-prefix("https://everydayastronaut.com") {
body, #wrapper, #page_content_wrapper.fixed, #page_content_wrapper .sidebar .content .sidebar_widget li h2.widgettitle span, h2.widgettitle span, #gallery_lightbox h2, .slider_wrapper .gallery_image_caption h2, #body_loading_screen, h3#reply-title span, .flex-viewport, #page_caption, .top_bar, .footer_bar {
    background-color: #111;
}
#logo_wrapper {
    border-bottom-color: #333;
}
.top_bar, .footer_bar, hr, h2.widgettitle, h2.widgettitle::before, table tr th, table tr td, .post_caption .post_detail {
    border-color: #333;
}
#page_caption h1, .ppb_title, .post_caption h1 {
    color: #aaa;
}
h1, h2, h3, h4, h5, h6, h7, pre, code, tt, blockquote, .post_header h3 a, .post_header.fullwidth h4 a, .post_header h5 a, .post_header.grid h6 a, .site_loading_logo_item i, .post_header h6, #footer, #copyright {
    color: #888;
}
a:hover, .post_header h5 a:hover {
    color: #d8d8d8;
}
#menu_wrapper div .nav > li.current-menu-item > a, #menu_wrapper div .nav > li.current-menu-parent > a, #menu_wrapper div .nav > li.current-menu-ancestor > a, #menu_wrapper div .nav li ul li.current-menu-item a, #menu_wrapper div .nav li.current-menu-parent ul li.current-menu-item a {
    color: #ccc;
}
#menu_wrapper .nav ul li ul, #menu_wrapper div .nav li ul {
    background-color: #222;
    border-color: #333;
}
#menu_wrapper .nav ul li ul li a:hover, #menu_wrapper div .nav li ul li a:hover, #menu_wrapper div .nav li.current-menu-parent ul li a:hover, #menu_wrapper .nav ul li.megamenu ul li ul li a:hover, #menu_wrapper div .nav li.megamenu ul li ul li a:hover, #menu_wrapper .nav ul li.megamenu ul li ul li a:active, #menu_wrapper div .nav li.megamenu ul li ul li a:active {
    background-color: #282828;
}
#menu_wrapper .nav ul li a.hover, #menu_wrapper .nav ul li a:hover, #menu_wrapper div .nav li a.hover, #menu_wrapper div .nav li a:hover {
    color: #ccc;
}
.prelaunch_previews_tooltip .prelaunch_previews_tooltiptext {
    background-color: #282828;
    color: #888;
}
.post_excerpt.post_tag a {
    background-color: #222;
}
#page_content_wrapper .sidebar .content .sidebar_widget li h2.widgettitle span, h2.widgettitle span {
    color: #555;
}
#copyright a, #copyright a:active, #footer a, #footer a:active, #footer_menu li a, #footer_menu li a:active {
    color: #888;
}
.post_attribute, .comment_date {
    color: #444;
}
.wp-caption p.wp-caption-text {
    color: #aaa;
}
.footer_bar_wrapper {
    border-color: #555;
}
input[type="text"], input[type="password"], input[type="email"], input[type="url"], textarea, input[type="tel"] {
    background-color: #222;
    border-color: #555;
    color: #888;
}
#commentform input[type="text"]:focus, #commentform input[type="email"]:focus, #commentform input[type="url"]:focus, #commentform textarea:focus {
    border-color: #aaa;
}
input[type="submit"], input[type="button"], a.button, .button, .pagination a:hover, .woocommerce .footer_bar .button, .woocommerce .footer_bar .button:hover, .woocommerce-page div.product form.cart .button, .woocommerce #respond input#submit.alt, .woocommerce a.button.alt, .woocommerce button.button.alt, .woocommerce input.button.alt, input, textarea, select, option {
    color: #888;
    background-color: #222;
    border-color: #222;
}
input[type="button"]:hover, input[type="submit"]:hover, a.button:hover, .button:hover, .button.submit, a.button.white:hover, .button.white:hover, a.button.white:active, .button.white:active {
    color: #aaa;
    background-color: #222;
    border-color: #222;
}
.top-bar a, .site-header a, .top-bar .search-bar__input::placeholder, .sub-footer, .site-footer, .sub-footer a, .site-footer a {
    color: #888;
}
.icon-search {
    fill: #888;
}
.site-nav__item:first-child, .site-nav__item {
    border-color: #222;
}
.site-nav__link, .breadcrumb-nav, .breadcrumb-nav a, .product-form, .product-single__description {
    color: #555;
}
.product__title a, .collection__title a, .product__prices, .product-single__price {
    color: #888;
}
.pagination a, .pagination span {
    background-color: #181818;
}
.pagination a:hover, .pagination span:hover {
    background-color: #222;
    color: #aaa;
}
.pagination .current, .pagination .current:hover {
    background-color: #222;
    color: #fff;
}
img {
    opacity: .7;
}
#post_featured_slider li {
    opacity: .8;
}
}
