css:
@-moz-document regexp(".*youtube.com/watch.*") {
  @media only screen and (max-width: 656px) {
      #masthead > #container, #logo-red-icon-container
      {
          height: 10px;
      }

      #masthead > #container .style-scope
      {
          height: 10px;
          padding: 0;
      }

      #masthead > #container .style-scope > span#country-code 
      {
          display: none;
      }

      #masthead:hover > #container .style-scope > span#country-code 
      {
          display: block;
      }

      #masthead > #container > #search.style-scope, 
      #masthead > #container > #search.style-scope > form#search-form.style-scope,
      #masthead > #container > #search.style-scope > form#search-form.style-scope > div#container.style-scope,
      #masthead > #container > #search.style-scope > form#search-form.style-scope > div#container.style-scope > div#search-input,
      #masthead > #container > #search.style-scope > form#search-form.style-scope > div#container.style-scope > div#search-input > div > div,
      #masthead > #container > #search.style-scope > form#search-form.style-scope > div#container.style-scope > div#search-input > div > div > #search,
      #masthead > #container > #search.style-scope > form#search-form.style-scope > button#search-icon-legacy
      {
          height: 30px !important;
      }

      #masthead > #container > #search.style-scope,
      #masthead > #container > #back-button
      {
          margin-top: 30px;
          padding: 8px;
          height: 40px;
          width: 40px; 
      }

      #masthead:hover > #container > #search.style-scope
      {
          margin-top: 0;
          padding: 0;
      }

      #masthead:hover > #container > #back-button
      {
          margin-top: 0;
      }

      #masthead > #container > #back-button > button#button,
      #masthead > #container > #back-button > button#button > .style-scope
      {
          height: 24px;
          width: 24px;
      }

      #masthead:hover > #container #logo-red-icon-container,
      #masthead:hover > #container #end
      {
          height: auto;
      }

      #masthead:hover,
      #masthead:hover > #container
      {
          height: 56px;
      }

      #masthead:hover > #container #end.style-scope.ytd-masthead
      {
          height: 40px
      }

      #masthead:hover > #container #buttons.style-scope.ytd-masthead,
      #masthead:hover > #container #buttons.style-scope.ytd-masthead > .style-scope.ytd-masthead,
      #masthead:hover > #container #buttons.style-scope.ytd-masthead > .style-scope.ytd-masthead > div#button.style-scope,
      #masthead:hover > #container #buttons.style-scope.ytd-masthead > .style-scope.ytd-masthead > div#button.style-scope > a,
      #masthead:hover > #container #search-button.style-scope > .style-scope.yt-icon-button.circle, 
      #masthead:hover > #container #search-button.style-scope > .style-scope.yt-icon-button.circle > .style-scope,
      #masthead:hover > #container #buttons.style-scope.ytd-masthead > .style-scope.ytd-masthead > div#button.style-scope > a > #button > .style-scope.yt-icon-button.circle,
      #masthead:hover > #container #buttons.style-scope.ytd-masthead > .style-scope.ytd-masthead > div#button.style-scope > a > #button > .style-scope.yt-icon-button.circle > .style-scope
      {
          height: 40px;
      }

      #masthead:hover > #container #search-button.style-scope,
      #masthead:hover > #container #buttons.style-scope.ytd-masthead > .style-scope.ytd-masthead > div#button.style-scope > a > #button,
      #masthead:hover > #container #guide-button
      {
          padding: 8px;
          height: 40px;
          width: 40px; 
      }

      #masthead:hover > #container #search-button.style-scope > button#button,
      #masthead:hover > #container #search-button.style-scope > button#button > .style-scope,
      #masthead:hover > #container #buttons.style-scope.ytd-masthead > .style-scope.ytd-masthead > div#button.style-scope > a > #button > button#button,
      #masthead:hover > #container #buttons.style-scope.ytd-masthead > .style-scope.ytd-masthead > div#button.style-scope > a > #button > button#button > .style-scope,
      #masthead:hover > #container #guide-button > button#button,
      #masthead:hover > #container #guide-button > button#button > .style-scope
      {
          height: 24px;
          width: 24px;
      }

      #masthead:hover > #container button.style-scope.ytd-topbar-menu-button-renderer
      {
          width: 60px;
          height: 40px;
          padding-left: 6px;
          padding-right: 6px;
      }

      #masthead > #container .style-scope.ytd-topbar-menu-button-renderer.no-transition
      {
          width: 10px;
      }

      #masthead:hover > #container .style-scope.ytd-topbar-menu-button-renderer.no-transition,
      #masthead:hover > #container .style-scope.ytd-topbar-menu-button-renderer.no-transition > img#img
      {
          height: 32px;
          width: 32px;
      }

      #page-manager.style-scope
      {
          margin-top: 10px;
      }
  }
}
