css:
@-moz-document domain("www.sausd.us") {

    @font-face {
        font-family: "HelveticaNeueCondensedBold";
        src: url('https://dl.dropboxusercontent.com/s/tcjvag8iy02to2w/HelveticaNeueLTW1G-BdCn.woff?dl=0') format('woff');
        src: url('https://dl.dropboxusercontent.com/s/63qqnyfy9s1cl60/HelveticaNeueLTW1G-BdCn.woff2?dl=0') format('woff2');
    }

.ui-articles.ui-article.ui-article-title.has-thumb, #sw-app-headlines-6192 .ui-widget-detail
{
    color: white !important
}

a, h1, h2, h3, h4, h5, h6, span
{
    color: white !important;
}

#gb-channel-list, #gb-channel-list-outer, #hp-content-upper, #hp-scroller-outer, #gb-global-icons-outer, #hp-content-lower, #sp-content-outer, #spn-content-outer, #spn-content, [role="menuitem"],[class="sw-channel-dropdown"], [class="ui-widget-header"]
{
    color: white !important;
    background-image: url(https://imgur.com/MoeYb84.png) !important;
}

[role="menuitem"],[class="sw-channel-dropdown"], img, .view-calendar-link, .headlines-scroll-ctrl.back.icon-font, .headlines-scroll-ctrl.next.icon-font
{
    border-radius: 10px
}

.sw-calendar-block-time
{
    color: white !important
}

#hp-gallery-controls, #gb-tagline, [title="roadrunner"], #gb-account-btn, #gb-school-btn, #gb-district, #module-content-51851, #gb-global-icons-outer
{
    display: none !important
}

[title="principal"]
{
    border-radius: 10px !important;
    border: none !important
}

#gb-logo
{
    transform: scale(0.6) !important;
    top: -10%
}

#gb-sitename
{
    font-family: HelveticaNeueCondensedBold
}

.rs-gallery-desc
{
    font-family: HelveticaNeueCondensedBold !important
}

#module-content-46705 .ui-widget.app.cs-rs-multimedia-rotator
{
    border-radius: 10px !important;
    background-image: url(https://imgur.com/MoeYb84.png) !important
}

.hp-gallery-desc-wrapper
{
    display: none !important
}

.ui-widget.app.flexpage, .ui-widget.app.headlines
{
    border-radius: 10px !important;
    background-image: url(https://imgur.com/MoeYb84.png) !important;
    color: white
}

.ui-widget.app.flexpage .ui-widget-header
{
    display: none !important
}
}
