css:
@-moz-document domain("admin.hostify.cz"), domain("admin-stg.hostify.cz") {
/* background */
body,
.bg-primary,
.bg-transparent,
.bg-light,
.card-body,
.modal-header,
.modal-body,
.modal-footer,
.card,
textarea,
input,
select,
.noUi-connects,
.badge-light {
    background-color: var(--bg-darkest) !important;
}

/* border */
textarea,
input,
select,
.noUi-connects,
.badge-light {
    border: var(--border) !important;
}

/* color */
body,
.bg-primary,
.bg-transparent,
.bg-light,
.card-body,
.modal-header,
.modal-body,
.modal-footer,
.card,
textarea,
input,
select,
.noUi-connects,
fa-icon,
.nav-item,
.nav-link,
.row,
.text-center,
.font-weight-normal,
.font-weight-semibold,
.info-span,
.d-table-cell,
td,
th,
.custom-control-label,
button.close,
.badge-light {
    color: #ffffff !important;
}

body {
    color: #c1c1c1 !important;
}


.btn-light {
    background-color: transparent !important;
}

.navbar {
    border: var(--border-none);
    background-color: var(--blue) !important;
}

.sidebar {
    border: var(--border-none);
    color: #ffffff !important;
    background: #141414 !important;
}


table,
table * {
    border: var(--border-none);
}

.text-muted,
i,
b,
u,
.text-muted .fa-icon,
.font-weight-bold {
    color: #808080 !important;
}

.text-primary {
    color: var(--blue) !important;
}

.progress {
    background-color: var(--bg-dark-ish) !important;
}

.modal-body,
.modal-footer {
    border-top: var(--border-none);
}

.swal2-modal {
    background-color: var(--bg-dark) !important;
}

.swal2-toast {
    background-color: var(--bg-dark) !important;
    box-shadow: 0 0 0.5em #fff6 !important;
}

.swal2-title {
    color: #ffffff;
}

.swal2-confirm {
    background-color: var(--blue) !important;
}

.page-item {
    background-color: transparent !important;
    border: var(--border-none);
}

.page-link {
    color: #ffffff !important;
    background-color: #ffffff50 !important;
    border: var(--border-none);
}

.page-item.disabled,
.page-item.disabled .page-link {
    background-color: #ffffff20 !important;
    border: var(--border-none);
}

.input-group-text {
    background-color: transparent !important;
}

input[name=consoleCommand] {
    background-color: var(--bg-dark) !important;
    border: 1px solid #fff3;
}

textarea,
input,
select {
    color: #ffffff !important;
    background-color: var(--bg-dark) !important;
    border: 1px solid #fff5 !important;
}

.badge-light {
    background: var(--blue) !important;
    color: #ffffff !important;
}

.modal-content {
    background-color: var(--bg-darkest) !important;
}


[class*="primary"],
.nav-link.active {
    background: var(--blue) !important;
}

.text-primary,
.btn-outline-primary:not(.active) {
    background: none !important;
}

.text-danger {
    color: var(--red) !important;
}

.text-warning {
    color: var(--yellow) !important;
}

.alert-info {
    color: #fff;
    background-color: var(--bg-dark-ish);
    border-color: transparent;
}

.alert-dismissible .close {
    position: absolute;
    top: 0;
    right: 0;
    padding: .75rem 1.25rem;
    color: var(--bg-dark-ish) !important;
    opacity: .7;
}

/* Login */
app-login .intro,
app-register .intro {
    background-color: var(--bg-darkest) !important;
}

app-login form,
app-login form input,
app-register form,
app-register form input {
    background-color: var(--bg-darkest);
    box-shadow: 0 0 1rem rgba(0, 0, 0, .15)!important;
}

.switch-primary {
    background-color: transparent !important;
}
}

@-moz-document domain("serverlist.hostify.cz") {
::-webkit-scrollbar {
    width: 14px;
    height: 14px;
}

::-webkit-scrollbar-button {
    background-color: #3e4346 !important;
}

::-webkit-scrollbar-track {
    background-color: #646464 !important;
}

::-webkit-scrollbar-track-piece {
    background-color: #3e4346 !important;
}

::-webkit-scrollbar-thumb {
    height: 50px;
    background-color: #242424 !important;
    border: 2px solid #3e4346 !important;
}

::-webkit-scrollbar-corner {}

::-webkit-resizer {}

::-webkit-scrollbar-button:vertical:start:decrement {
    background: linear-gradient(130deg, #696969 40%, rgba(255, 0, 0, 0) 41%),
    linear-gradient(230deg, #696969 40%, rgba(0, 0, 0, 0) 41%),
    linear-gradient(0deg, #696969 40%, rgba(0, 0, 0, 0) 31%);
    background-color: #b6b6b6;
}

::-webkit-scrollbar-button:vertical:end:increment {
    background: linear-gradient(310deg, #696969 40%, rgba(0, 0, 0, 0) 41%),
    linear-gradient(50deg, #696969 40%, rgba(0, 0, 0, 0) 41%),
    linear-gradient(180deg, #696969 40%, rgba(0, 0, 0, 0) 31%);
    background-color: #b6b6b6;
}

::-webkit-scrollbar-button:horizontal:end:increment {
    background: linear-gradient(210deg, #696969 40%, rgba(0, 0, 0, 0) 41%),
    linear-gradient(330deg, #696969 40%, rgba(0, 0, 0, 0) 41%),
    linear-gradient(90deg, #696969 30%, rgba(0, 0, 0, 0) 31%);
    background-color: #b6b6b6;
}

::-webkit-scrollbar-button:horizontal:start:decrement {
    background: linear-gradient(30deg, #696969 40%, rgba(0, 0, 0, 0) 41%),
    linear-gradient(150deg, #696969 40%, rgba(0, 0, 0, 0) 41%),
    linear-gradient(270deg, #696969 30%, rgba(0, 0, 0, 0) 31%);
    background-color: #b6b6b6;
}

/********* GLOBAL ***********/
::selection {
    background: #338ccc;
    color: #fff;
}

::-moz-selection {
    background: #338ccc;
    color: #fff;
}

:root[nighteye="active"] .CodeMirror-cursor {
    border-color: #cecece !important;
}

/*All medium websites */
body.is-withMagicUnderlines .markup--anchor {
    text-decoration: underline;
}

body.is-withMagicUnderlines .postArticle.is-withAccentColors .markup--quote.is-other {
    background-image: linear-gradient(rgba(51, 204, 138, 0.2), rgba(51, 204, 138, 0.2)) !important;
}

/****************************/
html {
    color: #bfbfbf;
    background-image: none !important;
    background: #1f1f1f !important;
}
body {
    background-color: #1f1f1f;
    background-image: none !important;
}
input,
select,
textarea,
button {
    color: #bfbfbf;
    background-color: #1f1f1f;
}
font {
    color: #bfbfbf;
}

html {
    background-image: none !important;
    filter: brightness(100%) contrast(100%) saturate(100%);
}
}

@-moz-document domain("hostify.cz") {
:root {
    --border: 1px solid var(--blue);
    --border-none: 0px solid #0000;
    --bg-darkest: #121212;
    --bg-dark: #252525;
    --bg-dark-ish: #2d2d2d;
}

::-webkit-scrollbar {
    width: 8px;
    height: 8px;
    background-color: #2f2f2f;
}
::-webkit-scrollbar-thumb {
    border-radius: 10px;
    box-shadow: inset 0 0 6px rgba(0, 0, 0, .3);
    background-color: #555555;
}
::-webkit-scrollbar-track {
    box-shadow: inset 0 0 6px rgba(0, 0, 0, .3);
    border-radius: 10px;
    background-color: #2f2f2f;
}
}

@-moz-document domain("hostify.cz") {
#pma_navigation {
    background: inherit;
}
}
