css:
@-moz-document domain("beta.protonmail.com") {
.dropDown-logout-initials {
    border-radius: 4px;
    /* hidden in next section */
  }
  .aside-link {
    border-radius: 4px;
  }
  .item-icon {
    border-radius: 4px;
    width: 3rem;
    height: 3rem;
  }
  .toolbar-icon {
    width: 22px;
    height: 22px;
  }
  .conversation:not(.read):not(.active) .senders, .conversation:not(.read):not(.active) .subject-num-message, .conversation:not(.read):not(.active) .subject-text, .conversation:not(.read):not(.active) .time {
    font-weight:500;
  }
  .pm_labels[class*="-label"] {
    border-radius: 3px;
  }
  .pm_labels[data-format="stack"][class*="-label"]:not(:first-of-type) {
    border-radius: 0 4px 4px 0;
    margin-left: 2px;
  }
  .pm_labels[data-format="stack"][class*="-label"]:not(:first-of-type):hover {
    border-radius: 4px;
  }
  .toolbar-button {
    padding: 0 1rem;
    position: relative;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    text-decoration: none;
  }
  .toolbar-button > .mauto {
    margin: unset;
  }
  .toolbar-button > a {
    text-decoration: none;
  }
  .toolbar-button--dropdown {
    display: grid;
    grid-template-columns: 1fr 1fr;
    padding: 0 1.25rem;
  }
  .toolbar-button--dropdown svg {
    margin-bottom: 0;
  }
  .toolbar-button--dropdown svg:nth-child(1) {
    justify-self: end;
  }
  .toolbar-button--dropdown::after {
    grid-column: auto / span 2;
    margin-top: -5px;
  }
  .toolbar-button::after {
    content: attr(pt-tooltip);
    color: white;
    font-size: 95%;
  }
  .item-container-row > span:first-of-type {
    color: lightgrey;
  }
.conversation-view {
  padding: 1rem 2rem;
}
.message-container::after {
  display: none;
}
.message-iconInfo {
  margin-right: 0.5rem;
}
.pm-button.pm-button--for-icon::after {
  content: attr(pt-tooltip);
  font-size: 95%;
}
}

@-moz-document domain("beta.protonmail.com") {
.sortViewDropdown-container {
  display: none;
}
.toogleModeElementsDropdown-container {
  display: none;
}
.toolbarDesktop-select-page {
  display: none;
}
.dropDown-logout-initials {
  display: none;
}
}
