css:
@-moz-document domain("forums.spacebattles.com") {
/* i really want this to be global */
:root {
    --SB-main-link-color: #b7deff;
    --SB-main-color-two: #415b87
}
.bbCodeQuote .attribution {
    background-repeat: repeat-x;
    background-position: top;
    border-bottom-width: 0px;
    border-bottom-style: solid;
}
.bbCodeBlock .type {
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
}

.message-newIndicator {
    font-size: 90%;
    color: #000;
    background: #658aca;
}
.button.button--scroll, a.button.button--scroll {
     background: rgba(255, 255, 255, 0.75);
}
a {
    color: var(--SB-main-link-color);
}
.p-breadcrumbs a {
    color:  var(--SB-main-link-color);
}
.tagCloud a {
    color:  var(--SB-main-link-color)   
}
.pageNav-page>a {
    color:  var(--SB-main-link-color);
}
.pageNav-jump {
    color:  var(--SB-main-link-color);
}
html[data-logged-in="true"] .structItem-cell .unreadLink {
 background:   var(--SB-main-link-color);
    height: 8px;
    width: 8px;
 border-radius: 50%
}
.p-body-inner {
    display: flex;
    flex-direction: column;
    width: 90%;
    max-width: 90%;
}
.block--messages .message-cell--threadmark-header .threadmarkLabel--unread {
    color: #191f2d;
    background:  var(--SB-main-link-color);
}
.XenBase .block--messages .message .message-content a {
    color: #8fddfb;
}
.message-footer .message-actionBar .actionBar-set--external a {
    color: #8fddfb;
}
.XenBase :not(.block-header) .button.button--link, .XenBase :not(.block-header) a.button.button--link {
    background-color:  var(--main-color-two);
}
.structItem-status--watched::before {
    color: #98d9ff;
}
.block-outer-opposite .block--threadmark-control.rss {
    color:  var(--main-link-color);
}
.block-tabHeader .tabs-tab.is-active {
    border-color: #91c6ff;
}
.menu-tabHeader .tabs-tab.is-active {
    color: #79d5ff;
}
.block--messages .message-cell--threadmark-header .threadmark-control:hover, .block--messages .message-cell--threadmark-footer .threadmark-control:hover {
    background:  #698abf;
    color: #fff;
}
.p-nav-list .p-navEl {
    color:  var(--main-link-color);
}
.sv-rating-type-icon.is-active, .sv-rating-type-icon:hover {
    transform: scale(1);
}
.actionBar-action.actionBar-action--sv-rate.actionBar-action--sv-rate .sv-rating-type__icon {
    margin-top: -2px;
    margin-right: 4px;
}
.tooltip--preview .threadmarkLabel {
    background:   var(--SB-main-color-two);
}

/* Changes the checkbox/radio color */
.iconic>input[type=checkbox]+i:before, .iconic>input[type=checkbox]+i:after, .iconic>input[type=radio]+i:before, .iconic>input[type=radio]+i:after{
        color: #9fe5ff;
}
.notices .notice.notice--primary {
    color: #fff;
    background: #183054;
}
}
