css:
@-moz-document domain("system.erecruiter.pl") {
body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #64d7dc #1c1c1c;
}
span,label,.headerbar strong,input[type="file"],table.grid th,em{
    color: #64d7dc;
}
.grey-box{
    background: transparent;
    border: 1px solid #64d7dc;
}
input[type="text"], input[type="password"], input[type="email"], input[type="url"], input[type="date"], input[type="time"], input[type="datetime"], select, textarea{
    background: #1c1c1c;
    color: #fff;
    box-shadow: none;
}
table.grid{
    background: #1c1c1c;
}
table.grid td,.ClauseAndConsents .clause a{
    color: #fff;
}
.resBox{
    background: #1c1c1c;
}
div.imgLogo:after{
    content: "Dark theme by Maciejozaman";
    color: #64d7dc;
}
}
