left: 600px !important;
padding-top: 4px !important;}

#res > div > ol > .g >.s cite,
#res > div > ol > .g >.s > .gl,
#res > div > ol > .g >.s .fl {font-size: 0.9em !important;}

/*---- IMAGE ----*/
