margin-top: 68px !important;}
#tads + #res {margin-top: 4px !important;}
#tads:not(:hover) {opacity: 0.5 !important;}


/*---- GOOOOOGLE ----*/
#nav .b a:link {color: #333 !important}
#nav .b a:visited {color: #666 !important}

/* Add a result */
