css:
@-moz-document url-prefix("https://www.olx.ua") {
#mybpwaycfxccmnp-dblt-backdrop-filter {
        display: block !important;
        position: fixed !important;
        top: 0 !important;
        bottom: 0 !important;
        left: 0 !important;
        right: 0 !important;
        margin: 0 !important;
        pointer-events: none !important;
        z-index: 2147483647 !important;
        filter: invert(100%) hue-rotate(180deg) !important;
    }
    img:not(.mwe-math-fallback-image-inline):not([alt="inline_formula"]),
    video {
        filter: invert(100%) hue-rotate(180deg) !important;
    }


    html,
    img:not(.mwe-math-fallback-image-inline):not([alt="inline_formula"]),    /* TODO: "black on transparent" mark */
    embed[type="application/x-shockwave-flash"],
    object[type="application/x-shockwave-flash"],
    video,
    div#viewer.pdfViewer div.page
    {
        filter: invert(100%) hue-rotate(180deg) !important;
    }
    /* #28 */
    :fullscreen video,
    video:fullscreen
    {
        filter: none !important;
    }

    html {
        background-color: black !important;
    }


button,
input,
optgroup,
select,
textarea {
    background-color: white;
    color: black;
}




#bigImageContent   {
    background-color: #000  !important;
    color: black;
}


.fix { 
	position: relative}

#mobileAppsbadge .icon  {
        filter: invert(100%) hue-rotate(180deg) !important;
    }

.locationrequest .input-container .chooselocation {
    border: solid 1px #c3c3c3a3;
    background: rgb(240, 239, 239);
    color: #313131;
}

.overgallery, .viewspace {
   position: absolute !important;
	   background: rgba(240, 239, 239, 0)  !important;
        filter: invert(100%) hue-rotate(180deg) !important;
    }


.pricelabel {
  
    
  
    background: #26a700;
    text-shadow: 0 1px 0 #fff0  !important;
}

.overgallery .photospace {
    background: #0000  !important;
  
}
}
