css:
@-moz-document url-prefix("https://hard.rozetka.com.ua"), url-prefix("https://my.rozetka.com.ua"), domain("rozetka.com.ua") {
#mybpwaycfxccmnp-dblt-backdrop-filter {
        display: block !important;
        position: fixed !important;
        top: 0 !important;
        bottom: 0 !important;
        left: 0 !important;
        right: 0 !important;
        margin: 0 !important;
        pointer-events: none !important;
        z-index: 2147483647 !important;
        filter: invert(100%) hue-rotate(180deg) !important;
    }
    img:not(.mwe-math-fallback-image-inline):not([alt="inline_formula"]),
       
	video {
        filter: invert(100%) hue-rotate(180deg) !important;
    }


    html,
    img:not(.mwe-math-fallback-image-inline):not([alt="inline_formula"]),    /* TODO: "black on transparent" mark */
    embed[type="application/x-shockwave-flash"],
    object[type="application/x-shockwave-flash"],
    video,
    div#viewer.pdfViewer div.page
    {
        filter: invert(100%) hue-rotate(180deg) !important;
    }
    /* #28 */
    :fullscreen video,
    video:fullscreen,  video
    {
        filter: none !important;
    }

    html {
        background-color: black !important;
    }


button,
input,
optgroup,
select,
textarea {
    background-color: white;
    color: black;
}

.exponea-popup-banner   {display:none  !important;}
}
