css:
@-moz-document regexp('^https?:\/\/(www\.)?japscan\.co.*') {
body {
	background: #252525;
	color: rgb(176, 197, 255) !important;
}

p a {
	color: #fff !important;
}

a.text-dark,
a.text-dark:focus,
a.text-dark:hover {
	color: #fff !important;
}

.card {
	background-color: #555555 !important;
}

.rectangle-list span,
.rectangle-list li {
	background-color: #555555 !important;
}

.list-group-item {
	color: #fff !important;
	background-color: #555555 !important;
}

.list-group-item:hover {
	color: #fff !important;
	background-color: #4b4b4b !important;
}

.text-dark:hover,
.text-dark:hover:after {
	color: #fff !important;
}

.nav-tabs {
	border-color: #383838 !important;
	color: #fff !important;
}

.nav-tabs .nav-link {
	color: #fff !important;
}

.nav-tabs .nav-link.active {
	background-color: #555555 !important;
	border-color: #555555 !important;
	border-right-color: #383838 !important;
	border-left-color: #383838 !important;
	color: #fff !important;
}

.nav-tabs .nav-link:hover {
	border-color: #383838 !important;
	border-top-color: #555555 !important;
}

.nav-item .nav-link {
	color: rgb(167, 190, 253) !important;
}

.nav-item .nav-link:not(.active):hover {
	color: rgb(126, 161, 255) !important;
}

.carousel .carousel-indicators li {
	background: #3d3d3d !important;
}

.page-item .page-link {
	color: #fff !important;
}

.page-item:not(.active) .page-link:hover {
	background: #3d3d3d !important;
}

.text-danger {
	color: rgb(255, 86, 86) !important;
}

.clearfix {
	color: #fff !important;
}

.clearfix a {
	color: rgb(126, 161, 255) !important;
}

.clearfix a:hover {
	color: rgb(109, 148, 255) !important;
}

.navbar {
	background-color: #3d3d3d !important;
}

.ss-main .ss-single-selected {
	color: #fff !important;
	background-color: #3d3d3d !important;
	border-color: #202020 !important;
}

.ss-main .ss-content.ss-open {
	background-color: #3d3d3d !important;
	border-color: #202020 !important;
}

.ss-main .ss-content .ss-search {
	background-color: #3d3d3d !important;
}

.ss-main .ss-content .ss-search input {
	color: #fff !important;
	background-color: #555555 !important;
	border-color: #202020 !important;
}

.ss-main .ss-content .ss-search input:focus {
	box-shadow: 0 0 5px #555555 !important;
}

.ss-main .ss-content .ss-list .ss-option {
	color: #fff !important;
	background-color: #3d3d3d !important;
}

.ss-main .ss-content .ss-list .ss-option:hover {
	color: rgb(160, 160, 160) !important;
	background-color: #3d3d3d !important;
}

.ss-main .ss-content .ss-list .ss-option.ss-disabled {
	color: #fff !important;
	background-color: #555555 !important;
}
}
