@-moz-document domain("doramy.club") {
body {
    background: #0e0e0e;
    
}
nav {
    height: 45px;
    border-bottom: 1px solid rgba(0, 0, 0, 0.08);
    margin: 0 12px;
    background-color: #2f2a2a;
}
.post-home {
    margin: 15px;
    height: 230px;
    border-bottom: 1px solid rgba(56, 36, 36, 0.08);
    padding-bottom: 15px;
    position: relative;
    background-color: #2d2a2a;
  }
.naz {
    width: 100px;
    color: #cccccc;
    font-weight: 700;
}
tbody {
    background: transparent;
    border: 0 none;
    padding: 0;
    border: 0;
    outline: 0;
    vertical-align: top;
    color: #fff8f8;
}
.post-home {
    margin: 10px;
    height: 230px;
    border-bottom: 1px solid rgba(56, 36, 36, 0.08);
    padding-bottom: 20;
    position: relative;
    box-shadow: -1px 2px 4px 1px black;
}

.content {
    padding: 0;
    margin: 0 auto;
    width: 700px;
    position: relative;
    background-color: #1d1b1b;
}
.poloska aside {
    margin: 0 auto;
    width: 350px;
    position: relative;
    background-color: #2d2a2a;
}
.block-podbor {
    background: #2d2a2a;
    margin-top: 5px;
    padding-bottom: 10px;
    margin-bottom: 10px;
    padding-top: 10px;
}
.gnezdo {
    width: 320px;
    background: none repeat scroll 0 0 #1d1b1b;
    overflow: hidden;
    padding: 0 15px 15px 15px;
}
.sid-blok {
    border-left: 2px solid #2d2a2a;
}
.boxe a {
    display: inline-block;
    font-size: 14px;
    color: #f7f5af;
    border-bottom: 1px solid #2d2a2a;
    
}
.boxe {
    margin: 10px 0;
    overflow: hidden;
    border-left: 3px solid #2d2a2a;
}
.fullcontent {
    background: #2d2a2a;
    padding-top: 5px;
}
.zhanr-blok {
    background: none repeat scroll 0 0 #2d2a2a;
    overflow: hidden;
    padding: 10px 0;
    width: 1050px;
    margin-bottom: 5px;
}
.zhanr a {
    color: #000000;
    display: inline-block;
    text-align: center;
    padding: 3px 0;
    background: none repeat scroll 0 0 #fcf8f8;
    border: 1px solid #000000;
    width: 134px;
    height: 20px;
    line-height: 18px;
    font-size: 14px;
    box-shadow: 1px 2px 19px black;
    }
    
.menu-link a {
    display: block;
    white-space: nowrap;
    font-size: 1em;
    color: #eaf5af;
    font-weight: 700;
    box-shadow: inset 1px 1px 4px black;
}
.afisha {
    background-color: #2f2a2a;
    margin: 5px 0;
    overflow: hidden;
    padding: 15px 0;
    border-bottom: 0 solid #CCC;
    width: 1050px;
    position: relative;
}
.menuhom {
    background-color: #2f2a2a;
}
h1, h2, h3, h4, h5, h6 {
    color: #000;
    font-weight: 400;
    background-color: #1d1b1b;
    color: #e44141;
}
.menusing {
    background-color: #2f2a2a;
    margin-top: 10px;
}
.opisanie {
    clear: both;
    margin-top: 20px;
    padding: 0 0 10px;
    color: #e0d4d4;
    line-height: 20px;
}

.otzvy span {
    font-size: 19px;
    display: inline-block;
    color: #dcdcdc;
}
.rt-bl {
    position: relative;
    height: 50px;
    margin-top: 15px;
    margin-bottom: 15px;
    border: 1px solid #000000;
    border-radius: 3px;
    background: none repeat scroll 0 0 #1d1b1b;
}
.fav-list a {
    position: relative;
    float: left;
    width: 208px;
    margin: 10px 8px 10px 8px;
    color: #eaf5af;
    font-size: 14px;
    height: 350px;
    text-align: center;
    font-weight: 700;
    box-shadow: -5px 4px 7px 1px black;
}
.akterh2 h2 {
    clear: both;
    padding: 10px 0 10px 20px;
    background: #2d2a2a;
    font-size: 18px;
    color: #697ee8;
    box-shadow: 0px 1px 6px black;
}
.akter h1 {
    margin-top: 20px;
    display: inline-block;
    color: #339caa;
}
.akter h1:first-letter {
    color: #e44141;
    font-weight: 400;
}
.post-home img {
    height: 225px;
    width: 165px;
    float: left;
    margin: 10px;
    box-shadow: 1px 2px 20px 0px black;
}
.post-home span {
    display: inline-block;
    margin-top: 15px;
    margin-bottom: 0;
    font-size: 20px;
    width: 480px;
    color: #eaf5af;
}
.taxs p {
    margin-top: 5px;
    color: #555;
    font-size: 14px;
    margin-right: 10px;
    color: #ded1d1;
}
.otzv {
    margin-bottom: 10px;
    padding: 5px;
    color: #dad3d3;
}
.ssilka a {
    font-size: 15px;
    color: #e0eaa8;
    overflow: hidden;
    font-weight: 700;
    border-bottom: 1px solid #272727;
}
.doramvk img {
    width: 32px;
    height: 14px;
}
.perelink a {
    float: left;
    text-align: center;
    width: 124px;
    margin: 5px 5px 10px;
    font: bold 12px Tahoma;
    color: #d0d0d0;
    line-height: 14px;
}
.post-ratings IMG, .post-ratings-loading IMG, .post-ratings-image IMG {
    border: 0px;
    padding: 0px;
    margin: 0px;
    height: 17px;
}
.tabs {
    margin: 0 0 5px;
    height: 28px;
    border-bottom: 3px solid #1d1b1b;
}
.budu-add a, .viewed-add a {
    position: absolute;
    left: 35px;
    font-weight: 700;
    color: #358b21;
    padding: 9px;
    text-shadow: 0px 5px 4px black;
}
.ssilka {
    font-size: 15px;
    clear: both;
    position: relative;
    color: #555;
    padding: 6px 0 0 20px;
    font-weight: 700;
    color: #e0eaa8;
}
.bxslider a {
    display: block;
    height: 245px;
    color: #ddd9d6;
    font: bold 12px Tahoma;
    text-align: center;
}
.pogodam li span {
    font-family: Verdana,Geneva,sans-serif;
    color: #e44141;
    display: inline-block;
    padding: 3px 5px 2px;
    font-size: 17px;
}
.taxs p {
    margin-top: 5px;
    color: #555;
    font-size: 0px;
    margin-right: 10px;
    color: #ded1d1;
    height: 0px;
  }
