css:
/*Author: FlameCaro
Navigationbar: Samarti*/

@-moz-document url-prefix("https://www.horsereality.com"), url-prefix("https://www.horsereality.nl") {
  
  @font-face   {
    font-family: 'Lobster Two';
    font-style: normal;
    font-weight: 400;
    src: local('Lobster Two'), local('LobsterTwo'), url(https://fonts.gstatic.com/s/lobstertwo/v11/BngMUXZGTXPUvIoyV6yN5-fN5qU.woff2) format('woff2');
  }
  
  body.background {
    background: #4a7a9b url("https://i.imgur.com/hxFDa7I.png") no-repeat fixed 0 0/100% auto !important;
  }
  
  h4 {
    color: #ab7070 !important;
  }
  
  .alert.info {
    background-color: #ac4949 !important;
    border: #000 !important;
    color: #000 !important;
  }
  
  .center {
    background-color: #e7d6d6 !important;
  }

  .npc_box {
    background-color: #dab2b2 !important;
  }
  
  .container {
    background-color: #e7d6d6 !important;
  }
  
  button.yellow, input[type="submit"].yellow {
    background-color: #e7d6d6 !important;
  }
  
  button, input[type="submit"] {
    color: #000 !important;
  }
  
  .bluebar_container {
    background-color: #3a0000 !important;
  }
  
  .footer {
    background-color: #3a0000 !important;
  }
  
  #bbmenu.user ul.slidedown li ul.slidedownmenu {
    background-color: #590000 !important;
  }
  
  .breadcrumbs {
    background-color: #dab2b2 !important;
  }
  
  .breadcrumbs a:link, .breadcrumbs a:visited, .breadcrumbs a:active {
    color: #000 !important;
  }
  
  .even {
    background-color: #dab2b2 !important;
  }
  
  .odd {
    background-color: #d9c0c0 !important;
  }

  /*Navigationbar*/
  .leftnav  {
    background: -webkit-linear-gradient(top,#6d0000 0%,#ac4949 100%);
    border-bottom-width: 2px;
    border-radius: 0 0 80px 0;
    border-right-width: 2px;
    border-top-color: #6d0000;
    border-right-color: #6d0000;
    border-bottom-color: #6d0000;
    border-left-color: #6d0000;
    border-top-style: ridge;
    border-right-style: ridge;
    border-bottom-style: ridge;
    border-left-style: ridge;
    box-shadow: none;
    margin-left: 0px;
    width: 148px;
    height: 630px;
    margin-top: -1px;
  }
  
  .leftnav > ul > li {
    color: #dab2b2 !important;
  }
  
  .subtitle {
    color: #dab2b2 !important;
  }
  
  .leftnav > ul > li > ul > li a:active, .leftnav > ul > li > ul > li a:link, .leftnav > ul > li > ul > li a:visited {
    color: #000 !important;
  }
  
  /*Riding School*/
  .container_12 .grid_8.training_left {
    background-color: #dab2b2 !important;
  }
  
  .training_left .horsetraining .traincon {
    background-color: #845c5c !important;
  }
  
  .top {
    background-color: #6d0000 !important;
  }
  
  .container_12 .grid_8.training_left {
    border-left: 1px solid #6d0000 !important;
    border-bottom: 5px solid #6d0000 !important;
    border-right: 1px solid #6d0000 !important;
  }
  
  .trainbar {
    background-color: #dab2b2 !important;
    border: 2px solid #6d0000 !important;
  }
  
  .trainbar > .exp {
    background: -webkit-linear-gradient(top,#c57c7c 0%,#9f3535 100%);
  }
  
  /*Veteranian + Equistore + Blacksmith*/
  .vet_blocks {
    background-color: #dab2b2 !important;
  }
  
  .storeblocks, .horseblocks {
    background-color: #dab2b2 !important;
  }
  
  #store_left.tabnav .store_tabclick:hover, #store_left.tabnav .store_tabclick.tabsel {
    background-color: #dab2b2 !important;
  }
  
  .store_flex select {
    background: url(https://www.horsereality.com/images/bottom-arrow-blue.png) 100% 50% no-repeat #e7d6d6 !important;
  }
  
  button.dark, input[type="submit"].dark {
    background-color: #e7d6d6 !important;
  }
  
  input, select {
    background-color: #e7d6d6 !important;
  }
  
  /*Laboratory*/
 .grid_6.genetics {
    background-color: #dab2b2 !important;
    border-left: 1px solid #6d0000 !important;
    border-bottom: 5px solid #6d0000 !important;
    border-right: 1px solid #6d0000 !important;
 }
  
  .lab_selectall {
    background-color: #6d0000 !important;
  }
  
  .subtop {
    background-color: #ac4949 !important;
  }
  
  .genetic_tests .test_block .first {
    background-color: #dab2b2 !important;
  }
  
  .genetic_result {
    background-color: #dab2b2 !important;
  }
  
  /*University*/
  .adopt_blocks {
    background-color: #dab2b2 !important;
  }
  
  .alert.warning {
    background-color: #ac4949 !important;
    border: 1px solid #000 !important;
    color: #000 !important;
  }
  
  .uni_blocks {
    background-color: #dab2b2 !important;
  }
  
  .uni_blockimg {
    background-color: #dab2b2 !important;
  }
  
  .alert.success {
    background-color: #ac4949 !important;
    border: 1px solid #000 !important;
    color: #000 !important;
  }
  
  .uni_xp {
    background: -webkit-linear-gradient(top,#c57c7c 0%,#9f3535 100%);
  }
  
  .uni_statusbar {
    background-color: #dab2b2 !important;
    border: 2px solid #000 !important;
  }
  
  .mavatar {
    border: 3px solid #000 !important;
  }
  
  /*Wholesaler + Market + Retirement home + Foundation*/
  .alert.error {
    background-color: #db424f !important;
    border: 1px solid #000 !important;
    color: #000 !important;
  }
  
  .premium-only {
    border: 1px #3a0000 solid !important;
    background: #d29191e3 !important;
  }
  
  form fieldset label, fieldset label {
    color: #8b5454 !important;
  }
  
  .col_150 a:active, .col_150 a:link, .col_150 a:visited {
    color: #8b5454 !important;
  }
  
  .numbers {
    background: #dab2b2 !important;
  }
  
  .numselected {
    background: #6d0000 !important;
  }
  
  .container_12 .grid_6.half_block {
    background-color: #dab2b2 !important;
    border-left: 1px solid #6d0000 !important;
    border-bottom: 5px solid #6d0000 !important;
    border-right: 1px solid #6d0000 !important;
  }
  
  .col_200 a:active, .col_200 a:link, .col_200 a:visited {
    color: #8b5454 !important;
  }
  
  .adopt_blocktitle a:active, .adopt_blocktitle a:link, .adopt_blocktitle a:visited {
    color: #8b5454 !important;
  }
  
  .adopt_blocktext p a:active, .adopt_blocktext p a:link, .adopt_blocktext p a:visited {
    color: #8b5454 !important;
  }
  
  /*Navigation General + Forum*/
  table tr {
    background-color: #dab2b2 !important;
  }
  
  .exchange_actions_window {
    border: 1px #6d0000 solid !important;
  }
  
  .exchange table thead th {
    background-color: #ac4949 !important;
  }
  
  .exchange_actions_window table td div span {
    background-color: #d29191e3 !important;
  }
  
  button, input[type="submit"] {
    background-color: #d29191e3 !important;
  }
  
  .column_200 a:active, .column_200 a:link, .column_200 a:visited {
    color: #8b5454 !important;
  }
  
  .col_300 a:active, .col_300 a:link, .col_300 a:visited {
    color: #8b5454 !important;
  }
  
  .new {
    background-color: #f20494 !important;
  }
  
  .col_500 a:active, .col_500 a:link, .col_500 a:visited {
    color: #8b5454 !important;
  }
  
  /*Events*/
  .col_400 a:active, .col_400 a:link, .col_400 a:visited {
    color: #8b5454 !important;
  }
  
  .col_100 a:active, .col_100 a:link, .col_100 a:visited {
    color: #8b5454 !important;
  }
  
  .energy {
    background: -webkit-linear-gradient(top,#c57c7c 0%,#9f3535 100%);
  }
  
  .energybar {
    background-color: #e7d6d6 !important;
    border: 2px solid #000 !important;
  }
  
  /*Personal*/
  .adopt_blocktext .returnsInDays {
    color: #8b5454 !important;
  }
  
  table tr td a:active, table tr td a:link, table tr td a:visited {
    color: #8b5454 !important;
  }
  
  table {
    background-color: #6d0000 !important;
    border-bottom: 3px solid #6d0000 !important;
    border-right: 1px solid #6d0000 !important;
    border-left: 1px solid #6d0000 !important;
  }
  
  .energybar_half {
    background-color: #e7d6d6 !important;
border: 2px solid #000 !important;
  }
  
  .block_tabclick:hover, .block_tabclick.tabsel {
    background-color: #dab2b2 !important;
  }
  
  /*Horses page*/
  p a:active, p a:link, p a:visited {
    color: #8b5454 !important;
  }
  
  .horse_left .right a:active, .horse_left .right a:link, .horse_left .right a:visited {
    color: #8b5454 !important;
  }
  
  .horse_left {
    background-color: #e7d6d6de !important;
  }
  
  .looking_at {
    background-color: #e7d6d6de !important;
  }
  
  .tabclick, .tabclick2, .tabclick3 {
    background-color: #e7d6d6 !important;
  }
  
  .tabclick.tabsel, .tabclick2.tabsel, .tabclick3.tabsel {
    background-color: #dab2b2 !important;
  }
  
  .pedigree {
    background-color: #dab2b2 !important;
  }
  
  .training_right > .block > .comp img, .training_right > .block > .show img {
    background-color: #dab2b2 !important;
  }
  
  .training_right > .block {
    background-color: #dab2b2 !important;
  }
  
  .horse_blockimg {
    background-color: #e7d6d6 !important;
  }
  
  .training_left h6 {
    background-color: #ac4949 !important;
  }
  
  .training_right > .block > .barcon > .trainbar > .trained {
    background: -webkit-linear-gradient(top,#c57c7c 0%,#9f3535 100%);
  }
  
  .disc {
    background: -webkit-linear-gradient(top,#ADDA6E 0%,#586e3f 100%);
  }
  
  .affection {
    background: -webkit-linear-gradient(top,#FE0090 0%,#941A54 100%);
  }
  
  .genetic_table_row {
    border-bottom: 1px solid #6d0000 !important;
  }
  
  .genetic_potential {
    background-color: #dab2b2 !important;
  }
  
  .container_12 .grid_4.training_right {
    background-color: #dab2b2 !important;
  }
  
  .row_300 {
    background-color: #dab2b2 !important;
  }
  
  .horse_xp {
    background: -webkit-linear-gradient(top,#c57c7c 0%,#9f3535 100%);
  }
  
  .horse_statusbar {
    background-color: #e7d6d6 !important;
    border: 2px solid #000 !important;
  }
  
  div.horse_left h1  {
    font-family: "Lobster Two";
    font-size: 28px;
    text-align: left;
    color: #000 !important;
  }
  
  .traincon.selected {
    background-color: #674c4c !important;
  }
  
  /*Profile*/
  .profile_account {
    background-color: #e7d6d6 !important;
  }
  
  .profile_menu {
    background-color: #6d0000 !important;
    border-top: 2px solid #000 !important;
    border-bottom: 2px solid #000 !important;
  }
  
  .avatar {
    border: 4px solid #18344800 !important;
    background-repeat: no-repeat !important;
  }
  
  /*Delta Point Store*/
  .grid_12.deltapoints {
    background-color: #dab2b2 !important;
    border-left: 1px solid #6d0000 !important;
    border-bottom: 5px solid #6d0000 !important;
    border-right: 1px solid #6d0000 !important;
  }
  
  .grid_12.deltapoints .buy_container .buy_block {
    background-color: #845c5c !important;
  }
  
  .buy_block .buy_top {
    background-color: #6d0000 !important;
  }
  
  span.shoutout.bestdeal {
    background-color: #f20494 !important;
  }
  
  Element {
    color: #c90000 !important;
  }
  
  .buy_explain h5 {
    color: #d67fb0;
  }
  
  /*Message*/
  .mail_row {
    background-color: #dab2b2 !important;
    border-bottom: 1px solid #6d0000 !important;
  }
  
  /*DP Deals*/
  .header-cta {
    background-color: #dab2b2 !important;
  }
  
  .header-cta h2 {
    color: #f20494 !important;
  }
  
  span.shoutout.popular {
    background-color: #0096ff !important;
  }
 
}

 /*Clubs*/
@-moz-document url-prefix("https://v2.horsereality.com"), url-prefix("https://v2.horsereality.nl") {
  
  .header-menu {
    background: #3a0000 !important;
  }
  
  .main-content {
    background: #e7d6d6 !important;
  }
    
  .component.npc {
    background: #dab2b2 !important;
  }
  
  .side-nav {
    background: -webkit-linear-gradient(top,#6d0000 0%,#ac4949 100%);
  }
  
  .side-nav ul .subtitle {
    color: #dab2b2 !important;
  }
  
  .side-nav ul a {
    color: #000 !important;
  }
  
  .component.table .table tbody tr:nth-of-type(2n+1) {
    background-color: #d9c0c0 !important;
  }
  
  .component.table .table tbody {
    background-color: #dab2b2 !important;
  }
  
  .component.frame .wrapper {
    background: #6d0000 !important;
  }
  
  .component.table .table thead th {
    background: #ac4949 !important;
  }
  
  div.component table a {
    color: #8b5454 !important;
  }
  
  .btn.btn-cta {
    color: #000 !important;
    background-color: #e7d6d6 !important;
    border-color: #b78181 !important;
  }
  
  nav .pagination .page-item .page-link {
    background: #dab2b2 !important;
  }
  
  nav .pagination .page-item.active .page-link {
    background: #6d0000 !important;
  }
  
  .clubs nav.navbar.navbar {
    background-color: #6d0000 !important;
  }
  
  .component.frame .wrapper .header .action .light > .btn, .component.frame .wrapper .header .action > .btn {
    background: #e7d6d6 !important;
    color: #000 !important;
    border-color: #b78181 !important;
  }
  
  .component.frame .wrapper .content {
    background: #dab2b2 !important;
  }
  
  div.content > ul > li a{
    color: #8b5454 !important;
  }
  
  p a:active, p a:link, p a:visited {
    color: #8b5454 !important;
  }
  
  div.content > em a{
    color: #8b5454 !important;
  }
  
  .alert-warning {
    color: #000 !important;
    background-color: #ac4949 !important;
    border-color: #000 !important;
  }
  
  /*Confo Shows*/
  .btn.btn-darkblue {
    color: #000 !important;
    background-color: #e7d6d6 !important;
    border-color: #b78181 !important;
  }
  
  .shows .table .progress {
    background-color: #e7d6d6 !important;
    border: 2px solid #000 !important;
  }
  
  .shows .table .progress .progress-bar {
    background: -webkit-linear-gradient(top,#c57c7c 0%,#9f3535 100%);
  }
  
  .breadcrumb {
    background-color: #dab2b2 !important;
  }
  
  a {
    color: #fff !important;
  }
  
}
