css:
@-moz-document domain("reddit.com")
{

circle
  {
    fill: #f176f9;
  }
	
/* Hide subreddits top bar */
/*[[sr-top-bar]]*/
  .sr-list > .separator,
.sr-list > .separator + .sr-bar,
#sr-more-link
  {
    display: none !important;
  }

  #sr-header-area
  {
    height: 0 !important;
  }

  .sr-bar,
.dropdown.srdrop
  {
    float: right !important;
    margin-left: 1em;
  }
/* Hide annoying stuff */
  .give-gold-button,
.goldvertisement,
.embed-comment,
.nub
  {
    display: none !important;
  }
/* Reset */
  *, *:before, *:after
  {
    background-color: #f4c4f7 !important;
    border: none !important;
    color: #420f46 !important;
  }
/* Default links */
  a
  {
    color: #662b6a !important;
  }

  a:visited
  {
    color: #662b6a !important;
  }

  a:hover
  {
    color: #c813d5 !important;
  }
/* Background */
 
#sr-more-link
  {
    background-color: #ed96f4 !important;
  }
/* Top bar */
  #header-img,
#mail,
.sidebox.create .spacer a
  {
    filter: invert(80%) hue-rotate(180deg) !important;
  }

  .drop-choices,
.hover-bubble,
.modal-dialog
  {
    background: #f4b3f2 !important;
  }

  .tabmenu .selected a
  {
    color: #999 !important;
  }
/* Side bar */
  .listing-chooser-collapsed .grippy
  {
    width: 1px !important;
  }

  .listing-chooser-collapsed .listing-chooser
  {
    padding-right: 1px !important;
  }

  .listing-chooser li.selected
  {
    box-shadow: none !important;
  }

  .grippy:after
  {
    display: none !important;
  }
/* Inputs and buttons */
  input,
textarea,
.linkinfo .shortlink input,
.new-comment .usertext-body,
.morelink a,
.morelink:hover a,
.fancy-toggle-button a,
.usertext button
  {
    background: #450f49 !important;
    color: #f4b3f2 !important;
    font-weight: normal !important;
  }
/* Listing */
  .thing .title
  {
    color: #451848 !important;
  }

  .title:visited
  {
    color: #451848 !important;
  }

  .thing .title:hover
  {
    color: #d417e2 !important;
  }

  .arrow
  {
    filter: brightness(45%);
  }

  .arrow.upmod,
.arrow.downmod
  {
    filter: brightness(50%) contrast(120%);
  }

  .link .score.likes
  {
    color: #da77d7 !important;
  }

  .link .score.dislikes
  {
    color: #da77d7 !important;
  }

  .expando-button
  {
    filter: brightness(45%) contrast(180%);
    background-color: transparent !important;
  }

  .moderator,
.green
  {
    color: #5b1058 !important;
  }

  .admin,
.nsfw-stamp *
  {
    color: #4e1d4c !important;
  }

  .pagename a,
.trophy-name
  {
    color: #999 !important;
  }

  .buttons li
  {
    padding: 0 !important;
  }

  .buttons a
  {
    margin-right: 8px !important;
    color: #888 !important;
  }

  .buttons a:visited
  {
    color: #666 !important;
  }

  .buttons a:hover
  {
    color: #aaa !important;
  }

  .pagename,
.tabmenu li,
.link .midcol,
.buttons a,
.subreddit
  {
    font-weight: normal !important;
  }

  .search-expando.collapsed:before,
.comment-fade
  {
    display: none !important;
  }

  .recommended-link
  {
    border-color: #333 !important;
  }
/* Comments */
  .link .usertext .md,
blockquote,
pre,
code,
.gold-accent
  {
    background-color: #111 !important;
  }

  .md blockquote
  {
    border-left: solid 4px #333 !important;
  }

  .md td
  {
    border: solid 1px #333 !important;
  }

  hr
  {
    border-bottom: solid 1px #333 !important;
  }

  .comment .author,
.morecomments a
  {
    font-weight: normal !important;
  }
/* RES */
  .guider,
.guiders_button,
.res-fancy-toggle-button,
#RESConsoleContainer,
#RESShortcutsAddFormContainer
  {
    background: #eca2e9 !important;
  }

  .RESDialogSmall,
.RESDropdownOptions,
.RESNotification,
#alert_message
  {
    background: #111 !important;
  }

  .RES-keyNav-activeElement,
.RES-keyNav-activeElement .md-container
  {
    background: #eca2e9 !important;
  }

  .res-nightmode .arrow
  {
    filter: none !important;
  }
/* new.reddit.com */
  #header,
#lightbox,
#hamburgers,
#overlayScrollContainer,
#SHORTCUT_FOCUSABLE_DIV > div:first-child,
header,
div[data-redditstyle],
body > div,
div[style^="left:"]
div[role="button"]
  {
    background-color: #eca2e9 !important;
  }

  #header svg
  {
    filter: invert(90%) hue-rotate(180deg);
  }

  div[contenteditable="true"]
  {
    background: #eca2e9 !important;
  }

  #overlayFixedContent + div
  {
    background: rgba(0, 0, 0, 0.7) !important;
  }

  div[data-slot]
  {
    display: none !important;
  }

  .icon, .icon:before
  {
    color: inherit !important;
  }

  a[data-click-id="timestamp"]
  {
    color: #777 !important;
  }

  a[data-click-id="body"] h2
  {
    color: #aaa !important;
  }

  a[data-click-id="body"]:visited h2
  {
    color: #777 !important;
  }

  .scrollerItem
  {
    box-shadow: none !important;
  }
}
