css:
@-moz-document domain("mensis.pl") {
body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #0274be #1c1c1c;
}
h1,h2,h3,h4{
    color: #fff;
}
/*HEADER*/
.regular_header div#primary-nav{
    background: #1c1c1c;
}
#primary-navigation a{
    color: #fff;
}
.topbar .widget_search .search-form input[type="text"]#s{
    background-color: #1c1c1c;
    border: 1px solid #fff;
    color: #fff;
}
.ajax-search-results-container,.ajax-search-results li{
    background-color: #1c1c1c;
}
.thetime{
    color: #fff;
}
#regular-header{
    background-color: #1c1c1c;
}
#logo img{
    filter: invert(100%);
}
#secondary-navigation .sub-menu{
    background-color: #1c1c1c;
}
#secondary-navigation .navigation ul ul li{
    background-color: #1c1c1c;
}
/*SEARCH*/
#s, .mts-subscribe input[type="text"], .widget_product_search .search-field{
    box-shadow: none;
    color: #fff;
}
/*SEARCH*/
/*HEADER*/
/*STRONA-GŁÓWNA*/
article.latestPost{
    background-color: #1c1c1c;
}
.post-miniature-title h2{
    color: #fff;
}
.post-miniature-title h2:hover{
    color: #0274be;
}
.front-view-content{
    color: #fff;
}
.primary-slider, #sidebar .widget, article.latestPost{
    border-color: #fff;
}
#sidebar .widget{
    background-color: #1c1c1c;
}
#sidebar .widget h3{
    color: #fff;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.breadcrumb{
    color: #fff;
}
.article{
    background-color: #1c1c1c;
}
.single_post header{
    background-color: #1c1c1c;
}
.single-title{
    background: #0274be;
    color: #fff;
}
header .post-info a{
    color: #0274be;
}
header .post-info a:hover{
    color: #fff;
}
.post-single-content{
    background-color: #1c1c1c;
    color: #fff;
}
.related-posts{
    background-color: #1c1c1c;
}
.related-posts h4,.latestPost .title a{
    color: #fff !important;
}
.latestPost .title a:hover{
    color: #0274be !important;
}
.note{
    background-color: #1c1c1c;
    color: #fff;
    border: 1px solid #fff;
}
/*KOMENTARZE*/
#commentsAdd{
    background-color: #1c1c1c;
}
#respond h4 span{
    color: #fff;
}
input, textarea, select{
    background-color: #1c1c1c !important;
    color: #fff;
}
input::placeholder, textarea::placeholder, select::placeholder{
    color: #fff;
}
input::selection, textarea::selection, select::selection{
    color: #0274be;
}
/*KOMENTARZE*/
/*ARTYKUŁ*/
/*SKLEP*/
.ss-full-width{
    background-color: #1c1c1c;
}
.woocommerce nav.woocommerce-pagination ul li a,.woocommerce nav.woocommerce-pagination ul li a:hover{
    border-radius: 0;
}
.woocommerce .woocommerce-breadcrumb a{
    color: #0274be;
}
.woocommerce .woocommerce-breadcrumb{
    color: #fff;
}
.woocommerce-product-details__short-description ul li{
    color: #fff;
}
.woocommerce div.product .woocommerce-tabs ul.tabs li.active{
    background-color: #0274be;
}
.toggle .wps_togglet, .toggle .toggleta,.toggle .togglec{
    background-color: #1c1c1c;
}
.toggle .wps_togglet span, .toggle .toggleta span{
    text-shadow: none;
    color: #fff;
}
/*SKLEP*/
/*PRENUMERATA*/
.semi-rounded .column-item{
    background-color: #1c1c1c;
    color: #fff;
}
.pricingtable-33275 .column-item-data.odd{
    background-color: #3b3b3b;
    color: #fff;
}
/*KOSZYK*/
.single_page{
    background-color: #1c1c1c;
}
.woocommerce-message,.woocommerce-info{
    background-color: #1c1c1c;
    color: #fff;
}
.woocommerce table.shop_table th{
    background-color: #1c1c1c;
    color: #fff;
}
#add_payment_method #payment, .woocommerce-cart #payment, .woocommerce-checkout #payment{
    background-color: #1c1c1c;
}
#add_payment_method #payment div.payment_box, .woocommerce-cart #payment div.payment_box, .woocommerce-checkout #payment div.payment_box{
    background-color: #1c1c1c;
    color: #fff;
    border: 1px dashed #fff;
    border-radius: 4px;
}
/*KOSZYK*/
/*PRENUMERATA*/
/*FOOTER*/
.copyrights{
    background-color: #1c1c1c;
}
.to-top:before{
    content: "Ciemny motyw by Maciejozaman ";
    color: #0274be;
}
/*FOOTER*/
}
