css:
@-moz-document domain("radio.yandex.ru") {
html, body, .page-root__content{
    background-color: #0d0d0d;
    color: #eee;
}
.nav__level, .theme-white .deco-pane-popup, body .local-theme-white.deco-pane-popup, body .local-theme-white .deco-pane-popup, .theme-white .deco-popup-menu, body .local-theme-white.deco-popup-menu, body .local-theme-white .deco-popup-menu{
    background: black;
    color: #eee;
}
.theme-white .typo, .theme-white .typo-h1, .theme-white .typo-h2, .theme-white .typo-h2_bold, .theme-white .typo-h3, .theme-white .typo-h3_bold, .theme-white .typo-h3_regular, .theme-white .typo-header_bold, .theme-white .typo-small, .theme-white .typo-small_bold, body .local-theme-white.typo, body .local-theme-white .typo, body .local-theme-white.typo-h1, body .local-theme-white .typo-h1, body .local-theme-white.typo-h2, body .local-theme-white .typo-h2, body .local-theme-white.typo-h2_bold, body .local-theme-white .typo-h2_bold, body .local-theme-white.typo-h3, body .local-theme-white .typo-h3, body .local-theme-white.typo-h3_bold, body .local-theme-white .typo-h3_bold, body .local-theme-white.typo-h3_regular, body .local-theme-white .typo-h3_regular, body .local-theme-white.typo-header_bold, body .local-theme-white .typo-header_bold, body .local-theme-white.typo-small, body .local-theme-white .typo-small, body .local-theme-white.typo-small_bold, body .local-theme-white .typo-small_bold, .button__label, .nav__item-title, .button-radio-slider__item>span, .theme-white .deco-link, body .local-theme-white.deco-link, body .local-theme-white .deco-link, .theme-white .deco-link_no-hover-color:hover{
    color: #eee !important;
}
.nav__item:hover, .nav__item_selected, .button_action.button:hover, .button-radio-slider__item:hover, .button-radio-slider__selector, .theme-white .deco-popup-menu__item:hover{
    background: #4a4a4a;
}
.button_action.button, .button-radio-slider{
    background: transparent;
}
.body_state_playing .icon_pp, body .icon_like, body .icon_dislike, body .icon_settings, .icon-theme-white .d-icon_volume-sprite, body .local-icon-theme-white.d-icon_volume-sprite, body .local-icon-theme-white .d-icon_volume-sprite, .icon-theme-white .d-icon_equalizer, body .local-icon-theme-white.d-icon_equalizer, body .local-icon-theme-white .d-icon_equalizer, .settings-stream__title .settings-stream__personal-edit, .d-logo__radio__ru, body .icon_vertical_dots, .icon_pp, body .icon_lines, body .icon_close{
    filter: invert(90%);
}
.progress__shape_bg .progress__path, .progress__shape_loading .progress__path{
    stroke: rgba(238, 238, 238, .3);
}
.player-controls__play:hover .progress__path, .progress__shape_play:hover .progress__path, .progress__shape_play:hover .progress__path, .progress__shape_play .progress__path{
    stroke: rgba(238, 238, 238, .9);
}
.button_round.button_transparent{
    box-shadow: inset 0 0 0 2px rgba(238, 238, 238, .3);
}
.button_round.button_transparent:hover{
    box-shadow: inset 0 0 0 2px rgba(238, 238, 238, .9);
}
.button_transparent, .button_transparent:active, .body_state_pushed .nav__level{
    border-color: rgba(238, 238, 238, .3);
}
.button_transparent:hover{
    border-color: rgba(238, 238, 238, .9);
}
.theme-white .deco-pane-popup.deco-pane-arrow_up:before, body .local-theme-white.deco-pane-popup.deco-pane-arrow_up:before, body .local-theme-white .deco-pane-popup.deco-pane-arrow_up:before, .theme-white .deco-pane-popup.deco-pane-arrow_up:after, body .local-theme-white.deco-pane-popup.deco-pane-arrow_up:after, body .local-theme-white .deco-pane-popup.deco-pane-arrow_up:after{
    border-color: transparent transparent #000;
}

::-webkit-scrollbar{
		width:12px;
		background-color:#292929;
	}
	::-webkit-scrollbar:horizontal{
		height:12px;
	}
	::-webkit-scrollbar-track{
		border:1px #292929 solid;
		border-radius:0px;
		-webkit-box-shadow:0 0 6px #292929 inset;
	}
	::-webkit-scrollbar-thumb{
		background-color:#000000;
		border:1px solid #000000;
		border-radius:0px;
	}
	::-webkit-scrollbar-thumb:hover{
		background-color:#bfbfbf;
		border:1px solid #bfbfbf;
	}
	::-webkit-scrollbar-thumb:active{
		background-color:#bfbfbf;
		border:1px solid #bfbfbf;
	}
        ::-webkit-scrollbar-corner{
                background-color:#292929;
        }
}
