css:
@-moz-document domain("dante.iis.p.lodz.pl")
{
body
{
  background: #16222a;
  background: radial-gradient(circle, #3a6073, #16222a);
}
#app
{
  color:white;
}
.text-muted
{
  color:white!important;
}
div[data-v-1d29277a] .text-muted
{
  color:inherit!important
}
h1  
{
  text-transform: uppercase;
  text-align:center;
  letter-spacing:3px;
  text-shadow: 0 0 10px #34e7e4;
}
.breadcrumb
{
  background-color:#fff;
}
ul[data-v-141097de]
{
  background:#fff;
  width: fit-content;
  margin: auto;
}
thead[data-v-ead1a1b0],thead[data-v-a8a06678]
{
  background-color:rgba(0,0,0,.60);
}
[data-v-33a864f6] h1
{
  text-align:left;
}
.table-striped tbody tr:nth-of-type(odd)
{
  background-color:rgba(0,0,0,.40);
}
.table-striped tbody tr:nth-of-type(even)
{
  background-color:rgba(0,0,0,.60);
}
.table-responsive tbody tr:nth-of-type(odd)
{
  background-color:rgba(0,0,0,.10);
}
.table-responsive tbody tr:nth-of-type(even)
{
  background-color:rgba(0,0,0,.20);
}
.fa-star, .fa-star-half-alt
{
  color:gold;
}

div[data-v-33a864f6]
{
   background-color: #282c34;
}
.bg-light,.text-center
{
  background-color:transparent!important;
}

.card
{
  background-color:transparent!important;
  border:none;
}
.text-dark
{
  color:#007bff!important;
  font-weight:700;
}
.text-danger
{
  font-weight:700;
}
.nav-tabs .nav-item
{
	background: #485460;
}
a [data-v-141097de] 
{
  color:#007bff!important;
  font-weight:700;
}

li[data-v-33a864f6].nav-item 
{
  background:transparent!important;
}
h1::after {
    content: 'Porzućcie wszelką nadzieję, wszyscy którzy tu wchodzicie.';
    font-size: 2rem;
    text-shadow: none;
    position: absolute;
    top: 0;
  	left:0;
    text-align: center;
}
  h1{
    margin-top: 6rem;
  }
  div[data-v-33a864f6] h1::after{
    left:auto;
  }
}
