css:
@-moz-document domain("vk.com") {
/*	СТАРЫЕ НАДПИСИ МЕНЮ	*/

		/* Убрать действующие надписи */

#l_pr .left_label, #l_nwsf .left_label, #l_msg .left_label,
#l_gr .left_label, #l_fr .left_label, #l_ph .left_label,
#l_aud .left_label, #l_vid .left_label, #l_ap .left_label,
#l_fav .left_label, #l_doc .left_label, #l_ads .left_label, 
#l_apm .left_label {font-size: 0px;}

		/* Общий цвет и размер шрифта */

.left_label:after {
    font-size: 12.5px;
}

		/* Свое меню */

#l_pr .left_label:after {content:"Моя страница";}
#l_nwsf .left_label:after {content:"Новости";}
#l_msg .left_label:after {content:"Сообщения"; }
#l_gr .left_label:after {content:"Группы"; }
#l_fr .left_label:after {content:"Друзья";}
#l_ph .left_label:after {content:"Фотографии";}
#l_aud .left_label:after {content:"Музыка";}
#l_vid .left_label:after {content:"Видео"; }
#l_ap .left_label:after {content:"Игры";}
#l_fav .left_label:after {content:"Закладки";}
#l_doc .left_label.inl_bl:after {content:"Документы";}
#l_ads .left_label.inl_bl:after {content:"Реклама";}
#l_apm .left_label.inl_bl:after {content:"Настройки";}
}
