css:
@-moz-document domain(play.qobuz.com) {
    #root {
        color: #00acff;
    }

    body {
        background-color: #272727;
    }

    .header {
        background-color: #272727;
        color: #ffacff;
    }

    .main-menu a div {
        color: #00acff;
    }

    .input-text {
        color: #00acff;
    }

    #search .input-text {
        background-color: #000000!important;
    }

    .dropdown button {
        color: #00acff;
    }

    .btn-default {
        background-color: #000;
    }

    .c8 {
        color: #0068bb;
    }

    .item-list-list .item-name {
        color: #0068bb;
    }

    .track-list .tracklist-column-label {
        color: #008add;
    }

    .track-list li {
        background: #272727;
    }

    .player {
        background: #000;
    }

    .fullsearch {
        background: #272727;
    }

    .fullsearch .tagbar {
        background: #272727;
    }

    .fullsearch .tagbar-title {
        color: #00acff;
    }

    .fullsearch .tagbar-tags:nth-child(2) .tagbar-tag p {
        color: #00acff;
    }

    .album-item-card .item-name {
        color: #0068bb;
    }

    .album-item-card .item-artist {
        color: #008add;
    }

    .playlist-item-card .item-title {
        color: #008add;
    }

    .fullsearch .tagbar-tags:first-child .tagbar-tag {
        background: #000;
    }

    .homepage .everyday {
        background: #272727;
    }

    .homepage .explore {
        background: #272727;
    }

    .focus-item-card .item-meta {
        background-color: #000;
    }

    .focus-item-card .item-title {
        color: #0068bb;
    }

    .focus-item-card .item-desc {
        color: #008add;
    }

    .explore-week-album {
        background: #000;
    }

    .explore-week-album .main-title {
        color: #0068bb;
    }

    .explore-week-album .artist-title {
        color: #008add;
    }

    .explore-container .more-infos {
        color: #00acff;
    }

    .explore-item-card-2 {
        background-color: #000;
    }

    .explore-item-card-2 .main-title {
        color: #0068bb;
    }

    .explore-item-card-2 .artist-title {
        color: #008add;
    }

    .article-item-card {
        background-color: #000;
    }

    .nav-pills > button.active,
    .nav-pills > button.active:active,
    .nav-pills > button.active:focus,
    .nav-pills > button.active:hover,
    .nav-pills > button:active,
    .nav-pills > button:focus,
    .nav-pills > button:hover {
        color: #00acff;
    }

    .modal-header,
    .modal-content {
        background-color: #000;
    }

    .modal-title {
        color: #008add;
    }

    .similar-artist .item-name,
    .popin-desc h1,
    .popin-desc .desc-content {
        color: #00acff;
    }

    .user-library .bt9:last-child {
        background: #000;
        color: #00acff;
    }
}
