css:
@-moz-document domain("video.tedomum.net") {
  .sub-header-container, .playlist-info,
  p-table tr, p-table th, p-table th.ui-state-highlight,
  p-table p-paginator .ui-paginator-bottom,
  .first-step-block input[type="button"][disabled],
  .nav-tabs .nav-link.active, .margin-content .header {
    background-color: #161616 !important;
  }
  .modal .modal-content {
    background-color: #161616;
  }
  .header, button[disabled],
  .root .form-group-checkbox input + span,
  .action-button.button-styled.grey,
  input[type="submit"][disabled],
  .help-popover .popover-body,
  .radio::before,
  .root-tabset.video-add-tabset > .nav a.nav-link.active,
  .download-type .peertube-radio-container input[type="radio"] + label::before {
    background-color: #1C1C1C !important;
  }
  .dropdown-menu, .root-tabset.video-add-tabset .upload-video-container {
    background-color: #1C1C1C;
  }
  .btn-group-subscribe.unsubscribe-button .btn, tag,
  .peertube-select-container, .top-buttons .delete-history, input[type="password"],
  body .ui-table .ui-table-tbody > tr:hover, .margin-content .header a, .margin-content .header button,
  .video-bottom .video-info .video-info-first-row .video-actions-rates .video-actions .action-button {
    background-color: #282828 !important;
  }
  .main-col .sub-menu, .card, .input-group-text,
  .dropdown-item:hover, .dropdown-item:focus,
  .grey-button, .grey-button:hover,
  .nav-pills .nav-link.active,
  .modal .inputs .action-button-cancel,
  .modal .inputs .action-button-cancel:hover {
    background-color: #282828;
  }
  .sub-header-container, .header, table, .create-an-account,
  .sub-menu .actor .actor-info .actor-owner,
  .help-popover .popover-body, .video-channel .actor-owner,
  p-table tr, p-table th, p-table th.ui-state-highlight,
  p-table p-paginator .ui-paginator-bottom, p-table td,
  .video-bottom .video-info .video-info-first-row .video-info-channel a,
  .video-bottom .video-info .video-info-first-row .video-info-by a {
    color: #AAAAAA !important;
  }
  .title-page, body,
  .btn-group-subscribe .dropdown-item,
  .btn-group-subscribe .dropdown-item:hover,
  .modal .inputs .action-button-cancel {
    color: #CCCCCC;
  }
  .video-miniature .video-bottom .video-miniature-information .video-miniature-name,
  .section .section-title a, .dropdown-menu a, tag,
  .btn-group-subscribe .dropdown-item button,
  .btn-group-subscribe .dropdown-item button:hover,
  .video-channel .video-channel-info a.video-channel-names,
  .miniature .miniature-info .miniature-name, .actor-display-name,
  ngb-tabset.bootstrap .nav-link, ngb-tabset.bootstrap .nav-pills .nav-link.active,
  .video-bottom .video-info .video-info-first-row .video-actions-rates .video-actions .action-button,
  .video-info .video-info-name, .margin-content .header a, .margin-content .header button {
    color: #CCCCCC !important;
  }
  .grey-button, .grey-button:hover, .nav-pills .nav-link.active {
    color: #EEEEEE;
  }
  .btn-group-subscribe.unsubscribe-button .btn,
  .peertube-select-container select option,
  .peertube-select-container select,
  .top-buttons .delete-history {
    color: #EEEEEE !important;
  }
  .header .top-left-block .icon.icon-menu {
    background-color: #AAAAAA !important;
  }
  .icon-syndication, .cfp-hotkeys-container,
  .wrapper > div.instance-information ngb-accordion .btn,
  my-user-moderation-dropdown .dropdown-menu .dropdown-item,
  my-video-actions-dropdown .dropdown-menu .dropdown-item,
  my-video-actions-dropdown .dropdown-menu .dropdown-item svg {
    filter: invert(1) brightness(0.7);
  }
  .title-page:focus, .title-page:hover {
    color: #F1680D;
  }
  .input-group input:not([type="submit"]),
  .form-group input:not([type="submit"]),
  textarea, .peertube-radio-container input:checked + .radio::before,
  .download-type .peertube-radio-container input[type="radio"]:checked + label::before {
    filter: invert(1) hue-rotate(180deg) contrast(0.7);
  }
  .root .form-group-checkbox input + span, .radio::before,
  .peertube-select-container, input[type="password"],
  .download-type .peertube-radio-container input[type="radio"] + label::before {
    border-color: #444444 !important;
  }
  .input-group-text, .root-tabset.video-add-tabset .upload-video-container,
  .root-tabset.video-add-tabset > .nav a.nav-link.active,
  .root-tabset.video-add-tabset > .nav, .nav-tabs .nav-link.active, .nav-tabs,
  .btn-outline-secondary {
    border-color: #444444;
  }
  button[disabled], input[type="submit"][disabled],
  .first-step-block input[type="button"][disabled] {
    color: #888888 !important;
  }
  button[disabled], input[type="submit"][disabled],
  .first-step-block input[type="button"][disabled] {
    border: 1px solid black !important;
  }
  .alert-info {
    background-color: unset;
    border-color: #2672BF;
    border-width: 3px;
    color: #2672BF;
  }
  .alert-success {
    background-color: unset;
    border-color: #009900;
    border-width: 3px;
    color: #009900; 
  }
  .alert-danger {
    background-color: unset;
    border-color: #850000;
    border-width: 3px;
    color: #850000;
  }
  .dropdown-item svg, .help-tooltip-button svg, .action-button svg, .delete-history svg,
  p-table p-paginator .ui-paginator-bottom .ui-paginator-first, p-table p-paginator
  .ui-paginator-bottom .ui-paginator-last, p-table p-paginator .ui-paginator-bottom
  .ui-paginator-next, p-table p-paginator .ui-paginator-bottom .ui-paginator-prev,
  .modal .modal-header my-global-icon {
    filter: invert(1);
  }
  .actor-info .dropdown-menu .dropdown-item:hover,
  my-video-actions-dropdown .dropdown-menu .dropdown-item:hover {
    background-color: #C0C0C0;
  }
  .bs-popover-right > .arrow::after {
    border-right-color: #1C1C1C;
  }
  .bs-popover-bottom > .arrow::after {
    border-bottom-color: #1C1C1C;
  }
  .peertube-radio-container input:checked + .radio::before,
  .download-type .peertube-radio-container input[type="radio"]:checked + label::before{
    border-color: #EEEEEE !important;
  }
  .nav-tabs .nav-link:hover {
    border: unset !important;
  }
  .nav-tabs .nav-link.active:hover {
    border: 1px solid #444444 !important;
    border-bottom: unset !important;
  }
  my-video-actions-dropdown .dropdown-menu .dropdown-item svg,
  .modal .modal-header my-global-icon svg {
    filter: brightness(2);
  }
  #qrcode-panel qrcode {
    border: 10px solid white;
    filter: brightness(0.5) contrast(0.7);
  }
  .video-bottom .video-info .video-info-first-row .video-actions-rates .video-actions .action-button.action-button-like.activated svg {
    filter: brightness(0.5) sepia(1) saturate(5) hue-rotate(90deg);
  }
  .video-bottom .video-info .video-info-first-row .video-actions-rates .video-actions .action-button.action-button-dislike.activated svg {
    filter: brightness(0.5) sepia(1) saturate(5) hue-rotate(-48deg) saturate(10);
  }
  my-video-add-to-playlist .dropdown-menu .dropdown-item {
    filter: brightness(0.5) sepia(1) saturate(5);
  }
}
