css:
/*requesting full size of the images*/
img {
    min-width: auto !important;
    width: auto !important;
    height: auto !important;
    min-height: auto !important;
}

/*centering photosets*/
/* !!!please be aware that these containers are in the order they are nested, if you are planning to alter something!!! */
.post_full.is_photoset .photoset .photoset_row {
    display: inline-flex!important;
    align-items: center !important;
    margin: 0px !important;
    height: auto !important;
    justify-content: center !important;
}


.photoset,
.post_full.is_photo .photoset .photoset_row {
    display: inline-flex !important;
    margin: 0px !important;
    flex-direction: column !important;
    justify-content: center !important;
}


.post_full.is_photoset .photoset .photoset_row_3 .photoset_photo img,
.post_full.is_photoset .photoset .photoset_row_2 .photoset_photo img,
.post_full.is_photoset .photoset .photoset_row_1 .photoset_photo img,
figure.tmblr-full {
    display: inline-flex !important;
    align-content: center !important;
    margin: 0.5px !important;
    min-height: auto !important;
}



/*ensuring bigger images dont go outside boundaries*/
.post_full.is_photoset .photoset .photoset_row .photoset_photo img,
.post_full.no_body .post_media img,
.post_media_photo img,
.post_media_photo image,
.post_full.is_photo .post_media .post_media_photo,
.post_full.is_photo .post_media .post_media_photo_anchor {
    min-width: auto !important;
    width: auto !important;
    max-width: 540px !important;
}

.post_full.is_photoset .photoset .photoset_row_2 .photoset_photo img,
.photoset_row photoset_row_2 {
    min-width: auto !important;
    width: auto !important;
    max-width: 268px !important;
}

.post_full.is_photoset .photoset .photoset_row_3 .photoset_photo img,
.photoset_row photoset_row_3 {
    min-width: auto !important;
    width: auto !important;
    max-width: 177px !important;
}

/* necessary resizing for icons, thumbnails and such */
.reblog-list-item .reblog-avatar,
.reblog-list-item .reblog-avatar .reblog-avatar-image-thumb,
.post-activity-note-avatar .post-activity-avatar-link img,
.blog-selector-container .selected-blog .avatar {
    width: 25px !important;
    height: 25px !important;
}

/* xkit fixes start */

.xkit-people-notifier-person .people-notifier-avatar{
    width: 16px !important;
    height: 16px !important;
}

.notification .avatar_frame img,
.notification .banner_avatar_frame img,
.notes .note .avatar_frame img,
.ui_avatar .frame img,
.notification .notification_right img,
#xkit-extensions-panel-left .xkit-extension .icon,
.xkit-gallery-extension .icon img{
    width: 30px !important;
    height: 30px !important;
}

/* xkit fixes end */
.blog-list-item .blog-list-item-avatar-image,
.radar .radar_avatar img {
    width: 37px !important;
    height: 37px !important;
}

.post-composer_note-post .avatar img,
.post_full.is_note .avatar img,
.popover--tumblelog-select-dropdown .ts-avatar,
.peepr-body .indash_blog .post_chrome.answer .post_content .avatar img {
    width: 40px !important;
    height: 40px !important;
}

.native-audio-container .audio-image img {
    width: 85px !important;
    height: 85px !important;
}

.radar .radar_content .thumbnail_anchor img {
    width: 300px !important;
    height: 255px !important;
}
