css:
@-moz-document url-prefix("https://www.loups-garous-en-ligne.com/jeu/")
{
 /**
  * Remise en état de la page de jeu avant la MàJ du 04/12/2019
  * Firmament - 05/12/2019 v.1
  */

  .liste-mort
  {
      display: none!important;
  }

  .apparition-mort-avatar
  {
      display: none!important;
  }

  .mort-avatar
  {
      display: none!important;
  }

  .canal_perso .vote-notice
  {
      color: inherit!important;
      margin: auto!important;
  }

  #block_chat_game .vote
  {
      background-color: transparent!important;
      padding: 2px 10px!important;
  }

  #block_chat_game .devote
  {
      background-color: transparent!important;
  }

  #block_chat_game .pseudo-homme
  {
      color: inherit!important;
  }

  #block_chat_game .pseudo-femme
  {
      color: inherit!important;
  }

  #block_chat_game .pseudo-homme::before
  {
      background-image: url('https://www.loups-garous-en-ligne.com/jeu/assets/images/boy.png');
      background-size: 16px 16px;
      display: inline-block;
      width: 16px; 
      height: 16px;
      vertical-align: sub;
      margin-right: 8px;
      content:"";
  }

  #block_chat_game .pseudo-femme::before
  {
      background-image: url('https://www.loups-garous-en-ligne.com/jeu/assets/images/girl.png');
      background-size: 16px 16px;
      display: inline-block;
      width: 16px; 
      height: 16px;
      vertical-align: sub;
      margin-right: 8px;
      content:"";
  }

  [class^="chat-badge-"].chat-badge-dead
  {
      background-color: transparent !important;
  }

  [class^="chat-badge-"].chat-badge-specta
  {
      background-color: transparent !important;
  }

  [class^=chat-badge-].chat-badge-tomdj
  {
      background-color: transparent !important;
  }

  #block_chat_game .guide-notice
  {
      background-color: transparent !important;
      padding: inherit!important;
      color: #0a7b1a!important;
  }

  .canal_guide .canal_joueurs
  {
      display: inherit;
      width: inherit;
      background-color: transparent!important;
      color: #0a7b1a!important;
      border-radius: inherit;
      padding: 3px 10px!important;
      margin: inherit;
      overflow: initial;
  }

  .canal_guide .canal_joueurs .pseudo-homme::before
  {
      content: none!important;
  }

  .canal_guide .canal_joueurs .pseudo-femme::before
  {
      content: none!important;
  }

  .vote img:nth-of-type(2)
  {
      width: 23px !important;
      height: 23px !important;
  }

  .canal_meneur .canal_center
  {
      text-align: left;
      padding: none!important;
      float: left;
      width: 100%;
  }
}
