css:
@-moz-document domain(keepa.com) {

html, .filterBar {
    background: #383838!important;
}

span, p, a, i, li, div{
    color: rgba(255,255,255,0.8)
}

.leftPanelBottomMenu {
 	color: rgba(255,255,255,0.5)
}

.leftPanelBottomMenu:hover {
    color: rgba(255,255,255,1)
}

.title{
    color: rgba(255,255,255,0.8)
}

#customView, #filterCats a, #filterList a, #manageShowall a, #sortList a, .operations {
    color: rgba(255,255,255,0.8) !important;
    text-shadow: none !important;
}

#customView:hover, #filterCats a:hover, #filterList a:hover, #sortList a:hover, .operations:hover {
    color: rgba(255,255,255,1);
    text-shadow: none;
}

#clearVisited, #clearVisitedSetting, #leftPanelMarkAllVisited, #queryJSON {
    color: rgba(255,255,255,0.8);
}

#settingsDelete, .gcblink, .settings-chpw, .url {
    color: #55adff;
}

.productBox{
    position: relative;   
}

.productBox::after {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    display: block;
    content: ' ';
    background: rgba(0,0,0,0.4);
}

.productBox:hover::after {
    display: none;
}

.whiteDeals .colorTip {
    background-color: #000;
    border: 1px solid transparent;
    color: rgba(255,255,255,0.8);
}

.detailProductPage {
    background-color: #383838;
    border: 1px solid #383838;
}

.productThumb {
    background-color: #fff !important;
    border: 1px solid #272727;
}

.productTableDescriptionPriceDescr, .productTableDescriptionTitle, .productTableDescriptionBrand, .legendLabel, .legendRange {
    color: rgba(255,255,255,0.8)
}

.legendLabel:hover, .legendRange:hover {
    color: rgba(255,255,255,1)
}

.legendRange.active{
 	color: #55adff;   
}


.tabItem {
 	background-color: rgba(255,255,255,0.3) !important;
    color: white;
}

#tabAmazon>a, .tabDeals>span, .tabExtern>a{
    color: white; 
}

span.productPriceTableTdLargeS{
 	color: black !important   
}
}
