css:
@-moz-document domain("rubinreport.com") {
:root {
	--red: #df6166;
	--bg: #222;
	--fg: #ddd;

	--block-bg: #333;
	--container-border: #888;

	--side-bar-header-bg: #444;
	--side-bar-link-color: #ddd;
	--side-bar-link-hover-bg: hsla(0, 100%, 100%, .05);

	--tag-fg: var(--fg);

	--preview-bg: #444;
	--preview-fg: var(--fg);
	--preview-title-fg: #fff;
	
	--tb-bg: #444;
	--tb-fg: var(--fg);
}

body {
	color: var(--fg);
}
.bg_header {
	background: var(--bg);
}
a {
	color: var(--red);
}
a:hover {
	color: var(--red);
}

/** Containers */
.wcontainer {
	background: var(--block-bg) !important;
	border-color: var(--container-border);
}

/* Sidebar Left */
.sidebar .chat {
	background: var(--side-bar-header-bg);
	border-bottom-color: var(--container-border);
}
.sidebar .nav li a {
	color: var(--side-bar-link-color);
}
.sidebar .nav li a:hover {
	background: var(--side-bar-link-hover-bg);
}
.sidebar .nav li a.active {
	color: var(--red);
	box-shadow: inset 4px 0 var(--red);
}
.sidebar .nav li a.active .icon {
	color: var(--red);
}

/* Sidebar Right */
.cobrand>.content .c-right .li .date
{
	color: var(--red);
}

/* Footer */
.bottom {
	color: #eee;
}
.bottom ul li a {
	color: #eee !important;
}

/* Posts, Text */
h1,
h2,
h3,
h4,
h5,
h6 {
	color: var(--fg);
}
.post .content {
	color: var(--fg);
}
.post .post-bottom {
	border-top-color: var(--container-border);
}
.answer-comment-block {
	background: var(--block-bg);
	border-color: var(--container-border);
}
.answer-comment {
	border-bottom-color: var(--container-border);
}
.answer-comment > .right .comment-text {
	color: var(--fg);
}
a.tagged {
	color: var(--tag-fg) !important;
}
.post .author .right .username span /* wtf */ {
	color: var(--red);
}
/* Textbox/Attachments */
textarea.post {
	background: var(--tb-bg);
	color: var(--tb-fg);
	border-color: var(--container-border);
}
.question-answer-form .CodeMirror,
.question-answer-form textarea {
	background: var(--tb-bg);
}
.CodeMirror pre {
	color: var(--tb-fg);
}
.post-attachments,
.post-options {
	background: var(--block-bg);
	color: var(--fg);
	border-color: var(--container-border);
}
.writepost.active .post-attachments,
.writepost.active .post-options,
.writepost.active textarea {
	border-color: var(--container-border);
}

/* Feed Top Section */
.filter {
	border-color: var(--red);
}
.filter a.active {
	background: var(--red);
}
.cb_sort select {
	color: var(--red);
}

/* Link Preview */
.og-block .og-info-block {
	background-color: var(--preview-bg);
	color: var(--preview-fg);
}
.og-block .og-info-block .og-title {
	color: var(--preview-title-fg);
}
}
