css:
@-moz-document url-prefix("https://2sucres.org/") {
.mainColor {
    background-color: #004365;
}

.row.right-align.col.s12{
    margin-top: -70px;
    height: 50px;
}

#LeftCol > div.row.col.s12 > div:nth-child(1){
    padding-top: 15px;
}

.primaryNav {
    height: 54px !important;
    line-height: 54px;
    background-size: 139px 96px;
    padding: unset !important;
}

.row {
    margin-bottom: 5px;
}

.primaryNav .btn-large {
    box-shadow: unset !important;
}

.btn {
    margin-right: 10px;
}

.primaryNav .material-icons {
    color: #fff !important;
}

.primaryNav .right img {
    max-height: 54px !important;
}

.tbody > *:nth-child(odd) {
    background-color: #eef5fc;
}

.tr {
    font-size: 12px;
}

.topicName {
    font-weight: 700;
}

.topicIcon .material-icons {
    font-size: 18px;
}

.brand-logo > img {
    padding-left: 250px !important;
    padding-top: 55px !important;
    background: url(https://2sucres.org/static/media/logo-mobile.9a1a849a.png) no-repeat !important;
    height: 0px;
    margin: 0px;
}

.gilbertMain {
    width: 139px;
}

.header-message {
    margin-top: -9px;
}

.nav-warper, .nav-warper.pin-top {
    height: auto !important;
}

.stickers {
    border-bottom: 1px solid hsla(0,0%,50%,.5);
    overflow-y: auto;
}

/*blockquote {
    color: rgba(0,0,0,0.87) !important;
}*/

.jaune {
    background-color: #fd612c;
}

.btn.jaune {
    color: #fff !important;
    font-weight: 700;
    font-family: Arial;
    font-size: 13px;
}

.btn.jaune .material-icons {
    color: #fff !important;
}

.thead .tr {
    text-transform: uppercase;
    font-weight: 700;
}

.secondNav {
    background: #002f46;
    box-shadow: none !important;
}

.lever {
    background-color: #fd612ca0 !important;
}

.switch label input[type="checkbox"]:checked + .lever::after {
    background-color: #fd612c !important;
}

nav a {
    color: #4da0cd;
}

#TopicListe>.thead {
    height: unset;
}

.secondNav, .secondNav .btn {
    height: 40px !important;
}

.secondNav button>a {
    line-height: 40px;
}

nav .brand-logo > img {
    height: 30px;
}

nav .brand-logo {
    padding-top: 3px;
}

footer, .jaune {
    color: #fff;
}

.dropdown-content li>a, .dropdown-content li>span {
    color: #000
}

nav ul li {
    padding-left: 1em;
}

.topicDernier {
    overflow: visible;
    margin-right: 5px;
}
}
