/* === Audiomack dark theme by HitCoder ===

  if you notice any inconsistencies please contact me
  @HitCoder on twitter
  
  I tried my best to make it look consistent. My dev platform for this was firefox.

    tested briefly on chrome

*/

@-moz-document domain(audiomack.com) {
	.music-feed:not(.music-feed--chart) .music-detail-container, .recent-tracks .music-detail-container{
        background-color: #131313;
        border-bottom-color: #1a1a1a;
    }

    html, body{
        background-color: #000000;
        color: #ffffff;
    }

    .main-header{
        background-color: #131313;
        border-bottom-color: #131313;
        color: #ffffff;
    }

    .main-header__logo path:first-child{
        fill: #ffffff !important;
    }

    .site-sidebar{
        background-color: #131313;
        width: 240px;
        color: #ffffff;
        border-right-color: #131313; 
    }

    .site-sidebar__app-badges, .site-sidebar .social-icons, .site-sidebar__menu, .dashboard-sidebar__avatar-wrap{
        width: 240px;
    }

    .music__heading, .social-icons a, .feed-bar__list-item a, .feed-bar__list-item button, .vertical-menu__link, .user-card__title{
        color: #ffffff;
    }

    .site-sidebar .widget-title{
        color: #cccccc;
    }

    .vertical-menu__link:focus, .vertical-menu__link:hover, .site-sidebar__menu a:focus, .site-sidebar__menu a:hover, .site-sidebar__menu button:focus, .site-sidebar__menu button:hover{
        color: #f7a10c;
    }

    .feed-bar__list, .user-profile__nav .feed-bar__list{
        border-bottom-color: #1a1a1a;
    }

    .user-profile__main,.feed-bar, .music-feed, .user-card__inner{
        background-color: #131313;
    }

    .search-form__input{
        background-color: #222;
        color: #ccc;
        border-radius: 16px;
    }

    .tooltip--light, .main-header__block--notifications .tooltip{
        background-color: #131313;
        color: #fff;
        border-color: #333; 
    }

    .music-detail, .u-box-shadow, .notification-list__list-item--main{
        background-color: #131313;
        color: #fff;
    }

    .waveform__time{
        background: hsla(0,0%,0%,.8);
    }

    .BlogCarousel-module__post--Fu6ar, .BlogCarousel-module__image--2FhOt, .BlogCarousel-module__details--1gDJz, .PlaylistCard-module__details--3T_HV{
        background-color: #000;
        color: #fff;
        border-color: #000;
    }

    .BlogCarousel-module__content--s4Z2k{
        color: #ccc;
    }

    .music-interaction{
        color: #ccc;
        border-color: #1b1b1b;
        background-color: #1b1b1b;
    }

    .music-interaction:focus, .music-interaction:hover{
        color: #fff;
        border-color: #262626;
        background-color: #262626;
    }

    .button:hover{
        background-color: #ffc240;
        color: #fff;
    }

    .pinned-items__empty, .pinned-item{
        background-color: #131313;
        border-color: #131313;
    }

    .PlaylistCard-module__title--1ad3j, .PlaylistCard-module__title--1ad3j a{
        color: #ccc;
    }

    .PlaylistCard-module__title--1ad3j, .PlaylistCard-module__title--1ad3j a:hover{
        color: #fff;
    }

    .PostBlock-module__content--2pDyD{
        background-color: #0c0c0c;
    }

    .dashboard-table__cell--title a, .PostBlock-module__title--3tYA3 a, .notification-list__list-item--main .notification-list__list-item-target a{
        color: #fff;
    }

    .PostBlock-module__excerpt--RNS_F{
        color: #eee;
    }

    .PostBlock-module__readMore--1nWjP{
        color: #bbb;
    }

    .PostBlock-module__date--ab2YN{
        color: #aaa;
    }

    .body-text{
        background-color: #131313;
        color: #fff;
    }

    .Modal-module__modal--1tXWt{
        background-color: #131313;
        color: #fff;
        border-radius: 8px;
    }

    .c-dropdown__label, .input, [type="date"], [type="email"], [type="password"], [type="tel"], [type="text"], [type="url"], select, textarea{
        background-color: #222;
        color: #fff;
        border-color: #222;
        border-radius: 16px;
    }

    .notification-list__list-item--main .notification-list__content{
        border-color: #222;
    }

    .table tr, .table thead tr, .dashboard-table-bar{
        border-color: #222;
    }

    .table--multibody > tbody:hover, .table--multibody > tr:hover{
        background-color: #181818;
    }

    .StatBlock-module__inner--Ty-YY{
        background-color: #131313;
    }
    .u-bg-white{
        background-color: #131313;
    }

    .music-detail-container--fixed {
      border-bottom: 1px solid #222;
      background-color: #000;
    }
    .search-form__result--highlighted{
      background-color: #181818;
    }
    
    .music-detail + .album-tracklist{
      background-color: #131313;
    }
    .tracklist__track:focus, .tracklist__track:hover{
      background-color: #181818;
    }
    
    .tracklist__track-details{
      border-color: #181818;
    }
    .UploadStartPage-module__option--2OlG6{
      background-color: #131313;
      border-color: #131313;
    }

    .UploadStartPage-module__option--2OlG6:hover{
      border-color: #333;
    }

    .select{
      background-color: #131313;
      border-color: transparent;
    }

    .select select{
      background-color: #000;
      border-color: transparent;
    }

}
