css:
@-moz-document domain("diary.ru") {
#body h1 {
    font-size: 20px;
}

#body {
    font-size: 12px;
    line-height: 1.4;
}

.news .item span {
    font-family: "PT Sans", "Times New Roman", Times, serif;
}

.links a {
    font-family: "PT Sans", "Times New Roman", Times, serif;
    margin-right: 7px;
    font-size: 14px;
}

.serif {
    font-family: 'PT Sans',Helvetica,Arial,sans-serif;
}

.navbar .dropdown-menu {
    background-color: 
    #fff;
}

.navbar .dropdown-menu a, .navbar .dropdown-menu .checkbox-inline span {
    color: #262626;
}

.dropdown-menu {
box-shadow: 0 3px 4px rgba(0, 0, 0, 0.175);
}

.navbar .dropdown-menu [class*="i-"]:not(.close)::before {
    color: #CB3D43;
    opacity: 1;
}

.navbar .navbar-nav > li > .dropdown-menu .overflow {
    padding: 10px 10px 10px 15px;
}

.navbar .navbar-more .dropdown-menu[aria-labelledby="drop-common"] {
    min-width: 650px;
    font-size: 13px;
}

.navbar .navbar-nav > li.open::after {
    border-bottom-color: #fff;
}

.navbar .navbar-nav > li.open::before {
    border-bottom: 10px solid rgba(0, 0, 0, 0.5);
}

.postActionLinks {
    float: right;
}

.dropdown-menu {
    color: #1e1e1e;
}

body.left:not(.pay26) #side, body.right:not(.pay26) #side {
    position: relative!important;
}

.navbar-user > li.avatar a {
    filter: none!important;
}

.medium {
    font-size: 120%;
}

.diary > div.block:nth-child(1) > .page-header {
    display: none;
}

#aboutBlock {
    font-size: 14px;
}

.navbar .navbar-user > li.username a {
    overflow: visible;
}

.big, #MemberProfile .user {
    font-size: 100%;
}

a:hover, a:focus {
    text-decoration: underline;
}

.mark > li::marker {
    color: transparent;
}

@media (min-width: 1200px) {
.col-md-9, .content .main {
    width: 990px;
}

p.grey:nth-child(3) > noindex {
    display: inline-block;
    margin: 4px 0px;
}

.user.col-lg-4 {
    width: 190px;
    position: fixed;
    background: transparent!important;
}

.diary.col-lg-8 {
    float: right;
    width: 781px;
    margin-left: 190px;
}

.user.col-lg-4 p.grey span.black {
    color: #1e1e1e;
    display: block;
    margin-bottom: -17px;
}
}

@media (min-width: 420px) {
.navbar .navbar-user > li > a {
    padding: 24px 9px 9px 0;
}

.navbar .navbar-user > li .num {
    display: block;
    position: relative;
    font-size: 12px;
    line-height: 12px;
    text-align: right!important;
    margin-top: -38px;
}

.navbar .navbar-user > li .num a {
    padding: 3px 6px;
    background: #912D31;
    border-radius: 12px;
}
}     

@media (min-width: 992px) {
.navbar .navbar-user > li > a {
    padding: 17px 5px 5px;
}

.navbar .navbar-user > li .num {
    margin-top: -48px;
}
}

.i-lock {
    border: solid 1px #912D31;
    display: inline-block;
    position: relative;
    top: -2px;
    padding: 1px;
    height: 9px;
    width: 9px;
}

.i-lock::before {
    color: #912D31;
    font-size: 8px;
    margin: 0px 1px;
}
}
