css:
@-moz-document domain("live.bilibili.com") {

/* 头部列表下拉菜单调整 */
.area-list-panel {
	width: 100px !important;
   left: -34px !important;
   background-color: #eee !important;
   border-radius: 10px 10px 10px 10px !important;
}
/* 直播间底下活动去除 */
.flip-view {
	display: none;
}
/* 去除登场动画 */
.activity-welcome-box {
	display: none !important;
}
/* 主播头像框去除 */
.face-pendants {
	display: none;
}
/* 房间背景：淡灰 */
.room-bk {
    background-color: #F0F0F0;
}
.room-bg,
.room-bk .bk-img {
	display: none;
}
.bk-changers {
	display: none;
}

/* 顶栏背景 */
.link-navbar-ctnr,
.link-navbar,
.body.top-nav-light-theme, .live-top-nav-ctnr {
 	background: transparent !important;
}
/*顶栏文字重叠*/
#link-navbar-vm {
    position: absolute !important;
}
/* 页面底部 */
.link-footer-ctnr,
.live-footer {
    display: none;
}
/* 直播间主题颜色去除 */
#head-info-vm, #gift-control-vm {
	background: transparent;
	box-shadow: 0 0 5px 1px rgba(158,179,193,.5);
	background-color: #EEE;
}
#rank-list-ctnr-box {
	background: transparent;
	background-color: #e9e9e9;
}
.live-skin-coloration-area .live-skin-separate-border {
	
	border-color: #e1e1e1 !important;
}
.live-skin-coloration-area .live-skin-separate-area-hover:hover {
	background-color: #e1e1e1;
}
.live-skin-coloration-area .live-skin-highlight-border {
	border-color: #aaa;
}
.live-skin-coloration-area .live-skin-highlight-bg {
	background-color: #23ade5 !important;
}
.live-skin-coloration-area .live-skin-separate-area-hover:hover, .live-skin-coloration-area .live-skin-separate-area {
	background-color: #fff !important;
}
/* 屏蔽看板娘 */
.live-haruna-ctnr {
    display: none;
}
.right-container {
    display: none;
}
.room-feed {
    display: none;
}
/* 隐藏搜索框 */
.search-bar {
	display: none;
}
/* 隐藏主播排名 */
.hour-rank, .week-star-rank,.rank-text {
	display:none !important;
}
/* 关注按钮颜色 */
.attention-btn-ctnr .left-part {
	background-color:#23ade5 !important;
}
.attention-btn-ctnr .right-part {
	background-color:#fafafa !important;
}
/* 文字颜色 */
.live-skin-coloration-area .live-skin-normal-a-text:link, .live-skin-normal-a-text:visited {
	color: #adadad !important;
}
.live-skin-coloration-area .live-skin-main-text {
	color: #4a4a4a !important;
}
.live-skin-coloration-area .live-skin-normal-a-text {
	color: #9d9d9d !important;
}
.live-skin-coloration-area .live-skin-normal-text {
	color: #999 !important;
}
.live-skin-coloration-area .live-skin-main-a-text:link, .live-skin-main-a-text:visited {
	color: #23ade5 !important;
}
.live-skin-coloration-area .live-skin-main-a-text:hover {
	color: #aaa !important;
}
.live-skin-coloration-area .live-skin-highlight-text {
	color: #23ade5 !important ;
}
.guard-rank-cntr .rank-cntr .rank-list-box .online a[data-v-707db781] {
	color: #999 !important;
}
.live-skin-coloration-area .live-skin-normal-a-text:hover, .live-skin-normal-a-text:active {
   color: #333 !important;
}
.up-level-icon {
	color:#888 !important;
	border-color: #aaa !important;
}
.live-skin-coloration-area .live-skin-highlight-border {
	color: #888 !important;
}
.live-skin-coloration-area .live-skin-highlight-border {
	border-color: #aaa !important;
}
.nav-logo, .nav-item, .shortcut-item, .top-nav-btn {
	color:#9d9d9d !important;
}
.nav-item:hover, .shortcut-item:hover, .top-nav-btn:hover{
	
	background-color: #999 !important;
	color:#eee !important;
}
.custom-link {
	display: none !important;
}
}
