css:
@-moz-document domain("drawception.com") {
.text-muted {
    color: var(--text-color-muted) !important;
}
:root {
    --bg-accent-color: #d7cfb9 !important;
    --bg-accent-highlight-color: #f2f2f2 !important;
    --bg-color: #f7dca2 !important;
    --bg-hover-color: #94cbda !important;
    --bg-disabled-color: #6ab7bf !important;
    --bg-highlight-color: #b8a593 !important;
    --bg-duck-option: #b8a593 !important;
    --bg-duck-option-hover: #b8a593 !important;
    --text-color: #1ca4d2 !important;
    --text-hover-color: #b8a593 !important;
    --text-color-lighter: #9cbf80 !important;
    --text-color-darker: #6ab7bf !important;
    --text-color-muted: #6ab7bf !important;
    --border-color: #b8a593 !important;
    --border-highlight-color: #b8a593 !important;
    --link-color: #1ca4d2 !important;
    --link-color-hover: #dc863e !important;
    --comment-highlight-color: #fffdc9 !important;
    --store-btn-bg-color: #65bbe2 !important;
    --store-btn-bg-hover-color: #b8a593 !important;
    --store-btn-text-color: #dc863e !important;
    --modal-overlay-color: rgba(184,165,147,0.2) !important;
    --colorpicker-border-color: rgba(0,0,0,0.25) !important;
    --popup-border-color: #d2e1ab !important;
    --navbar-bg-color: #9cbf80 !important;
    --navbar-border-color: #d2e1ab !important;
    --navbar-btn-text-color: #1ca4d2 !important;
    --navbar-btn-text-hover-color: #dc863e !important;
    --navbar-btn-border-color: #d2e1ab !important;
    --navbar-btn-border-hover-color: #6ab7bf !important;
    --navbar-btn-text-shadow: 0px 0px 3px #d7cfb9 !important;
    --progress-bar-bg-color: #d2e1ab !important;
}
.text-danger {
    color: var(--border-color) !important;
}
.btn-primary {
    background-color: var(--navbar-btn-text-hover-color) !important;
}
a:focus, a:hover {
    color: var(--link-color-hover) !important;
    text-decoration: underline !important;
}
.label-warning {
    background-color: var(--border-color) !important;
}
.theme-night {
    --bg-accent-color: #3ad3a8 !important;
    --bg-accent-highlight-color: #f2f2f2 !important;
    --bg-color: #2271a2 !important;
    --bg-hover-color: #b197a8 !important;
    --bg-disabled-color: #3ad3a8 !important;
    --bg-highlight-color: #b197a8 !important;
    --bg-duck-option: #b197a8 !important;
    --bg-duck-option-hover: #b197a8 !important;
    --text-color: #e5f2ff !important;
    --text-hover-color: #d6b1de !important;
    --text-color-lighter: #a1ffb8 !important;
    --text-color-darker: #3ad3a8 !important;
    --text-color-muted: #e5f2ff !important;
    --border-color: #53e6ef !important;
    --border-highlight-color: #b197a8 !important;
    --link-color: #a1ffb8 !important;
    --link-color-hover: #a1ffb8 !important;
    --comment-highlight-color: #fffdc9 !important;
    --store-btn-bg-color: #1ca4d2 !important;
    --store-btn-bg-hover-color: #b197a8 !important;
    --store-btn-text-color: #ffb44c !important;
    --modal-overlay-color: rgba(184,165,147,0.2) !important;
    --colorpicker-border-color: rgba(0,0,0,0.25) !important;
    --popup-border-color: #b197a8 !important;
    --navbar-bg-color: #a1ffb8 !important;
    --navbar-border-color: #b197a8 !important;
    --navbar-btn-text-color: #2271a2 !important;
    --navbar-btn-text-hover-color: #ffb44c !important;
    --navbar-btn-border-color: #b197a8 !important;
    --navbar-btn-border-hover-color: #3ad3a8 !important;
    --navbar-btn-text-shadow: 0px 0px 3px #fad489 !important;
    --progress-bar-bg-color: #e5f2ff !important;
}
.progressbar-bg {
    background-color: var(--navbar-btn-border-color) !important;
}
}
