@-moz-document domain("seznamzpravy.cz") {
body{
    background-color: rgb(244,244,244);
    border: silver !important;
}
body * {
    border: Gray !important;
}
main, header {
    background-color: rgb(244,244,244) !important;
    color: rgb(24,24,24) !important;
}
main *, header *, 
.d_fz, .d_fI, .c_fK, .d_fI, .d_fJ + .d_fI .c_fK,
.d_fI + .d_fH .c_fJ,
.c_fB, .d_fA+.d_fz .c_fB,
.d_fo+.d_fn .c_fp,
.d_fo~.d_fn,
.d_fq~.d_fn,
.d_fl+.d_fk .c_fm,
.d_fe+.d_fd .c_ff,
.d_fb + .d_fa .c_fc,
.d_fa + .d_e9 .c_fb,
.d_fH + .d_fG .c_fI, /* reklama BRAND */
.d_e6~.d_e5, .d_e8~.d_e5, /* reklama BRAND header */
.d_e6+.d_e5 .c_e7,  /* reklama BRAND main */
.d_fG, .d_fq, .d_eM .f_a9   /* hlavička */ {
    background-color: rgb(244,244,244);
    color: rgb(24,24,24)
}
/* top strip */
.d_eB,
.d_eO {background-color:  rgb(224,224,224);
}
/* brand logo */
.f_br * , .f_bs * {
    color: Gray;
}
/* logo SZ */
.f_bs svg  {
    display: none;
}
/* search button */
.f_bi,
.f_bg {    
    background-color: rgb(224,224,224) !important;
}
/* reklama top */
.d_fg .f_aT,
.d_fl .f_aT,
.d_ep .f_aP {   
    background-color: rgb(224,224,224);
}
.d_e8.d_fj.d_fh.d_fl.d_fE{
    margin-top:0;
}
/* reklama top - pruh */
/*
.f_a2, 
.d_ex,
*/
.d_ex .f_a2 ,
.d_eX,
.j_dR *  {
    background-color: transparent;
    margin-top:0;
}

/* autor CV */
.j_iE,
.j_iD,
.j_ia,
.j_h3,
.f_dd *  {
    background-color: rgb(234,234,234);    
}
/* nadpis Doporučené, Hlavní zprávy,... */
.e_af * {
    color: Gray !important;
}
.f_cs * {
    background-color: white
}
/* Související články */
.d_h9.j_ee, .d_h9.j_ee * {
    background-color: rgb(244,244,244) ;
}
.d_gW {
    outline-width: 0 !important;
}
/* box Co byste měli vědět */
.d_fo + .d_fn .c_fp,
.j_in,
.f_dN .j_in,
/* info box */
.f_dn.f_ed *,
.j_hY, 
.j_iH, .c_fK, .d_fJ + .d_fI.c_fK,
.f_dV.j_iH {
    background-color: white
}
/* načíst další články link */
.e_B.e_A , 
.e_B.e_A *,
/* pozadí fotek a linků */
.e_aD, .e_aH {
    background-color: rgb(200,200,200);
}
/*** Odkazy na články - v textu ***/
/* rameček kolem vnějšího boxu  data-dot="mol-document-links" */
.f_bZ,
.f_aT,
._5dbc53d22771a35c1126d98c,
._5e72837e4a9a966730e2652a,
.d_g8,
.f_b0,
.f_ch,
.f_ck{
    outline-color: transparent !important; 
    /*background-color: transparent !important;*/
}
/* vnější boxbox */
.j_dr *,
.j_bV {
    color: Silver
}
/* vnitřní box  box mol-document-link="mol-document-link" */
.f_bY {
    background-color: transparent !important;
} 
/* vnitrni box - pozadí textu data-dot="mol-article-card" data-e2e="mol-article-card" */
/* main, header, main *, header *, .d_fH + .d_fG .c_fI, .d_fG */
main, header, main *, header *, .d_fH + .d_fG .c_fI, .d_fG,
.f_bX {
    background-color: transparent;
}
/* link */
.e_B .j_bR:not(.k_ds) {
    color: Silver;  
}
.e_B:hover .j_bR:not(.k_ds) {
    color: Gray;
    text-decoration: none;
}
}


/******** IFRAMES top- korona ************/
@-moz-document domain("share.seznam.cz") {
.root {
    background: white;
}
}
  
/******** IFRAMES reklama ************/
@-moz-document domain("imedia.cz") {
.combi, .combi__ad,
.drtg {
    background-color: #f4f4f4 !important;
    border-color: #f4f4f4 !important;
}
.drtg .drtg_text__ad {
    background-color: #ddd !important;
    border-color: #f4f4f4 !important;
}

.drtg_text__title,
.drtg_text__price,
.product-price,
.combi__title, 
.combi__company_name {
    color: #fff !important;
}

/* banner sidebar DRTG */
.dynamic_banner, 
.dynamic_banner *,
.dynamic-banner,
.dynamic-banner *,
.drtg__ad,
.drtg__ad *,
.drtg__shop,
.drtg__shop * {
    border-color: #f4f4f4 !important;
}
/* logo inzerenta */
.drtg__shop-wrapper,
.drtg__shop_center-wrapper,
.drtg__shop_center,
.drtg__shop,
.drtg__shop_logo {
    background-color: white; 
}
}

/******** IFRAMES seznam ************/
@-moz-document domain("share.seznam.cz") {
.root {
    background: white;
}
}
