css:
@-moz-document domain("hs.segnosystems.com") {
.content-completed-label.ng-scope {
    /* Completed Badge */
    background: #ffffffe3;
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0px;
    right: 0px;
    z-index: 950;
    padding: 42px 106px;
}

.content-completed-label strong {
    color:#394246ba;
    font-size:28px;
}

.group-listing {
    margin: 0px 0;
}

.group-listing.do-next .arrow {
    display:none;
}

.header .top {
    background-color: #26292d;
}

.header .top .hamburger {
    padding: 23px 20px 0;
    font-size: 20px;
}

.input-wrapped-button {
    font-size: 20px;
}

/* Header Navigation */

.header .details .row {
    background-color: #35393e;
}

.header .details .row .item-wrapper {
    border-left: 1px solid hsl(210, 10%, 16%);
    margin-right: -4px;
}

.header .details .row .item-wrapper .heading {
    font-size: 10pt;
    font-weight: 900;
}

.header .details .row .item-wrapper .value {
    margin: -57px 0;
}

.header .details .row .item-wrapper:hover {
    background: #4a4d56;
}

/* Breadcrumbs */
.lmp-breadcrumbs {
    position: absolute;
    left: 0px;
    top: 166px;
    width: 100%;
    padding: 10px 20px;
    color: white;
    font-size: 10pt;
    margin-bottom: 0px;
    background: #25292d;
}


/* Profile Dropdown */
.header .profile-drop-down {
    position: absolute;
    right: 0px;
    top: 66px;
    background-image: transparent !important;
    background-color: #25292d;
}

/* Progress Bar */
.progress-wrapper.visible {
    margin: 0px;
    position: fixed;
    width: 100%;
    z-index: 1000;
    bottom: 0px;
    left: 0px;
    background: hsla(213, 8%, 92%, 1);
    box-shadow: 0px -4px 8px #25292d26;
    border-top: 1px solid white;
}

.progress-wrapper.big-pb.visible {
    padding: 1em 0px;
}

.pb {
    -webkit-box-shadow: 0 0 00px 0 #888;
    box-shadow: 0 0 0px 0 #888;
    border:1px solid #c4c4c4;
    border-radius:5px !important;
}


.target-month, .percent-complete, .percent-remaining, .due-date, .learning-plan-label-wrapper {
    font-size: 10pt;
    margin:0px;
    padding:0.5em 0px;
    font-weight:300;
}

.pb, .pb-target {
    height: 10px;
    border-radius: 3px
}


/* Page Alerts */
.alert-info {
    background-color: #edeff1;
    border: 1px solid hsla(216, 7%, 75%, 1);
    color: #265988;
    z-index: 10000;
    width: 50%;
    padding: 50px 80px 50px 110px !important;
    position: relative;
    display: table;
    left: 50%;
    transform: translate(-50%);
    margin-bottom: 35px;
    box-shadow: 0px 60px 6px #25292d4d;
    border-radius: 5px;
}

.alert-info * {
    line-height: 20pt;
    color: #3d474a;
    text-align: justify;
    font-size: 11pt;
}

.preview-alert-icon.type-icon {
    font-size: 56px;
    font-weight: 100 !important;
    position: absolute;
    left: 30px;
    top: 50%;
    transform: translate(0px, -50%);
    cursor: default;
}

.preview-alert-icon.icon-Close {
    font-size: 26px;
    font-weight: 600;
    cursor: pointer;
    padding: 14px;
    top: -1px;
    right: -1px;
    border-top-right-radius: 5px;
    position: absolute;
    z-index: 20000;
    border-radius: 0px;
    background: #35393e1f;
    color: #25292d;
}

.preview-alert-icon.icon-Close:hover {
    background: #35393e;
    color: hsl(210, 10%, 95%);
}


/* Course Filter Buttons */
.heading-wrapper .text-center {
    text-align:left;
    margin-bottom:25px;
    margin-top:-10px;
}
.btn {
    font-weight: 600 !important;
    padding: 1em 2em;
    border: 0px solid white;
    display: inline;
}

.btn:active, .btn.active {
    outline: 0;
    -webkit-box-shadow: inset 0 0px 0px rgba(0,0,0,0);
    box-shadow: inset 0 0px 0px rgba(0,0,0,0);
}

.btn.btn-default {
    font-weight: 600;
}


/* Course Material Rows */

.content-list {
    margin: 0 auto;
    max-width: 800px;
    border: 1px solid #d3d4d5;
    box-shadow: 0px 0px 10px #00000030;
}

.content-listing {
    border-top: 0px solid white;
    border-left: 0px solid white;
    border-right: 0px solid white;
    border-bottom: 0px solid #eaeaea;
    border-radius: 0px;
}

.content-listing-image-wrapper {
    max-width: 100%;
    width: 100%;
    height: 100%;
    position: absolute;
    border-bottom: 1px solid #35393e38;
    padding: 0px;
}

a.wrapper.content-listing-image-link {
    justify-content: flex-start;
    background: transparent;
    border-radius: 0px;
}

a.wrapper.content-listing-image-link:hover {
    background: #35393e1c;
}

.content-listing img {
    /* Proportional Thumbnails */
    max-width: 100%;
    max-height: 80px;
    margin-left: 28px;
    opacity: 0.75;
}

/* Content Title */
.wrapper.rows {
    margin-left: 130px;
}

.content-listing .media-heading a {
    text-align: left;
    word-break: keep-all;
    width: 100%;
}

.content-listing .media-heading span {
    display: none;
}

.content-listing a {
    color: #6e6e6e;
    font-weight: 300;
    font-size: 16pt;
    margin-bottom: -8px;
}

/* Course Ribbons */

.ribbon {
    right: 0px;
    top: 0px;
    overflow: inherit;
}

.ribbon span {
    font-size: 12px;
    color: #fff;
    -ms-transform: rotate(0deg);
    transform: rotate(0deg);
    -webkit-transform: rotate(0deg);
    width: 130px;
    background: #343a3e;
    -webkit-box-shadow: 0 0px 0px 0px #000;
    box-shadow: 0 0px 0px 0px #000;
    position: absolute;
    top: 0px;
    right: 0px;
}


/* Irrelevant Objects */
.disableable.disabled, .heading-wrapper .alert, .greeting-large, .heading-wrapper h3.ng-binding, .heading-wrapper a[class*="help"] {
    display:none;
}
}
