css:
/* i really want this to be global */
 body {
    background-color: black !important;
    color: #FDFDFD;
}
.okno {
    background: #000000;
}
.nazwa-szkoly {
    color: #FDFDFD;
}
.logo-szkoly {
    content: url("http://goldthegame.epizy.com/images/pobrane.png") !important;
}
.header img {
    content: url("http://goldthegame.epizy.com/images/mobidziennik.png") !important;
}
div#content h1, .h1_01 {
    color:#FDFDFD !important;
    text-shadow: 1px 1px 6px #fff !important;
}
.naglowek1 {
    color: #FDFDFD;
}
.szary1 {
    color: #FDFDFD;
}
ul.dropdown li a.current {
    background-color: black;
    color: #FDFDFD;
}
.zielony1 {
    background-color: #546a21;
}
.komorka_biala {
    background-color: #5f1f1f;
}
.komorka_kolor {
    background-color: black;
}
table.spis a {
    color: #FDFDFD !important;
}
table.spis tr:hover {
    background-color: #5F5F5F !important;
}
.plany-cnt {
    color: #fdfdfd !important;
}
.plansc_godz > div > span {
    background-color: #1f1f1f !important;
}
.plansc_godz > div > span b {
    background-color: #1f1f1f !important;
    color: #e10000 !important;
}
.plansc_cnt > .plansc_cnt_w > .bx.pz {
    background-color: #b10000 !important;
    border: 1px solid #b10000 !important;
}
div#top_green {
    background-image: url("http://goldthegame.epizy.com/images/pasek_gorny.jpg");
    background-color: #b10000 !important;
}
div#menu_gray {
    background-image: url("http://goldthegame.epizy.com/images/pasek_gorny.jpg");
    background-color: #b10000 !important;
}
div#menu_gray a {
    color:white;
}
.powiadomienie_menu {
    background: linear-gradient(to bottom, #00b305 0%, #019a00 50%, #0e9600 51%, #008a04 71%, #0f6500 100%) !important;
    color: white !important;
}
ul.dropdown ul {
    background-color: #b10000 !important;
}
#profil_box {
    background-color: #b10000 !important;
}
.li_no_hover {
    background-color: #000 !important;
}
#profil_box ul li {
    border-bottom: #000 !important;
}
.btn-primary {
    background-image: linear-gradient(to bottom, #e80000 0, #880000 100%);
    border-color: #802424;
}
.okno a {
    color: #f02424 !important;
}
small {
    color: white !important;
}
.naglowek3 span {
    font-size: 15px;
    text-shadow: -1px 1px 0 #000, 1px 1px 0 #000, 1px -1px 0 #000, -1px -1px 0 #000;
}
div.tickLabels {
    color:#fff !important;
}
.okienko_informacyjne {
    text-shadow: -1px 1px 0 #000, 1px 1px 0 #000, 1px -1px 0 #000, -1px -1px 0 #000;
}
.ui-widget-content {
    background: url('http://goldthegame.epizy.com/images/cal_bg.png') !important;
    color: #fff !important;
    font-size: 20px;
}
.fc-grid .fc-other-month .fc-day-number {
    opacity: 1 !important;
    color: #f00 !important;
}
.ui-state-highlight, .ui-widget-content .ui-state-highlight, .ui-widget-header .ui-state-highlight {
    background: #e2c800 !important;
    color: black !important;
}
.ui-widget-header {
    color: white !important;
    background: #b10000 !important;
    text-shadow: 1px 1px 6px #fff !important;
}
.gradient01 {
    background: #b10000 !important;
}
