/* Background */
@media (min-width: 1000px) {
    #content {
        background: #222;
    }
}

.container-welcome-home {
    background: #666;
    padding: 0.625rem 1.25rem
}

.container-welcome-home,
.container-welcome-home .h1 {
    font-family: Arial, "Helvetica Neue", Helvetica, sans-serif;
    font-size: 0.6875rem;
    line-height: 1.0625rem;
    color: #fff
}
.container-welcome-home .lien-jv {
    color: #fff;
    text-decoration: underline
}


.item-home-accroche .foot-accroche {
    background: #444;
    height: 2rem;
    border-bottom: 0.0625rem solid #411;
    padding: 0.4rem 0.5rem 0 0.5rem
}

.accr-item-large .foot-accr-item-large {
    background: #111;
    height: 9.375rem;
    padding: 0.5rem 0.5rem 0 0.5rem
}

.layoutWrapperInner__3Q2ReG {
    position: relative;
    background: #222;
    z-index: 1
}
.jv-header-menu .jv-nav-lvl2 {
    background-color: #222
}

.container-content {
    background: #222;
    margin: 0 auto;
    padding: 1.25rem 1.25rem 0 1.25rem;
    min-height: 37.5rem;
    min-width: 20rem
}

.item-small-pres {
    overflow: hidden;
    position: relative;
    padding: 0.75rem;
    background: #444;
    margin-bottom: 0.75rem;
    border-bottom: 0.0625rem solid #411;
    height: 5.375rem
}
.container-row-video .accr-item-large {
    border-color: #411;
}

.container-row-video {
    margin: 0 -1.25rem 1.25rem -1.25rem;
    padding: 1.25rem 1.25rem 0 1.25rem;
    background-color: #111
}

.container-row-video .item-small-pres,
.container-row-video .accr-item-large .foot-accr-item-large {
    background: #444;
    border-color: #411;
}


.bloc-onfire .list-onfire-link {
    position: relative;
    background: #111;
    display: inline-block;
    width: 100%;
    height: 2rem;
    line-height: 2rem;
    vertical-align: middle;
    font-family: "robotoboldcondensed", Arial, Helvetica, sans-serif;
    font-weight: normal;
    font-size: 0.875rem;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    color: #ff2f46;
    margin-bottom: 0.5rem;
    padding-left: 2.75rem
}
.bloc-onfire .list-onfire-link:active,
.bloc-onfire .list-onfire-link:hover {
    background: #c44;
    color: #fff
}

.bloc-onfire .list-onfire-link:before {
    position: absolute;
    display: block;
    counter-increment: section;
    content: counter(section);
    top: 0;
    left: 0;
    width: 2rem;
    height: 2rem;
    color: #fff;
    font-size: 1.25rem;
    text-align: center;
    background-color: #651100
}

.bloc-onfire .inner-onfire {
    background: #333;
    padding: 1.25rem 1.25rem 0 1.25rem;
    overflow: hidden;
    margin-bottom: 1.25rem
}

.container-row-lists {
    margin: 0 -1.25rem 1.25rem -1.25rem;
    padding: 1.25rem 1.25rem 0 1.25rem;
    background-color: #000
}

.table-ending-home {
    background: #444;
    display: table;
    margin-left: -1.25rem;
    margin-right: -1.25rem;
    table-layout: fixed
}



.table-ending-home .cell-right {
    display: table-cell;
    background: #444;
    vertical-align: top
}


.video-footer-container {
    background-color: #444;
    padding-top: 2.1875rem;
    padding-bottom: 2.1875rem
}

/*bleu => orange*/
/* liens blanc */
