css:
@-moz-document domain("davidwalsh.name") {
body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #7AC9ED #1c1c1c;
}
/*HEADER*/
/*MAIN-MENU*/
#main-nav{
    background: #231F20;
    box-shadow: none;
    border: 1px solid #7AC9ED;
}
.main-nav-left .active, .main-nav-right{
    background: #231F20;
}
.main-nav-left{
    background: #1c1c1c;
}
.main-nav-left li a{
    color: #7AC9ED;
}
.main-nav-right .heading{
    color: #fff;
}
/*MAIN-MENU*/
/*POPULAR*/
.new-main-popular{
    background: #1c1c1c;
}
.new-main-popular strong{
    color: #fff;
}
.new-main-popular ul a{
    color: #7AC9ED;
    border-color: #7AC9ED;
}
.new-main-popular ul a:hover{
    border-top: 1px solid #7AC9ED;
    border-bottom: 0;
}
/*POPULAR*/
/*HEADER*/
/*FRONT-PAGE*/
h2{
    color: #7AC9ED;
}
.post-list li h2 a{
    color: #7AC9ED;
}
.post-list .preview{
    color: #fff;
}
/*FRONT-PAGE*/
/*ARTICLE*/
code{
    background: #1c1c1c;
    border: 1px solid #69C773;
    color: #800080
}
article li code, article p code{
    color: #800080;
}
pre, pre[class*="language-"]{
    background: #1c1c1c !important;
    text-shadow: none;
    color: #800080;
}
.post-more h2{
    background: #1c1c1c;
    border: 1px solid #eee;
}
.post-more h2 a{
    color: #eee !important;
}
.post-list li h3 a{
    color: #7AC9ED;
}
.token.operator{
    background: #1c1c1c;
}
/*TAGS*/
.sidebar-topics li a{
    background: #1c1c1c;
    border: 1px solid #fff5bb;
    color: #fff5bb;
}
.sidebar-topics li a:hover{
    background: #fff5bb;
    color: #1c1c1c;
}
/*TAGS*/
/*DISCUSSION*/
.article-block h2,.comments .box,.comments .children li .box{
    background: #1c1c1c;
    border: 1px solid #eee;
    color: #eee;
}
.comment-user,.comment-user a,.comment-controls i{
    color: #7AC9ED;
}
.comment-user a:hover,.comment-controls i:hover{
    color: #07a;
}
#comment-form input[type="text"],#comment-form input[type="email"],#comment-form input[type="text"]:focus,#comment-form input[type="email"]:focus,#comment-form textarea,#comment-form textarea:focus{
    background: #1c1c1c;
    color: #fff;
}
#ace-box{
    border: 1px solid #fff;
}
.ace_text-layer{
    color: #800080;
}
.ace_gutter .ace_layer{
    background: #231F20;
    color: #fff;
}
/*DISCUSSION*/
/*ARTICLE*/
/*TOPICS*/
main h2 a{
    color: #7AC9ED;
}
/*TOPICS*/
/*FOOTER*/
footer{
    background: #231F20;
}
footer span:first-child:before{
    content: "Dark theme by maciejozaman ";
    color: #7AC9ED;
}
/*FOOTER*/
}
