css:
@-moz-document domain("scpfoundation.net") {
::selection {
    color: #e1d8d8;
    background: #c4c2c2;
}
body {
    background-color: #191919;
    color: #bbb;
}
div#container-wrap {
    background: url(http://scp-wiki.wdfiles.com/local--files/roget-s-workbench/header.png) top left repeat-x;
}
#search-top-box-input:hover,
#search-top-box-input:focus,
#search-top-box-input {
    background: #333;
    padding: 1px 5px;
}
#search-top-box input.empty {
    width: 123px;
}
#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    background-color: #333;
    background: linear-gradient(to bottom, #726f6f, #333, #302e2e)
}
#top-bar a {
    color: #bbb;
}
a {
    color: #bbb;
}
#side-bar .side-block,
#side-bar .heading {
    background-color: #222 !important;
    border-color: #bbb !important;
    color: #bbb !important;
    box-shadow: 0 2px 6px rgb(85, 85, 85);
}
.scp-other-branches {
    background: #222 !important;
    border-color: #222;
}
.scp-featured__block_type_daily {
    background: #222;
}
.scp-featured__block {
    background: #222;
}
#toc {
    background: #191919 !important;
}
h1, #page-title {
    color: #bbb;
}
#top-bar ul li.sfhover a:hover {
    background: #444;
    color: #bbb;
}
#top-bar ul li:hover a {
    background: #333;
    color: #bbb;
    border-color: #333 !important;
}
.page-rate-widget-box .rate-points {
    background: #333 !important;
    border-color: #555 !important;
}
.page-rate-widget-box .cancel a{
    background: #333;
    color: #bbb;
}
.page-rate-widget-box .cancel a:hover  {
    background: #555; 
}
.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a {
    color: black;
}
.page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover {
    background-color: #555;
}
.page-rate-widget-box .cancel {
    background: #333 !important;
    border: solid 1px #333;
}
.content-panel.standalone {
    background: #222 !important;
    box-shadow: 0 2px 6px #33333369, inset 0 1px rgba(255, 255, 255, 0.3), inset 0 10px 20px rgba(255, 255, 255, 0.25), inset 0 -15px 30px rgba(0, 0, 0, 0.1) !important;
}
.yui-navset .yui-content {
    background: #222;
}
.yui-navset .yui-content, .yui-navset .yui-navset-top .yui-content {
    border: none;  
}
.yui-navset .yui-nav, .yui-navset .yui-navset-top .yui-nav {
    border-width:0 0 5px;
    border-color: #191919 !important;
}
.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a em {
border: #555;
}
.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a:focus, .yui-navset .yui-nav .selected a:hover {
    background: #444;
}
.yui-navset .yui-nav a, .yui-navset .yui-navset-top .yui-nav a {
    background: #888;
}
.yui-navset .yui-nav a:hover {
    background-color: #888;
}
table.wiki-content-table th {
    background-color: #333;
}
blockquote {
    background-color: #222;
    border-color: #444;
}
.thread-container .post .head {
    background: #222 !important;
}
.code {
    background: #222;
}
.forum-thread-box .description-block {
    background: #222;
}
.forum-group div.head {
    background-color: #444;
}
.forum-group table tr.head td {
    background-color: #222;
}
.thread-container .post .options a {
    color: #bbb;
}
.thread-container .post .short {
    background: #222;
}
.thread-container .post .short .options {
    color: #bbb;
}
div.new-post a {
    background: #222;
}
.owindow {
    background: #222;
}
.owindow .title {
    background: #222
}
.rimg {
    background-color: #191919 !important;
}
.changes-list-item:hover {
    background-color: #333;
}
.changes-list-item .comments {
    color: #888;
}
}

@-moz-document domain("interwiki.scpdb.org") {
::selection {
    color: #e1d8d8;
    background: #c4c2c2;
}
.interwiki {
    background: #222 !important;
    color: #bbb !important;
    border: 1px solid #bbb;
    border-radius: 10px;
    margin-bottom: 15px;
    box-shadow: 0 2px 6px rgba(85, 85, 85);
}
.interwiki a {
    color: #bbb;
}
.interwiki__title {
    border-bottom: solid 1px #bbb;
    background-color: #191919;
    color: #bbb;
    box-shadow: 0 2px 6px rgb(85, 85, 85);
}
.interwiki__entry a,
.interwiki__entry a:visited {
    color: #bbb;
}
}
