css:
@-moz-document domain("esphome.io") {

body, div.body {
    background-color: #1c1c1c;
    color: #bbb;
}

tt, code {
    background-color:#000;
    color:#ecf0f3;
}

div.sphinxsidebar h3, div.sphinxsidebar h4 {
    font-family: Georgia,serif;
    color: #999;
}

div.sphinxsidebar a {
    color: #aaa;
}

.row-odd {
    background-color: #191919;
}

img[src$=".svg"] {
   -webkit-filter: invert(1) brightness(70%);
   filter: invert(1) brightness(70%);
}

.highlight {
   -webkit-filter: invert(1) brightness(70%);
   filter: invert(1) brightness(70%);
}

table.docutils td, table.docutils th {
    border:none;
}

table.docutils {
    border: 2px solid #191919;
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    box-shadow: none;
}

a {
    color:#66c4ed;
    text-decoration: underline;
}

a:hover {
    color:currentcolor;
}

div.note {
    background-color: #000;
    border: 1px solid #444;
}

input {
    background: #000;
    color: inherit;
}

div.warning {
    background-color: #641c19;
    border: 1px solid #b94444;
}
}
