css:
@-moz-document domain("educus.nl") {
body {
    background-color: #262626;
    color: #ddd;
}

.container {
    background-color: #262626;
    border-style: solid;
    border-width: 1px;
    border-radius: 0;
    border-color: #666666;
    width: 90%;
}

a[href] {
    color: #ddd;
}

a[href]:hover {
    text-decoration: none;
}

/* ===HEADER=== */

.layHeader {
 	max-width: none;   
}

.layTopbar, .layTopbarLeft, .layTopbarRight {
	background: none;    
}

.top_button a[href]{
	border-left-style: solid;
    border-width: 1px;
    border-color: #666666;
}

.top_button.icon_beheer a.mainMenuItem {
    border-right-style: solid;
    border-width: 1px;
    border-color: #666666;
}

.search.right input.unit_80 {
    background-color: #262626;
    color: #ddd;
 	border-style: solid;
    border-width: 1px;
    border-color: #666666;
    border-radius: 0;
    width: 160px !important;
}

.instellingsLogo {
	background: none;
}

.layTitlebar {
    width: 100%
}

.layTitlebarTitle {
    padding-left: 15px;
    color: #ddd;
    font-style: normal;
    text-transform: uppercase;
    text-align: right;
}

.layTitlebarButtons {
    float: left;
}

.layTitlebarButton {
    background-color: inherit !important;
    border-style: solid !important;
    border-width: 1px !important;
    border-color: #666666 !important;
    border-radius: 0 !important;
}

/* === MENUBAR === */

.widget-menu .menuLink {
    background-color: #262626;
	color: #ddd !important;
    border-radius: 0 !important;
}

.widget-menu .menuLink:hover {
    background-color: #444444;
	color: #ddd !important;
    border-radius: 0 !important;
}

.menuLink:hover, 
.widget-menu-dropdown:hover>.menuLink {
    background-color: #444444 !important;
}

.widget-menu li ul {
    background-color: #444444;
}

.menu-vinden {
    display: none;
}

.laySearchbar {
    color: #666666;
}

.searchButton img {
    background-color: inherit;
    border: none;
}

/* === CONTENT === */

.content {
    width: 100%;
    overflow: auto;
}

.layLeft {
    width: 80%;
    float: left;
    overflow: hidden;
}


@media screen and (max-width: 1260px) {
    .layLeft {
        width: 98%;
    }
}

.scrollContainer, .scrollBox, #scrollBox {
    width: auto;
    background-color: inherit;
}

.scrollContainer {
    margin-left: 20px;
    border-style: solid;
    border-width: 1px;
    border-color: #666666;
    border-radius: 0;
    box-shadow: none;
}

.laySide {
    background-color: #262626;
    width: 20%;
    float: right;
    margin-left: 5px;
    margin-right: 20px;
    border-style: solid;
    border-width: 1px;
    border-color: #666666;
    border-radius: 0;
    box-shadow: none;
}

.layFooter {
    width: 99%;
    padding: 15px 0px;
    margin-left: 20px;
}

.contHalf.right {
    width: auto;
    float: left !important;
    margin-left: 20px;
}

/* === TABLES === */

.tblDatagrid {
    background-color: #262626;
    color: #ddd;
    border: none;
}

.tblDatagrid tbody th, 
.tblDatagrid thead th,
.tblDatagrid tr.groupHeader td,
.laySide .tblDatagrid th {
    background-color: #444444 !important;
    color: #ddd !important;
    border: none !important;
    font-style: normal !important;
}

.wicket_orderNone a, 
.wicket_orderUp a, 
.wicket_orderDown a {
    color: #aaa !important;
}

.wicket_orderNone a:hover, 
.wicket_orderUp a:hover, 
.wicket_orderDown a:hover {
    color: #ddd !important;
    text-decoration: none;
}

.tblDatagrid td {
    border: none;
}

.tblDatagrid tbody tr:nth-child(odd) {
    background-color: #333;
}

.tblDatagrid tbody tr:hover {
    background-color: #666666;
}

.tblSelected td {
    background-color: #666666 !important;
}

tr.tablespacer {
    background-color: inherit;
}

.tblWaarnemingMatrix thead th,
.tblWaarnemingMatrix tbody th,
.tblWaarnemingMatrix td {
    border-style: solid;
    border-width: 1px;
    border-color: #666666;
}    

.tdYellow {
    background-color: #444444 !important;
}

.tdBuitenDatumBereik {
    background-color: #262626 !important;
}

.contDetail {
    background: none;
    padding-top: 8px;
    padding-left: 10px;
    border-color: #666666;
}

.contHalf {
    border-color: #666666;
}

/* Resultatenboom */

.tblResultaatNA {
    background-color: #262626;
}

.tblResultaatGeblokkeerd {
    background-color: #262626 !important;
    border-left-style: solid !important;
    border-right-style: solid !important;
    border-width: 1px !important;
    border-color: #666666 !important;
}

.tblAanpasbaarPoging {
    background-color: #444444;
    border-style: solid !important;
    border-width: 1px !important;
    border-color: #666666 !important;
}

.ciSlider,
.ciEditLeft,
.ciEditRight {
    background: #666666 !important;
    border-radius: 5px;
}

.ciLabel {
    font-style: normal !important;
}

.ciButtons {
    background-color: inherit !important;
}

.ciDetails {
    background-color: #444444 !important;
}

/* UI */

.ui-state-default {
    background: none;
    border-style: solid;
    border-width: 1px;
    border-color: #666666;
    border-radius: 0;
}

.ui-accordion-header a {
	color: #ddd !important;
}

.ui-widget-content {
    background-color: inherit;
    background: none;
}

/* === BUTTONS === */

a[href] .nav_bar, 
a[href].nav_prev, 
a[href].nav_next, 
a[href].nav_last {
	background-color: inherit;
    color: #ddd;
 	border-style: solid;
    border-width: 1px;
    border-color: #666666;
    border-radius: 0;
}

a[disabled] .nav_bar,
a[disabled] .nav_bar:hover {
    background-color: #666666;
    color: #ddd;
 	border-style: solid;
    border-width: 1px;
    border-color: #666666;
    border-radius: 0;
}

a[href] .nav_bar:hover, 
a[href].nav_prev:hover, 
a[href].nav_next:hover, 
a[href].nav_last:hover {
	background-color: #444444;
    color: #ddd;
 	border-style: solid;
    border-width: 1px;
    border-color: #666666;
    border-radius: 0;
    text-decoration: none;
}

.widget-button a {
    color: #ddd;
 	border-style: solid;
    border-width: 1px;
    border-color: #666666;
    border-radius: 0;
}

.widget-button a[href]:link {
    background-color: inherit;
}

.widget-button a[href]:hover,
.widget-button a[href]:hover>span {
    background-color: #444444;
    color: #ddd;
}

/* === BEHEER === */

.menu-fields-container {
	background-color: inherit !important;
}

.menu-fields {
    float: left;
    margin-left: 20px;
}

.menuBox {
    background-color: inherit;
}

.menu-favorites {
    background-color: inherit;
    border-style: solid;
    border-width: 1px;
    border-color: #666666;
    border-radius: 0;
}

.menu-favorites-title {
    background-color: #444444;
    border-radius: 0;
}

.menu-header-text {
    color: #ddd;
}

/* ===INPUTS=== */

input,
input[type="text"],
textarea,
select, 
.peildatumselect.required,
.inpSelect {
    background-color: #262626 !important;
    color: #ddd !important;
 	border-style: solid !important;
    border-width: 1px !important;
    border-color: #666666 !important;
    border-radius: 0 !important;
}

.required {
    background-color: #262644 !important;
}

input[disabled],
select[disabled] {
    background-color: inherit !important;
    color: #666 !important;
}

input:focus, 
select:focus {
    outline: none;
}

/* === VARIOUS === */

.widget-feedback {
    background-color: inherit;
}
}
