css:
@-moz-document domain("www.magnapolonia.org") {
body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #F54337 #1c1c1c;
}
input[type="text"], input[type="email"], input[type="url"], input[type="password"], input[type="search"]{
    background: #1c1c1c;
    color: #fff;
}
.tnp-subscription input[type="text"], .tnp-subscription input[type="email"], .tnp-subscription input[type="submit"], .tnp-subscription select{
    background: #1c1c1c;
    color: #fff;
    border: 1px solid #F54337;
}
/*HEADER*/
.top-header-section .date-section{
    color: #fff;
}
#site-navigation ul li a{
    color: #fff;
}
#site-navigation ul li ul li{
    background: #1c1c1c;
}

/*SEARCH*/
.header-search-wrapper .search-form-main{
    background: #1c1c1c;
    box-shadow: none;
    border-bottom: 2px solid #F54337;
}
.page-header .page-title{
    color: #F54337;
}
.status-publish p{
    color: #fff;
}
/*SEARCH*/
/*HEADER*/
/*STRONA-GŁOWNA*/
.ticker-content-wrapper .news-post a{
    color: #fff;
}
.ticker-content-wrapper .bx-controls a{
    color: #fff;
}
.ticker-content-wrapper .bx-controls a:hover{
    color: #F54337;
}
.block-header, .widget .widget-title-wrapper, .related-articles-wrapper .widget-title-wrapper,.editorial_magna_grid .featured-post-wrapper h3.block-title, .featured-news-list h3.block-title{
    background: #F54337;
}
.block-header .block-title, .widget .widget-title, .related-articles-wrapper .related-title,.editorial_magna_grid .featured-post-wrapper h3.block-title a{
    background: transparent;
}
.block-header .block-title a, .widget .widget-title, .related-articles-wrapper .related-title,.featured-news-list a{
    color: #fff;
}
.grid-posts-block .post-title a, .column-posts-block .post-title a,.featured-news-list a{
    color: #F54337;
}
.post-content-wrapper p{
    color: #fff;
}
.list-posts-block .single-post-wrapper .post-content-wrapper .post-title a{
    color: #F54337;
}
.tnp-subscription label{
    color: #fff;
}
.featured-news-list h3.block-title a{
    background: transparent;
    color: #fff;
}
/*STRONA-GŁOWNA*/
/*ARTYKUŁ*/
h1.entry-title{
    color: #F54337;
}
.field--type-string-long,.field--type-text-with-summary{
    color: #fff;
}
.entry-content{
    color: #fff;
}
.archive-classic .entry-title a{
    color: #F54337;
}
.archive-classic .entry-title a:hover{
    color: #fff;
}
/*ARTYKUŁ*/
/*PODSTRONY*/
.archive .page-header{
    background: none;
}
.archive .page-header .page-title{
    background: none;
    color: #fff;
}
/*WSPIERAJ-NAS*/
.status-publish table tbody td span{
    color: #fff !important;
}
#post-50267 .status-publish h3,#post-50267 .status-publish p,#post-50267 .status-publish h1{
    color: #F54337;
}
/*WSPIERAJ-NAS*/
/*PODSTRONY*/
/*FOOTER*/
.site-info{
    color: #fff;
}
.site-info:after{
    content: " | Ciemny motyw by Maciejozaman";
}
/*FOOTER*/
}

@-moz-document domain("sklep.magnapolonia.org") {
body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #F54337 #1c1c1c;
}
/*HEADER*/
nav.navbar{
    background: #1c1c1c;;
}
.navbar .nav-link{
    color: #fff;
}
.dropdown-menu{
    background: #1c1c1c;
    border: 1px solid #fff;
}
.dropdown-item{
    color: #fff;
}
.topbar a span,.fa-envelope::before,.fa-phone::before,.fa-facebook-f::before, .fa-facebook::before,.fa-user::before{
    color: #fff;
}
/*SEARCH*/
.aws-container .aws-search-field,.aws-container .aws-search-field:focus{
    background: #1c1c1c;
    color: #fff;
}
.aws-container .aws-search-form .aws-form-btn{
    background: #1c1c1c;
}
.aws-container .aws-search-form .aws-search-btn_icon,.aws-search-result .aws_result_excerpt{
    color: #fff;
}
.aws-search-result,.aws-search-result .aws_no_result:hover{
    background: #1c1c1c;
}
.aws-search-result .aws_result_price{
    color: #77a464;
}
.aws-search-result .aws_result_item:hover,.aws-search-result .aws_search_more a:hover{
    background: #1c1c1c;
}
/*SEARCH*/
/*HEADER*/
/*ZNIŻKA*/
.wbounce-modal .wbounce-modal-sub{
    background: #1c1c1c;
    overflow-y: hidden;
}
.wbounce-modal p{
    color: #2b8eec;
}
.modal-body center{
    color: #fff;
}
.modal-body a{
    color: red;
}
/*ZNIŻKA*/
/*STRONA-GŁÓWNA*/
.sale_notification_default .wrapper-noti.light{
    background: #1c1c1c;
}
span.woocommerce-loop-product__title{
    color: #F54337;
}
span.woocommerce-loop-product__title:hover{
    color: #fff;
}
.woocommerce a.button:hover{
    color: #1c1c1c;
}
.woocommerce nav.woocommerce-pagination ul li a, .woocommerce nav.woocommerce-pagination ul li span{
    color: #fff;
}
/*STRONA-GŁÓWNA*/
/*KOSZYK*/
.xoo-wsc-basket{
    background-color: #1c1c1c;
    box-shadow: none;
    border: 1px solid #fff;
}
.xoo-wsc-basket:hover{
    background-color: #F54337;
}
.xoo-wsc-bki{
    color: #fff;
}
.topcart .cart a,.wpmenucart-icon-shopping-cart-0{
    color: #fff;
}
.topcart .cart a:hover{
    background-color: #F54337;
}
.woocommerce-message{
    background: #1c1c1c;
    color: #8fae1b;
}
.restore-item{
    color: #fff;
}
.cart_item .product-name a{
    color: #fff;
}
.woocommerce .quantity .qty{
    background: #1c1c1c;
    color: #fff;
}
.woocommerce-cart table.cart td.actions .coupon .input-text{
    background: #1c1c1c;
    color: #fff;
    width: 100px;
}
.xoo-wsc-container{
    background: #1c1c1c;
}
/*KOSZYK*/
/*ZAKŁADKI*/
.fa-home::before,.bread-cat{
    color: #fff;
}
/*ZAKŁADKI*/
/*SORTOWANIE*/
.woocommerce .woocommerce-ordering select{
    background: #1c1c1c;
    color: #fff;
}
/*SORTOWANIE*/
/*LOGOWANIE*/
.woocommerce form .form-row input.input-text, .woocommerce form .form-row textarea{
    background: #1c1c1c;
    color: #fff;
}
/*LOGOWANIE*/
/*FOOTER*/
.footer .menu li a{
    color: #fff !important;
}
.footer p{
    color: #fff;
}
.footer p:before{
    content: "Ciemny motyw by Maciejozaman";
    color: #F54337
}
/*FOOTER*/
}
