css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("newzbin.com") {

body,#topblanker {
  background-color:white!important;
}

#content .warning,#pagetop_info,.dateLine, #content .freakytab {
  display:none !important;
}

table.one_col_data caption {
  background:white!important;
}

tr.odd *, tr.even * {
  background-color:white!important;
}

.sidebox {
  background-color:white!important;
  border:0!important;
}

.sidebox h2 {
  background:0!important;
}

.fullwidth,#quicksearch,fieldset {
  border:0!important;
  background:white!important;
}

table.results tbody.odd td,table.legend tr td ,table.legend {
  background-color:white!important;
  border:0!important;
}

#topActionsForm {
  padding:0!important;
  margin:0px!important;
}

#topActionsForm legend {
  display:none!important;
}

#topActionsForm div.center {
  width:auto!important;
}

.freakytab li a {
  background:0!important;
  color:black!important;
  border-left:1px solid #CCC!important;
  border-right:1px groove #CCC!important;
  border-top:1px solid #CCC!important;
}

select {
  border:0!important;
}

#topActionsForm div.center input#nzb_name_text {
  padding:0!important;
  margin:0px!important;
  margin-top:0px!important;
}

#topActionsForm div.center br {
  display:none!important;
}

table.legend caption {
  border:0!important;
  color:black!important;
  background:0!important;
}

form#PostActions {
  margin-bottom:50px!important;
}

div.selection ul li.current {
  font-weight:bold;
}


}
