css:
@-moz-document domain("polskieradio.pl") {
body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #CE0018 #1c1c1c;
}
/*HEADER*/
/*HEADER*/
/*STRONA-GŁÓWNA*/
#mosaic-modern .mosaic-column .mosaic-news .title *{
    color: #fff !important;
}
#mosaic-modern .mosaic-fade-out-wrapper::after,.container{
    background: #1c1c1c;
}
.HPtemp .HP_multibox a h2{
    color: #CE0018;
}
.HPtemp .HP_multibox a:hover h2,.HPtemp .HP_multibox .lead,.HPtemp .tabs li a:hover{
    color: #fff;
}
.HPtemp .ImgTitle_TitlesList .article:first-child h2,.HPtemp .ImgTitle_TitlesList h3,.HPtemp .columns-4 h2,.HPtemp .columns-3 h2,.HPtemp .extra-promo .col-md-6,.HPtemp .Books section article .reads, .Books section article .time,.HPtemp .tabs li a{
    color: #CE0018;
}
.HPtemp .ImgTitle_TitlesList .article:first-child h2:hover,.HPtemp .ImgTitle_TitlesList h3:hover,.HPtemp .columns-4 h2:hover,.HPtemp .columns-3 h2:hover,.HPtemp .extra-promo .col-md-6:hover,.HPtemp .Books section article .reads:hover, .Books section article .time:hover{
    color: #fff;
}
.HPtemp .Books .mask{
    border-bottom: 10px solid #CE0018;
    top: 0;
}
.HPtemp .Books section article,.HPtemp .Books section article .play i{
    background-color: #1c1c1c;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
#ajax-wrap-page,.ArticleMasterModern #PolskieRadio_artykuly_gora_bill,.ArticleMasterModern .ArticleMaster .article-listen-audio .sounds-wrapper{
    background: #1c1c1c;
}
.ArticleMasterModern .ArticleMaster h1.title,.ArticleMaster .comments .doLogin .pr-login-popup,.ArticleMasterModern .ArticleMaster .wrap-article .title a,.ArticleMasterModern .ArticleMaster header .box-title *,.ExternalArticle.ArticleMasterModern .ArticleMaster .title{
    color: #CE0018;
}
.ArticleMaster .desc,.ArticleMaster .content p,.ArticleMasterModern .ArticleMaster .BreadCrumbs a,.ArticleMasterModern .ArticleMaster .article-time .time, .ArticleMasterModern .ArticleMaster .article-time .last-updated,.ArticleMaster .comments .doLogin > *,.ArticleMasterModern .ArticleMaster .article-listen-audio .sounds-wrapper .description,.ArticleMasterModern .ArticleMaster .article-listen-audio,.ArticleMasterModern .ArticleMaster .wrap-article .title a:hover,.ArticleMasterModern .ArticleMaster ul > li,.ArticleMasterModern .ArticleMaster .third .positioner h2,.ExternalArticle.ArticleMasterModern .ArticleMaster .title:hover{
    color: #fff;
}
.swiper-scrollbar{
    border: 1px solid #CE0018;
}
.swiper-scrollbar-drag{
    background: #CE0018;
}
/*ARTYKUŁ*/
/*PODSTRONY*/
.pager-nav.angle > ul > li > *{
    color: #CE0018;
}
.jedynka a *{
    color: #f68e1e;
}
.jedynka a:hover *{
    color: #fff;
}
.dwojka a *{
    color: #009e61;
}
.dwojka a:hover *{
    color: #fff;
}
.trojka a *,.lastplay nav *,.trojka a{
    color: #d80372
}
.trojka a:hover *,.trojka a:hover{
    color: #fff;
}
.lastplay .section .content,.MusicWeekDay .section,.vert.briefcase .tabs.active li{
    background: #1c1c1c;
}
/*CHOPIN*/
.chopin-main-menu__item,p.radio-chopin-news-main-article__content--title,.articles-box__category,.radio-chopin-news-first-two-articles__content--title,.radio-chopin-news-what-left__content--title,.articles-box__title,.swiper-audio a *,.swiper-audio .swiper-audio__track-short-title,.article__title--medium,.chopin-maps__header-active,.chopin-maps__header:hover,.chopin-maps__title,.horizontal-slider .horizontal-slider-btn-icon{
    color: #AD7211;
}
p.radio-chopin-news-main-article__content--title:hover,p.radio-chopin-news-main-article__content--lead,.radio-chopin-news-first-two-articles__content--title:hover,.radio-chopin-news-first-two-articles__content--lead,.radio-chopin-news-what-left__content--lead,.articles-box__lead,.discography-slider .article__title--medium:hover,.article__description,.chopin-maps__lead,.horizontal-slider .horizontal-slider-btn-icon:hover{
    color: #fff;
}
.discography-logo,.chopin-logo__img{
    filter: invert(100%);
}
.chopin-maps__header-eur::before{
    background: #AD7211;
}
.composer,.articles-slider-vertical{
    background: transparent;
}
#backToTopBtn.visible{
    background-color: transparent;
}
#backToTopBtn.visible:hover{
    filter: invert(100%);
    background-color: transparent;
}
/*CHOPIN*/
/*RCKL*/
/*COOKIES*/
.wrap-cookie-info-ext{
    background: #1c1c1c;
}
.helper-center h5,.helper-center p{
    color: #fff;
}
.wrap-cookie-info-ext button{
    background: #1c1c1c;
    color: #fff;
    border-color: #CE0018;
}
/*COOKIES*/
.title--main,.title--small,.title--big{
    color: #CE0018;
}
.title--main:hover,.breaking-news .lead,.box-header__title,.title--small:hover,.title--big:hover,.player:hover .player__listen,.city__name,.weather-item-slash,.wether-wigdet__temp .fetchTemp.show,.business__articles .lead{
    color: #fff;
}
.player__listen{
    color: #e11838;
}
.quote__title,.quote__lead{
    color: #db2e2e;
}
.more-button{
    background: #db2e2e;
}
.more-button:hover .more-button__title{
    color: #1c1c1c;
}
.weather-widget,.listen2-swiper__container,.listen2-swiper__container::before,.listen2-swiper__container::after{
    background: #1c1c1c;
}
.weather-item-long__description-ico-803, .weather-item-long__description-ico-804{
    filter: invert(100%);
}
/*RCKL*/
/*SKLEP*/
/*HEADER*/
header,.login-bar,.menu{
    background: none;
}
.menu .innermenu .menu-list li h3 a{
    color: #c31621;
}
.menu .innermenu .menu-list li ul li h3 a{
    background: #1c1c1c;
}
.menu .innermenu .menu-list li ul > li:first-child{
    margin-top: 0;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
/*MENU*/
.box{
    background: #1c1c1c;
}
#box_menu ul li a{
    color: #fff;
}
/*MENU*/
.resetcss h1,#box_mainproducts.box .boxhead > h1,.box .productname,.goog-te-gadget .goog-te-combo,.resetcss h2,.article_name_span{
    color: #fff;
}
.resetcss p span a,.resetcss p a{
    color: #c31621;
}
.products.viewphot .product{
    background: #1c1c1c;
}
.floatcenterwrap .paginator li a{
    color: #fff;
    border: 1px solid #1c1c1c;
}
.floatcenterwrap .paginator li a:hover{
    border: 1px solid #4d80d1;
}
.resetcss p span{
    color: #fff !important;
}
/*STRONA-GŁÓWNA*/
/*PRODUKT*/
.modal{
    background: #1c1c1c;
}
.mask{
    background-image: none !important;
}
.manufacturer a{
    color: #c31621;
}
.product-innerdesc-wrap .price,.breadcrumbs .innerbreadcrumbs .breadcrumb-home span,.breadcrumbs .innerbreadcrumbs ul li a,#box_productfull .boxhead h1,#box_productfull .basket .addtofav,.fb-share-button a,.product-modules .boxhead h3{
    color: #fff;
}/*PRODUKT*/
/*FOOTER*/
footer{
    background: #1c1c1c;
}
footer .innerfooter ul.overall:after{
    content: "Ciemny Motyw by Maciejozaman";
    color: #4d80d1;
}
footer .innerfooter ul li.head{
    color: #4d80d1;
}
footer .innerfooter ul li a{
    color: #fff;
}
/*FOOTER*/
/*SKLEP*/
/*LOGIN*/
.ArticleMaster.loginregister .btn-container div > a,.underline{
    color: #CE0018
}
.ArticleMaster.loginregister .btn-container div > a:hover,.underline:hover{
    color: #fff;
}
.ArticleMaster.loginregister input{
    background: #1c1c1c;
    color: #fff;
}
/*LOGIN*/
/*SEARCH*/
#searchBar .search-wrapper input{
    background: #1c1c1c;
    color: #fff;
}
.search-bar-wrapper, .search-bar{
    background: #1c1c1c;
}
.article-item__title,.search-pagination a{
    color: #e31935;
}
.article-item__lead,.article-item__title:hover,.article-item__lead:hover,.search-pagination a:hover{
    color: #fff !important;
}/*SEARCH*/
/*PODSTRONY*/
/*AUTOR*/
.thismenu header a,#thismenu span a,.ludzie .AboutPerson .desription p a{
    color: #CE0018;
}
.thismenu header a:hover,#thismenu span a:hover,.ludzie .AboutPerson .desription p a:hover{
    color: #fff;
}
.ludzie input, .ludzie textarea{
    background: #1c1c1c;
    color: #fff;
}
.ludzie input.error, .ludzie textarea.error{
    border-color: #CE0018 !important;
}
.contact-form .forms .btn-default{
    border: 1px solid #ca020b;
}
/*AUTOR*/
/*FOOTER*/
#prFooter .footer-wrapper:after{
    content: "Ciemny motyw by Maciejozaman";
    color: #CE0018;
}
/*FOOTER*/
}

@-moz-document url("https://www.polskieradio.pl/18,Dzieci") {
.container{
    background: none;
}
}
