    background-color: #41464d;
}
.nav a, .nav a:hover, .nav a:active, .nav a:focus, .zon-teaser-standard__text, .zon-teaser-wide__text, .zon-teaser-lead__text, .zon-teaser-classic__text, .zon-teaser-printbox__text, .zon-teaser-upright__text, .zon-teaser-poster__text, .section-heading__link, .byline, .comment__rewrapper:hover .comment__cta, .comment__rewrapper:hover .comment-overlay__cta, .comment-overlay:hover .comment-overlay__cta, 
  .pager__page--current span, .header:not(.header--force-mobile) .nav__metadata, .nav__metadata, .overlay__headline
{
     color:#fff
}
.sticky-nav{
background: #2a2e33;
}
.cp-region--lead{ border-color: #2a2e33}

.header__logo, .sticky-nav__focusheadline, .sticky-nav__hplink, .sticky-nav__list, .sticky-nav__icon--zon {
    color: #ccc;
}
.zon-teaser-standard__title, .zon-teaser-printbox__title, .zon-teaser-upright__title, .zon-teaser-wide__title, .zon-teaser-lead__title, .zon-teaser-classic__title, .zon-teaser-poster__title, .comment__body, .comment__cta, .comment-overlay__cta, .jobbox-ticker-item__title, a, .overlay__text, .article-heading__title{
     color: #ccc
}
.nav__tag:hover, .sticky-nav__tag:hover, .nav__tag:active, .sticky-nav__tag:active, .nav__tag:focus, .sticky-nav__tag:focus, .comment__origin:hover, .comment__origin:active, .comment__origin:focus, .comment__reaction:hover, .comment__reaction:active, .comment__reaction:focus, .comment__date:hover, .comment__date:active, .comment__date:focus{
